{
	"openapi": "3.0.0",
	"info": {
		"title": "SWR Audio Lab - Radiohub",
		"description": "This documentation is also available as [openapi.json](https://docs.radiohub.swr.digital/openapi.json) or [openapi.yaml](https://docs.radiohub.swr.digital/openapi.yaml).\n\n  \nAuthorization keys can be requested via [lab@swr.de](mailto:lab@swr.de) and shall be sent as base64 encoded basic auth information in the header. Please report all problems or bugs to [lab@swr.de](mailto:lab@swr.de) or via the [Github repo](https://github.com/swrlab/swr-radiohub-docs/issues) and possibly include the `trace` information from the response header or info object.\n\n The dev environment uses data from the dev database; staging and production environments both access production data. The `appId` therefore follows the same structure. Caching is separate for each stage.",
		"contact": {
			"name": "SWR Audio Lab",
			"email": "lab@swr.de"
		},
		"license": {
			"name": "Â© SWR Audio Lab"
		},
		"version": "2.14.0"
	},
	"servers": [
		{
			"url": "https://api.lab.swr.de/radiohub",
			"description": "Radiohub production"
		},
		{
			"url": "https://api.lab.swr.de/radiohub-dev",
			"description": "Radiohub development"
		},
		{
			"url": "https://api.lab.swr.de/radiohub-stage",
			"description": "Radiohub staging"
		}
	],
	"paths": {
		"/v2/content/page/{identifier}/{query}": {
			"get": {
				"tags": ["content"],
				"summary": "Get a preconfigured content index page",
				"description": "Receive all teaser elements for a custom page",
				"parameters": [
					{
						"name": "identifier",
						"in": "path",
						"description": "Which identifier shall be used to query this content",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"enum": ["ref"]
						}
					},
					{
						"name": "query",
						"in": "path",
						"description": "Page ref",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string"
						},
						"example": "app-news"
					},
					{
						"name": "appId",
						"in": "query",
						"description": "Provide a valid appId to create a connection to user preference and station",
						"required": true,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "string"
						}
					}
				],
				"responses": {
					"500": {
						"description": "Server error occured."
					},
					"default": {
						"description": "If status code other than 200, an error message will be provided, otherwise the cover will be returned as image.",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/contentPageResponse"
								}
							}
						}
					}
				},
				"security": [
					{
						"BasicAuth": []
					}
				]
			}
		},
		"/v2/content/block/{identifier}/{query}": {
			"get": {
				"tags": ["content"],
				"summary": "Get a content.block index",
				"description": "Receive the full content.block elements",
				"parameters": [
					{
						"name": "identifier",
						"in": "path",
						"description": "Which identifier shall be used to query this content",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"enum": ["id"]
						}
					},
					{
						"name": "query",
						"in": "path",
						"description": "Block ID",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "integer"
						}
					},
					{
						"name": "appId",
						"in": "query",
						"description": "Provide a valid appId to create a connection to user preference and station",
						"required": true,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "string"
						}
					}
				],
				"responses": {
					"500": {
						"description": "Server error occured."
					},
					"default": {
						"description": "If status code other than 200, an error message will be provided, otherwise the cover will be returned as image.",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/contentBlockResponse"
								}
							}
						}
					}
				},
				"security": [
					{
						"BasicAuth": []
					}
				]
			}
		},
		"/v2/content/collection/{identifier}/{query}": {
			"get": {
				"tags": ["content"],
				"summary": "Get an entire content.collection",
				"description": "Receive all recent entries for a content collection by their reference.",
				"parameters": [
					{
						"name": "identifier",
						"in": "path",
						"description": "Which identifier shall be used to query this content",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"enum": ["id", "ref"]
						}
					},
					{
						"name": "query",
						"in": "path",
						"description": "Collection ref or ID",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string"
						},
						"example": "songtinder-sammler-100"
					}
				],
				"responses": {
					"500": {
						"description": "Server error occured."
					},
					"default": {
						"description": "If status code other than 200, an error message will be provided, otherwise the cover will be returned as image.",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/contentCollectionResponse"
								}
							}
						}
					}
				},
				"security": [
					{
						"BasicAuth": []
					}
				]
			}
		},
		"/v2/content/collection/latest/{identifier}/{query}/{offset}": {
			"get": {
				"tags": ["content"],
				"summary": "Get latest content.entry from content.collection",
				"description": "Receive a content object by the content collection id.",
				"parameters": [
					{
						"name": "identifier",
						"in": "path",
						"description": "Which identifier shall be used to query this content",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"enum": ["id", "ref"]
						}
					},
					{
						"name": "query",
						"in": "path",
						"description": "Radiohub import ID",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string"
						},
						"example": "4539478696787968"
					},
					{
						"name": "offset",
						"in": "path",
						"description": "Offset to the newest item. 0 is newest, higher will be older items. Max value is defined by `offsetMax` in the info response object",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "number"
						}
					}
				],
				"responses": {
					"500": {
						"description": "Server error occured."
					},
					"default": {
						"description": "If status code other than 200, an error message will be provided, otherwise the cover will be returned as image.",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/contentEntryResponse"
								}
							}
						}
					}
				},
				"security": [
					{
						"BasicAuth": []
					}
				]
			}
		},
		"/v2/content/entry/{identifier}/{query}": {
			"get": {
				"tags": ["content"],
				"summary": "Get a specific content.entry",
				"description": "Receive a content.entry by id or ref.",
				"parameters": [
					{
						"name": "identifier",
						"in": "path",
						"description": "Which identifier shall be used to query this content",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"enum": ["id", "ref"]
						}
					},
					{
						"name": "query",
						"in": "path",
						"description": "Radiohub ID or ref",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string"
						},
						"example": "4539478696787968"
					}
				],
				"responses": {
					"500": {
						"description": "Server error occured."
					},
					"default": {
						"description": "If status code other than 200, an error message will be provided, otherwise the cover will be returned as image.",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "#/components/schemas/contentEntryResponse"
								}
							}
						}
					}
				},
				"security": [
					{
						"BasicAuth": []
					}
				]
			}
		},
		"/v2/content/thumb/{ref}/{format}/img.jpg": {
			"get": {
				"tags": ["content", "images"],
				"summary": "Load a prefetched image from SWR CMS",
				"description": "Load the original image or resized version. Uncached images will be generated and served from storage.",
				"parameters": [
					{
						"name": "ref",
						"in": "path",
						"description": "Set ref for the image that shall be displayed (`thumbRef`).",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string"
						}
					},
					{
						"name": "format",
						"in": "path",
						"description": "Set the aspect ratio for the image",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"enum": ["16x9", "1x1"]
						}
					},
					{
						"name": "width",
						"in": "query",
						"description": "Optional width in pixels that the image shall be resized to",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"maximum": 2560,
							"minimum": 2,
							"type": "number"
						}
					},
					{
						"name": "height",
						"in": "query",
						"description": "Optional height in pixels that the image shall be resized to. If both width and height are set, then the image will be cropped.  Otherwise the respectively opposite dimension is set to auto",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"maximum": 2560,
							"minimum": 2,
							"type": "number"
						}
					},
					{
						"name": "fileType",
						"in": "query",
						"description": "Force the output to a specific file type. If unset, the output will be jpg.",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "string",
							"enum": ["jpg", "png", "webp"]
						}
					}
				],
				"responses": {
					"400": {
						"description": "Invalid query input"
					},
					"500": {
						"description": "Server error occured or key not found."
					},
					"default": {
						"description": "Image",
						"content": {
							"image/jpeg": {
								"schema": {
									"type": "string",
									"format": "binary"
								}
							},
							"image/png": {
								"schema": {
									"type": "string",
									"format": "binary"
								}
							},
							"image/webp": {
								"schema": {
									"type": "string",
									"format": "binary"
								}
							}
						}
					}
				},
				"servers": [
					{
						"url": "https://api.lab.swr.de/",
						"description": "This service is acessible through the primary path"
					}
				]
			}
		},
		"/v2/cover/{mdid}/": {
			"get": {
				"tags": ["cover"],
				"summary": "Get a cover based on SWR's mdid",
				"description": "Query Radiohub to get a cover for a played item based on a mdid reference.",
				"parameters": [
					{
						"name": "mdid",
						"in": "path",
						"description": "SWR mdid (starts with M0...)",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string"
						},
						"example": "M0571919.001"
					},
					{
						"name": "width",
						"in": "query",
						"description": "Optionally set a desired width, if no height is provided, the cover stays square-sized, otherwise it will be cropped. Default if unset is 256px, max is 1024px.",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "integer"
						}
					},
					{
						"name": "height",
						"in": "query",
						"description": "Optionally set a desired height, if no width is provided, the cover stays square-sized, otherwise it will be cropped",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "integer"
						}
					},
					{
						"name": "station",
						"in": "query",
						"description": "Apply a branding to the image. Branded covers are only available if this station has ever played that track.",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "string",
							"enum": [
								"swr1bw",
								"swr1rp",
								"swr2",
								"swr3",
								"swr4bw",
								"swr4rp",
								"dasding"
							]
						}
					}
				],
				"responses": {
					"500": {
						"description": "Server error occured."
					},
					"default": {
						"description": "If status code other than 200, an error message will be provided, otherwise the cover will be returned as image.",
						"content": {
							"image/*": {
								"schema": {
									"type": "string",
									"format": "binary"
								}
							}
						}
					}
				}
			}
		},
		"/v2/feedback/station/": {
			"post": {
				"tags": ["feedback"],
				"summary": "Publish an inbound feedback message",
				"description": "Send a feedback message from an external device or platform to SWR's feedback system.",
				"parameters": [
					{
						"name": "appId",
						"in": "query",
						"description": "Provide a valid `appId`.",
						"required": true,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "string"
						}
					}
				],
				"requestBody": {
					"description": "- When posting a feedback entry, `name` (body) and `appId` (query) are always required.\n- `city` is only optional for _dasding_, otherwise required as well.\n- Each post needs to contain either `file` or `message`.\n- The field `file` can be an image (e.g. jpg or png), audio (e.g. aac or mp3) or video (e.g. mp4 or mov) up to 50MB.\n- The station will be determined based on the user's `appId`.\n",
					"content": {
						"multipart/form-data": {
							"schema": {
								"$ref": "#/components/schemas/feedbackBody"
							}
						}
					},
					"required": true
				},
				"responses": {
					"200": {
						"description": "If status code other than 200, an error message will be provided, otherwise message is null."
					},
					"400": {
						"description": "Query not allowed or appId not provided."
					},
					"413": {
						"description": "File too large (>50MB)."
					},
					"500": {
						"description": "Server error occured."
					}
				},
				"security": [
					{
						"BasicAuth": []
					}
				]
			}
		},
		"/images/v1/get/{key}/img": {
			"get": {
				"tags": ["images"],
				"summary": "Load an image from image service",
				"description": "Load the original image or resized version. A list of all keys can be seen in the SWR Audio Lab dashboard. Contact lab@swr.de for access. Uncached images will be generated and served from storage. See response header \"swr-datasource\" for details. Clients should also respect cache control if possible.",
				"parameters": [
					{
						"name": "key",
						"in": "path",
						"description": "Set key for the image that shall be displayed.",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"example": "DASDING-512"
						}
					},
					{
						"name": "width",
						"in": "query",
						"description": "Optional width in pixels that the image shall be resized to. Minimum: 1, maximum 2560px",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"maximum": 2560,
							"minimum": 2,
							"type": "number"
						}
					},
					{
						"name": "height",
						"in": "query",
						"description": "Optional height in pixels that the image shall be resized to. Minimum: 1, maximum 2560px. If both width and height are set, then the image will be cropped. Otherwise the respectively opposite dimension is set to auto",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"maximum": 2560,
							"minimum": 2,
							"type": "number"
						}
					},
					{
						"name": "fileType",
						"in": "query",
						"description": "Force the output to a specific file type. If unset, the output will match the input file type.",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "string",
							"enum": ["jpg", "png", "webp"]
						}
					}
				],
				"responses": {
					"400": {
						"description": "Invalid query input"
					},
					"500": {
						"description": "Server error occured or key not found."
					},
					"default": {
						"description": "Image",
						"content": {
							"image/jpeg": {
								"schema": {
									"type": "string",
									"format": "binary"
								}
							},
							"image/png": {
								"schema": {
									"type": "string",
									"format": "binary"
								}
							},
							"image/webp": {
								"schema": {
									"type": "string",
									"format": "binary"
								}
							}
						}
					}
				},
				"servers": [
					{
						"url": "https://api.lab.swr.de/",
						"description": "This service is acessible through the primary path"
					}
				]
			}
		},
		"/images/v1/get/{key}/json": {
			"get": {
				"tags": ["images"],
				"summary": "Receive details about a cacheable image",
				"description": "Similar to the image service endpoint but here only a JSON object of the database entry will be served. Should only be used for debugging solutions, not to download an image. Accessibility of the sourceUrl is not guaranteed and will be limited in future versions.",
				"parameters": [
					{
						"name": "key",
						"in": "path",
						"description": "Set key for the image that shall be displayed. A list of all keys can be seen in the SWR Audio Lab dashboard. Contact lab@swr.de for access.",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"example": "DASDING-512"
						}
					}
				],
				"responses": {
					"401": {
						"description": "Unauthorized access to API."
					},
					"500": {
						"description": "Server error occured or key not found."
					},
					"default": {
						"description": "JSON object",
						"content": {
							"image/json": {
								"schema": {
									"type": "object"
								}
							}
						}
					}
				},
				"security": [
					{
						"BasicAuth": []
					}
				],
				"servers": [
					{
						"url": "https://api.lab.swr.de/",
						"description": "This service is acessible through the primary path"
					}
				]
			}
		},
		"/swr2/images/get/{key}/{format}/{overlay}/{width}/img.jpg": {
			"get": {
				"tags": ["images"],
				"summary": "Load an image from swr2 image service",
				"description": "Load the original image or resized version. A list of all keys can be seen in the SWR Audio Lab dashboard. Contact lab@swr.de for access. Uncached images will be generated and served from storage. See response header \"swr-datasource\" for details. Clients should also respect cache control if possible.",
				"parameters": [
					{
						"name": "key",
						"in": "path",
						"description": "Set key for the image that shall be displayed.",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"example": "logo"
						}
					},
					{
						"name": "format",
						"in": "path",
						"description": "Set the aspect ratio for the image. rund is a 1x1 image which is title safe for round usage.",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"enum": ["16x9", "4x3", "1x1", "rund"]
						}
					},
					{
						"name": "overlay",
						"in": "path",
						"description": "Toggle whether a swr2 logo overlay shall be included. This feature is end of life and you should always request the 0 version.",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"enum": ["0", "1"]
						}
					},
					{
						"name": "width",
						"in": "path",
						"description": "Width in pixels that the image shall be resized to",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"maximum": 2560,
							"minimum": 2,
							"type": "number",
							"example": 256
						}
					}
				],
				"responses": {
					"400": {
						"description": "Invalid query input"
					},
					"500": {
						"description": "Server error occured or key not found."
					},
					"default": {
						"description": "Image",
						"content": {
							"image/jpeg": {
								"schema": {
									"type": "string",
									"format": "binary"
								}
							}
						}
					}
				},
				"servers": [
					{
						"url": "https://api.lab.swr.de/",
						"description": "This service is acessible through the primary path"
					}
				]
			}
		},
		"/swr2/images/get/{key}/json": {
			"get": {
				"tags": ["images"],
