		"/v2/stream/start/{station}/": {
			"get": {
				"tags": ["stream"],
				"summary": "Start a linear non-interactive radio stream based on station and parameters",
				"description": "Linking service to provide the latest streamUrl with certain given parameters.\n\nFor SWR classic an additional parameter `isLive` is provided in the JSON output (see example response below).\n\nSome requests require an authentication with special permissions (`swrRadiohubStreamStart`) - Especially responsive radio triggered by the y parameter, those responses also feature a different JSON structure.\nNote Interactive radio sessions no longer use this call.",
				"parameters": [
					{
						"name": "station",
						"in": "path",
						"description": "Set the radio station. \n\nNote that not all stations provide all features such as video oder skippable radio.",
						"required": true,
						"style": "simple",
						"explode": false,
						"schema": {
							"type": "string",
							"enum": ["swr3", "dasding", "swrclassic"]
						}
					},
					{
						"name": "r",
						"in": "query",
						"description": "_(returnData)_ \n\nChoose whether to redirect to streamUrl or receive JSON data object.",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "boolean"
						}
					},
					{
						"name": "f",
						"in": "query",
						"description": "_(desiredFormat)_ \n\nSet the streaming format. Note that video is not available in combination with all queries. \n\nDefaults to mp3.",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "string",
							"enum": ["mp3", "aac", "video"]
						}
					},
					{
						"name": "q",
						"in": "query",
						"description": "_(desiredQuality)_ \n\nSet the streaming quality. Note that adaptive is only available for f=video while high and low are for mp3/aac.\n\nDefaults to low.",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "string",
							"enum": ["high", "low", "adaptive"]
						}
					},
					{
						"name": "d",
						"in": "query",
						"description": "_(deviceType)_ \n\nSet the type of device and/or aggregator. Example list is not complete and only serves as a reference. Names are provided by SWR Audio Lab.",
						"required": false,
						"style": "form",
						"explode": true,
						"schema": {
							"type": "string",
							"enum": [
								"amazonAlexaSkill",
								"googleHomeAction",
								"swrClassicAppleTv"
							]
						}
					}
				],
				"responses": {
					"400": {
						"description": "Query not allowed."
					},
					"500": {
						"description": "Server error occured or key not found."
					},
					"default": {
						"description": "JSON object",
						"content": {
							"image/json": {
								"schema": {
									"type": "object"
								}
							}
						}
					},
					"x-swrclassic": {
						"description": "Example of a result where `station` is `swrclassic` to signalize live modus.",
						"content": {
							"image/json": {
								"schema": {
									"type": "object",
									"properties": {
										"status": {
											"type": "integer",
											"format": "int64",
											"example": 200
										},
										"message": {
											"type": "string",
											"description": "Message will be provided if status is not 200, otherwise null"
										},
										"data": {
											"type": "object",
											"properties": {
												"isLive": {
													"type": "boolean",
													"example": true
												},
												"streamUrl": {
													"type": "string",
													"example": "https://...m3u8"
												},
												"streamTitle": {
													"type": "string",
													"example": "Live aus..."
												},
												"updatedAt": {
													"type": "integer",
													"format": "int64",
													"example": 1563785270
												},
												"airtimeStart": {
													"type": "integer",
													"format": "int64",
													"example": 1563692428
												},
												"airtimeEnd": {
													"type": "integer",
													"format": "int64",
													"example": 1563859828
												}
											}
										},
										"info": {
											"$ref": "#/components/schemas/infoResponse"
										},
										"links": {
											"type": "object"
										}
									}
								}
							}
						}
					}
				}
			}
		},
