{
   "components": {
      "schemas": {
         "definition_list": {
            "items": {
               "properties": {
                  "name": {
                     "type": "string"
                  },
                  "title": {
                     "type": "string"
                  }
               },
               "required": [
                  "title",
                  "name"
               ]
            },
            "type": "array"
         },
         "esqlate_argument": {
            "additionalProperties": false,
            "properties": {
               "name": {
                  "type": "string"
               },
               "value": {
                  "anyOf": [
                     {
                        "type": "number"
                     },
                     {
                        "type": "string"
                     },
                     {
                        "type": "integer"
                     },
                     {
                        "type": "boolean"
                     }
                  ]
               }
            },
            "required": [
               "name",
               "value"
            ],
            "type": "object"
         },
         "esqlate_complete_result_other_format": {
            "additionalProperties": false,
            "properties": {
               "location": {
                  "type": "string"
               },
               "type": {
                  "type": "string"
               }
            },
            "required": [
               "url",
               "type"
            ],
            "type": "object"
         },
         "esqlate_definition": {
            "additionalProperties": false,
            "properties": {
               "description": {
                  "description": "An opportunity to explain more...",
                  "type": "string"
               },
               "links": {
                  "items": {
                     "$ref": "#/components/schemas/esqlate_link"
                  },
                  "type": "array"
               },
               "name": {
                  "description": "unique id",
                  "pattern": "^_?[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "parameters": {
                  "items": {
                     "$ref": "#/components/schemas/esqlate_parameter"
                  },
                  "type": "array"
               },
               "row_links": {
                  "items": {
                     "$ref": "#/components/schemas/esqlate_link"
                  },
                  "type": "array"
               },
               "statement": {
                  "$ref": "#/components/schemas/esqlate_statement"
               },
               "statement_type": {
                  "enum": [
                     "SELECT",
                     "DELETE",
                     "UPDATE",
                     "INSERT",
                     "OTHER"
                  ],
                  "type": "string"
               },
               "title": {
                  "description": "The title for the Definition",
                  "type": "string"
               },
               "top_links": {
                  "items": {
                     "$ref": "#/components/schemas/esqlate_link"
                  },
                  "type": "array"
               }
            },
            "required": [
               "name",
               "title",
               "statement",
               "parameters"
            ],
            "type": "object"
         },
         "esqlate_error_result": {
            "additionalProperties": false,
            "properties": {
               "message": {
                  "type": "string"
               },
               "status": {
                  "enum": [
                     "error"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "status"
            ],
            "type": "object"
         },
         "esqlate_field_definition": {
            "additionalProperties": false,
            "properties": {
               "name": {
                  "type": "string"
               },
               "type": {
                  "type": "string"
               }
            },
            "required": [
               "name",
               "type"
            ],
            "type": "object"
         },
         "esqlate_link": {
            "additionalProperties": false,
            "description": "Will show a link in the result set allowing navigation to different URL (internal and external)",
            "properties": {
               "class": {
                  "type": "string"
               },
               "href": {
                  "description": "Application follows interpolonation rules where ${your_field} will be replaced by the your_field from the result set",
                  "type": "string"
               },
               "text": {
                  "description": "Application follows interpolonation rules where ${your_field} will be replaced by the your_field from the result set",
                  "type": "string"
               }
            },
            "required": [
               "href"
            ],
            "type": "object"
         },
         "esqlate_other_format_url": {
            "additionalProperties": false,
            "properties": {
               "mime_type": {
                  "type": "string"
               },
               "url": {
                  "type": "string"
               }
            },
            "required": [
               "mime_type",
               "url"
            ],
            "type": "object"
         },
         "esqlate_parameter": {
            "discriminator": {
               "mapping": {
                  "date": "#/components/schemas/esqlate_parameter_date",
                  "datetime": "#/components/schemas/esqlate_parameter_datetime",
                  "decimal": "#/components/schemas/esqlate_parameter_decimal",
                  "integer": "#/components/schemas/esqlate_parameter_integer",
                  "popup": "#/components/schemas/esqlate_parameter_popup",
                  "select": "#/components/schemas/esqlate_parameter_select",
                  "server": "#/components/schemas/esqlate_parameter_server",
                  "static": "#/components/schemas/esqlate_parameter_static",
                  "string": "#/components/schemas/esqlate_parameter_string"
               },
               "propertyName": "type"
            },
            "oneOf": [
               {
                  "$ref": "#/components/schemas/esqlate_parameter_select"
               },
               {
                  "$ref": "#/components/schemas/esqlate_parameter_datetime"
               },
               {
                  "$ref": "#/components/schemas/esqlate_parameter_date"
               },
               {
                  "$ref": "#/components/schemas/esqlate_parameter_integer"
               },
               {
                  "$ref": "#/components/schemas/esqlate_parameter_decimal"
               },
               {
                  "$ref": "#/components/schemas/esqlate_parameter_static"
               },
               {
                  "$ref": "#/components/schemas/esqlate_parameter_string"
               },
               {
                  "$ref": "#/components/schemas/esqlate_parameter_popup"
               },
               {
                  "$ref": "#/components/schemas/esqlate_parameter_server"
               }
            ]
         },
         "esqlate_parameter_date": {
            "additionalProperties": false,
            "properties": {
               "empty_string_is_null": {
                  "type": "boolean"
               },
               "highlight_fields": {
                  "items": {
                     "type": "string"
                  },
                  "type": "array"
               },
               "name": {
                  "pattern": "^[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "type": {
                  "enum": [
                     "date"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "name",
               "type"
            ]
         },
         "esqlate_parameter_datetime": {
            "additionalProperties": false,
            "properties": {
               "empty_string_is_null": {
                  "type": "boolean"
               },
               "highlight_fields": {
                  "items": {
                     "type": "string"
                  },
                  "type": "array"
               },
               "name": {
                  "pattern": "^[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "type": {
                  "enum": [
                     "datetime"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "name",
               "type"
            ]
         },
         "esqlate_parameter_decimal": {
            "additionalProperties": false,
            "properties": {
               "decimal_places": {
                  "description": "How many decimal places should be allowed",
                  "type": "number"
               },
               "empty_string_is_null": {
                  "type": "boolean"
               },
               "highlight_fields": {
                  "items": {
                     "type": "string"
                  },
                  "type": "array"
               },
               "name": {
                  "pattern": "^[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "type": {
                  "enum": [
                     "decimal"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "name",
               "type",
               "decimal_places"
            ]
         },
         "esqlate_parameter_integer": {
            "additionalProperties": false,
            "properties": {
               "empty_string_is_null": {
                  "type": "boolean"
               },
               "highlight_fields": {
                  "items": {
                     "type": "string"
                  },
                  "type": "array"
               },
               "name": {
                  "pattern": "^[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "type": {
                  "enum": [
                     "integer"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "name",
               "type"
            ]
         },
         "esqlate_parameter_popup": {
            "additionalProperties": false,
            "properties": {
               "definition": {
                  "type": "string"
               },
               "display_field": {
                  "type": "string"
               },
               "empty_string_is_null": {
                  "type": "boolean"
               },
               "highlight_fields": {
                  "items": {
                     "type": "string"
                  },
                  "type": "array"
               },
               "name": {
                  "pattern": "^[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "type": {
                  "enum": [
                     "popup"
                  ],
                  "type": "string"
               },
               "value_field": {
                  "type": "string"
               }
            },
            "required": [
               "name",
               "type",
               "definition",
               "value_field",
               "display_field"
            ]
         },
         "esqlate_parameter_select": {
            "additionalProperties": false,
            "properties": {
               "definition": {
                  "description": "The name of a definition to run",
                  "type": "string"
               },
               "display_field": {
                  "type": "string"
               },
               "empty_string_is_null": {
                  "type": "boolean"
               },
               "highlight_fields": {
                  "items": {
                     "type": "string"
                  },
                  "type": "array"
               },
               "name": {
                  "pattern": "^[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "type": {
                  "enum": [
                     "select"
                  ],
                  "type": "string"
               },
               "value_field": {
                  "type": "string"
               }
            },
            "required": [
               "name",
               "type",
               "value_field",
               "definition",
               "display_field"
            ]
         },
         "esqlate_parameter_server": {
            "additionalProperties": false,
            "properties": {
               "empty_string_is_null": {
                  "type": "boolean"
               },
               "highlight_fields": {
                  "items": {
                     "type": "string"
                  },
                  "type": "array"
               },
               "name": {
                  "pattern": "^[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "type": {
                  "enum": [
                     "server"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "type",
               "name"
            ],
            "type": "object"
         },
         "esqlate_parameter_static": {
            "additionalProperties": false,
            "properties": {
               "empty_string_is_null": {
                  "type": "boolean"
               },
               "highlight_fields": {
                  "items": {
                     "type": "string"
                  },
                  "type": "array"
               },
               "name": {
                  "pattern": "^[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "type": {
                  "enum": [
                     "static"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "name",
               "type"
            ]
         },
         "esqlate_parameter_string": {
            "additionalProperties": false,
            "properties": {
               "empty_string_is_null": {
                  "type": "boolean"
               },
               "highlight_fields": {
                  "items": {
                     "type": "string"
                  },
                  "type": "array"
               },
               "name": {
                  "pattern": "^[a-z][a-z0-9_]{0,99}$",
                  "type": "string"
               },
               "type": {
                  "enum": [
                     "string"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "name",
               "type"
            ]
         },
         "esqlate_request": {
            "additionalProperties": false,
            "properties": {
               "arguments": {
                  "$ref": "#/components/schemas/esqlate_request_argument_set"
               },
               "location": {
                  "description": "The status of a request",
                  "type": "string"
               },
               "status": {
                  "enum": [
                     "complete",
                     "pending"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "status",
               "arguments"
            ],
            "type": "object"
         },
         "esqlate_request_argument": {
            "additionalProperties": false,
            "properties": {
               "name": {
                  "type": "string"
               },
               "value": {
                  "anyOf": [
                     {
                        "type": "number"
                     },
                     {
                        "type": "string"
                     },
                     {
                        "type": "integer"
                     },
                     {
                        "type": "boolean"
                     }
                  ]
               }
            },
            "required": [
               "name",
               "value"
            ],
            "type": "object"
         },
         "esqlate_request_argument_set": {
            "items": {
               "$ref": "#/components/schemas/esqlate_request_argument"
            },
            "type": "array"
         },
         "esqlate_request_creation": {
            "properties": {
               "arguments": {
                  "items": {
                     "$ref": "#/components/schemas/esqlate_argument"
                  },
                  "type": "array"
               }
            },
            "required": [
               "arguments"
            ],
            "type": "object"
         },
         "esqlate_request_creation_response": {
            "additionalProperties": false,
            "properties": {
               "location": {
                  "type": "string"
               }
            },
            "required": [
               "location"
            ],
            "type": "object"
         },
         "esqlate_result": {
            "discriminator": {
               "mapping": {
                  "complete": "#/components/schemas/esqlate_success_result",
                  "error": "#/components/schemas/esqlate_error_result",
                  "preview": "#/components/schemas/esqlate_success_result"
               },
               "propertyName": "status"
            },
            "oneOf": [
               {
                  "$ref": "#/components/schemas/esqlate_success_result"
               },
               {
                  "$ref": "#/components/schemas/esqlate_error_result"
               }
            ]
         },
         "esqlate_statement": {
            "oneOf": [
               {
                  "type": "string"
               },
               {
                  "$ref": "#/components/schemas/esqlate_statement_normalized"
               }
            ]
         },
         "esqlate_statement_normalized": {
            "items": {
               "anyOf": [
                  {
                     "type": "string"
                  },
                  {
                     "$ref": "#/components/schemas/esqlate_parameter"
                  }
               ]
            },
            "type": "array"
         },
         "esqlate_success_result": {
            "additionalProperties": false,
            "properties": {
               "fields": {
                  "items": {
                     "$ref": "#/components/schemas/esqlate_field_definition"
                  },
                  "type": "array"
               },
               "full_data_set": {
                  "description": "When true the results herein are only partial",
                  "type": "boolean"
               },
               "full_format_urls": {
                  "description": "Locations of the results in other formats",
                  "items": {
                     "$ref": "#/components/schemas/esqlate_complete_result_other_format"
                  },
                  "type": "array"
               },
               "rows": {
                  "items": {
                     "$ref": "#/components/schemas/esqlate_success_result_row"
                  },
                  "type": "array"
               },
               "status": {
                  "enum": [
                     "preview",
                     "complete"
                  ],
                  "type": "string"
               }
            },
            "required": [
               "status",
               "fields",
               "rows"
            ],
            "type": "object"
         },
         "esqlate_success_result_row": {
            "items": {
               "anyOf": [
                  {
                     "type": "number"
                  },
                  {
                     "type": "string"
                  },
                  {
                     "type": "integer"
                  },
                  {
                     "type": "boolean"
                  }
               ]
            },
            "type": "array"
         }
      }
   },
   "externalDocs": {
      "description": "Esqlate Project Homepage",
      "url": "http://github.com/forbesmyester/esqlate"
   },
   "info": {
      "contact": {
         "email": "github.com@speechmarks.com"
      },
      "license": {
         "name": "MIT",
         "url": "https://opensource.org/licenses/MIT"
      },
      "title": "Esqlate",
      "version": "0.0.1"
   },
   "openapi": "3.0.1",
   "paths": {
      "/": {
         "get": {
            "operationId": "list",
            "responses": {
               "200": {
                  "content": {
                     "application/json": {
                        "schema": {
                           "$ref": "#/components/schemas/definition_list"
                        }
                     }
                  },
                  "description": "successful operation"
               }
            },
            "summary": "Gets a list of definition",
            "tags": [ ]
         }
      },
      "/definition/{definitionName}": {
         "get": {
            "operationId": "getDefinition",
            "parameters": [
               {
                  "description": "Name of the definition",
                  "in": "path",
                  "name": "definitionName",
                  "required": true,
                  "schema": {
                     "type": "string"
                  }
               }
            ],
            "responses": {
               "200": {
                  "content": {
                     "application/json": {
                        "schema": {
                           "$ref": "#/components/schemas/esqlate_definition"
                        }
                     }
                  },
                  "description": "successful operation"
               }
            },
            "summary": "Gets a definition",
            "tags": [ ]
         }
      },
      "/demand/{definitionName}": {
         "post": {
            "operationId": "demand",
            "parameters": [
               {
                  "description": "Name of the definition",
                  "in": "path",
                  "name": "definitionName",
                  "required": true,
                  "schema": {
                     "type": "string"
                  }
               }
            ],
            "requestBody": {
               "content": {
                  "application/json": {
                     "schema": {
                        "$ref": "#/components/schemas/esqlate_request_creation"
                     }
                  }
               },
               "required": true
            },
            "responses": {
               "200": {
                  "content": {
                     "application/json": {
                        "schema": {
                           "$ref": "#/components/schemas/esqlate_result"
                        }
                     }
                  },
                  "description": "The final results"
               }
            },
            "summary": "Runs a Request now, with Result delivered immediately"
         }
      },
      "/request/{definitionName}": {
         "post": {
            "operationId": "createRequest",
            "parameters": [
               {
                  "description": "If set to true, 200 responses will be sent instead of 301",
                  "in": "header",
                  "name": "x-no-redirect"
               },
               {
                  "description": "Name of the definition",
                  "in": "path",
                  "name": "definitionName",
                  "required": true,
                  "schema": {
                     "type": "string"
                  }
               }
            ],
            "requestBody": {
               "content": {
                  "application/json": {
                     "schema": {
                        "$ref": "#/components/schemas/esqlate_request_creation"
                     }
                  }
               },
               "required": true
            },
            "responses": {
               "200": {
                  "content": {
                     "application/json": {
                        "schema": {
                           "$ref": "#/components/schemas/esqlate_request_creation_response"
                        }
                     }
                  },
                  "description": "successful operation with x-no-redirect parameter",
                  "headers": {
                     "Location": {
                        "description": "Where to monitor the status of the Request",
                        "schema": {
                           "type": "string"
                        }
                     }
                  }
               },
               "301": {
                  "content": {
                     "application/json": {
                        "schema": {
                           "$ref": "#/components/schemas/esqlate_request_creation_response"
                        }
                     }
                  },
                  "description": "successful operation",
                  "headers": {
                     "Location": {
                        "description": "Where to monitor the status of the Request",
                        "schema": {
                           "type": "string"
                        }
                     }
                  }
               }
            },
            "summary": "Puts a Request on the queue, which will eventually create Result"
         }
      },
      "/request/{definitionName}/{requestId}": {
         "get": {
            "operationId": "getRequest",
            "parameters": [
               {
                  "description": "If set to true, 200 responses will be sent instead of 301",
                  "in": "header",
                  "name": "x-no-redirect"
               },
               {
                  "description": "The Id of the Request",
                  "in": "path",
                  "name": "requestId",
                  "required": true,
                  "schema": {
                     "type": "string"
                  }
               },
               {
                  "description": "Name of the definition",
                  "in": "path",
                  "name": "definitionName",
                  "required": true,
                  "schema": {
                     "type": "string"
                  }
               }
            ],
            "responses": {
               "200": {
                  "content": {
                     "application/json": {
                        "schema": {
                           "$ref": "#/components/schemas/esqlate_request"
                        }
                     }
                  },
                  "description": "successful operation with x-no-redirect parameter",
                  "headers": {
                     "Location": {
                        "description": "The status of a request",
                        "schema": {
                           "type": "string"
                        }
                     }
                  }
               },
               "202": {
                  "content": {
                     "application/json": {
                        "schema": {
                           "$ref": "#/components/schemas/esqlate_request"
                        }
                     }
                  },
                  "description": "has not been processed yet"
               },
               "301": {
                  "content": {
                     "application/json": {
                        "schema": {
                           "$ref": "#/components/schemas/esqlate_request"
                        }
                     }
                  },
                  "description": "successful operation",
                  "headers": {
                     "Location": {
                        "description": "The status of a request",
                        "schema": {
                           "type": "string"
                        }
                     }
                  }
               }
            },
            "summary": "Gets the details of a Request",
            "tags": [ ]
         }
      },
      "/result/{definitionName}/{resultId}": {
         "get": {
            "operationId": "getResults",
            "parameters": [
               {
                  "description": "Name of the definition",
                  "in": "path",
                  "name": "definitionName",
                  "required": true,
                  "schema": {
                     "type": "string"
                  }
               },
               {
                  "description": "The Id of the Result",
                  "in": "path",
                  "name": "resultId",
                  "required": true,
                  "schema": {
                     "type": "string"
                  }
               }
            ],
            "responses": {
               "200": {
                  "content": {
                     "application/json": {
                        "schema": {
                           "$ref": "#/components/schemas/esqlate_result"
                        }
                     }
                  },
                  "description": "The final results"
               }
            },
            "summary": "Gets the (maybe only some) Results created from a Request",
            "tags": [ ]
         }
      },
      "/result/{definitionName}/{resultId}.csv": {
         "get": {
            "operationId": "getResults",
            "parameters": [
               {
                  "description": "Name of the definition",
                  "in": "path",
                  "name": "definitionName",
                  "required": true,
                  "schema": {
                     "type": "string"
                  }
               },
               {
                  "description": "The Id of the Result",
                  "in": "path",
                  "name": "resultId",
                  "required": true,
                  "schema": {
                     "type": "string"
                  }
               }
            ],
            "responses": {
               "200": {
                  "content": {
                     "text/csv": { }
                  },
                  "description": "The final results"
               }
            },
            "summary": "Gets the (all of the) Results created from a Request in CSV format",
            "tags": [ ]
         }
      }
   },
   "servers": [
      {
         "url": "http://petstore.swagger.io/api/v2"
      }
   ]
}
