              "proxySpec": {
                "type": "object",
                "properties": {
                  "arcasVm": {
                    "type": "object",
                    "properties": {
                      "enableProxy": { "type": "string" },
                      "httpProxy": { "type": "string" },
                      "httpsProxy": { "type": "string" },
                      "noProxy": { "type": "string" }
