            "name": "LayerName",
            "in": "path",
            "required": true,
            "description": "The name or Amazon Resource Name (ARN) of the layer.",
            "schema": {
              "type": "string",
              "pattern": "(arn:[a-zA-Z0-9-]+:lambda:[a-zA-Z0-9-]+:\\d{12}:layer:[a-zA-Z0-9-_]+)|[a-zA-Z0-9-_]+",
              "minLength": 1,
