      "get": {
        "operationId": "GetPolicy",
        "description": "Returns the <a href=\"https://docs.aws.amazon.com/lambda/latest/dg/access-control-resource-based.html\">resource-based IAM policy</a> for a function, version, or alias.",
        "responses": {
