{
    "openapi": "3.0.3",
    "info": {
        "title": "E-Commerce API",
        "description": "As the title, this is API documentation of my simple E-Commerce API. We have deployed it on the internet with host `tp-o.tk` (You can choose it in the selection below). Azure for Students account is used to hire the AKS, so the server may be down whenever free credits run out. If that happens, clone this [repo](https://github.com/tp-o/e-commerce-api/tree/2.x.x) and run it on local environment before accessing API endpoints.",
        "version": "2.x.x",
        "license": {
            "name": "MIT",
            "url": "https://github.com/tp-o/e-commerce-api/blob/2.x.x/LICENSE"
        }
    },
    "servers": [
        {
            "url": "{protocol}://{host}:{port}/api/v2",
            "variables": {
                "protocol": {
                    "default": "https",
                    "enum": [
                        "http",
                        "https"
                    ]
                },
                "host": {
                    "default": "127.0.0.1",
                    "enum": [
                        "127.0.0.1",
                        "localhost",
                        "tp-o.tk"
                    ]
                },
                "port": {
                    "default": "443"
                }
            },
            "description": "Localhost"
        }
    ],
    "tags": [
        {
            "name": "publish",
            "description": "For everyone"
        },
        {
            "name": "signed-in",
            "description": "For signed-in people"
        },
        {
            "name": "user",
            "description": "For users"
        },
        {
            "name": "shop",
            "description": "For users having shop"
        },
        {
            "name": "admin",
            "description": "For admins"
        },
        {
            "name": "resource",
            "description": "Resource management"
        },
        {
            "name": "auth",
            "description": "Authentication"
        },
        {
            "name": "account",
            "description": "Account management"
        },
        {
            "name": "product",
            "description": "Product and related resources management"
        },
        {
            "name": "cart",
            "description": "Shopping cart"
        },
        {
            "name": "order",
            "description": "Order management"
        }
    ],
    "components": {
        "securitySchemes": {
            "bearerAuth": {
                "type": "http",
                "scheme": "bearer",
                "bearerFormat": "token"
            }
        },
        "schemas": {}
    },
    "security": [
        {
            "bearerAuth": []
        }
    ],
    "paths": {
        "/shops/{id_or_slug}": {
            "get": {
                "tags": [
                    "publish"
                ],
                "summary": "Get public information of the shop",
                "operationId": "getShop",
                "parameters": [
                    {
                        "name": "id_or_slug",
                        "in": "path",
                        "description": "ID or slug of shop",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "404": {
                        "description": "Not found"
                    }
                },
                "security": []
            }
        },
        "/shops/{id_or_slug}/products": {
            "get": {
                "tags": [
                    "publish",
                    "product"
                ],
                "summary": "Get publised products of the shop",
                "operationId": "getPublishedProductsOfShop",
                "parameters": [
                    {
                        "name": "id_or_slug",
                        "in": "path",
                        "description": "ID or slug of shop",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "page",
                        "in": "query",
                        "description": "Page number",
                        "schema": {
                            "type": "integer"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "404": {
                        "description": "Not found"
                    }
                },
                "security": []
            }
        },
        "/products/{id}": {
            "get": {
                "tags": [
                    "publish",
                    "product"
                ],
                "summary": "Get product by ID",
                "operationId": "getProduct",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "description": "Product's id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "404": {
                        "description": "Not found"
                    }
                },
                "security": []
            }
        },
        "/products/{id}/reviews": {
            "get": {
                "tags": [
                    "publish",
                    "product"
                ],
                "summary": "Get reviews of the product",
                "operationId": "getProductReview",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "description": "Product's id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        }
                    },
                    {
                        "name": "limit",
                        "in": "query",
                        "description": "Limit number of reviews per page",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "404": {
                        "description": "Not found"
                    }
                },
                "security": []
            }
        },
        "/products/search": {
            "get": {
                "tags": [
                    "publish",
                    "product"
                ],
                "summary": "Search product by keyword and filter",
                "operationId": "searchProduct",
                "parameters": [
                    {
                        "name": "limit",
                        "in": "query",
                        "description": "Limit number of products per page",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        }
                    },
                    {
                        "name": "page",
                        "in": "query",
                        "description": "Page number",
                        "schema": {
                            "type": "integer"
                        }
                    },
                    {
                        "name": "keyword",
                        "in": "query",
                        "description": "Product's keyword (required if filter is not presented)",
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "filter",
                        "in": "query",
                        "description": "Product's filter (required if keyword is not presented)",
                        "schema": {
                            "type": "object",
                            "properties": {
                                "category_ids": {
                                    "type": "array",
                                    "items": {
                                        "type": "integer"
                                    }
                                }
                            }
                        }
                    },
                    {
                        "name": "order_by",
                        "in": "query",
                        "description": "Order by newest, high price to low price, or low price to high price",
                        "required": true,
                        "schema": {
                            "type": "integer",
                            "enum": [
                                1,
                                2,
                                3
                            ]
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invliad input"
                    }
                },
                "security": []
            }
        },
        "/products/feature": {
            "get": {
                "tags": [
                    "publish",
                    "product"
                ],
                "summary": "Get feature products",
                "operationId": "getFeatureProduct",
                "parameters": [
                    {
                        "name": "limit",
                        "in": "query",
                        "description": "Limit number of products per page",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        }
                    },
                    {
                        "name": "page",
                        "in": "query",
                        "description": "Page number",
                        "schema": {
                            "type": "integer"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invliad input"
                    }
                },
                "security": []
            }
        },
        "/products/categories/{id}/children": {
            "get": {
                "tags": [
                    "publish",
                    "product"
                ],
                "summary": "Get children of the category",
                "operationId": "getCategoryChildren",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "description": "Category's id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "404": {
                        "description": "Not found"
                    }
                },
                "security": []
            }
        },
        "/resource/images": {
            "post": {
                "tags": [
                    "signed-in",
                    "resource"
                ],
                "summary": "Upload images (Access /resources/images/<id> to get the image)",
                "operationId": "uploadImages",
                "requestBody": {
                    "content": {
                        "multipart/form-data": {
                            "schema": {
                                "type": "object",
                                "properties": {
                                    "image": {
                                        "type": "string",
                                        "format": "binary",
                                        "description": "Uploaded image"
                                    },
                                    "ratio": {
                                        "type": "integer",
                                        "enum": [
                                            0,
                                            1
                                        ],
                                        "description": "0 is avatar - 1 is banner"
                                    }
                                },
                                "required": [
                                    "image",
                                    "ratio"
                                ]
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invalid input"
                    }
                }
            }
        },
        "/user/auth/sign-up": {
            "post": {
                "tags": [
                    "user",
                    "auth"
                ],
                "summary": "Sign up an user account",
                "operationId": "userSignUp",
                "parameters": [
                    {
                        "name": "username",
                        "in": "query",
                        "description": "Username of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "email",
                        "in": "query",
                        "description": "Email of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "password",
                        "in": "query",
                        "description": "Password of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "password_confirmation",
                        "in": "query",
                        "description": "Confirm the password",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "201": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invliad input"
                    }
                },
                "security": []
            }
        },
        "/user/auth/sign-in": {
            "post": {
                "tags": [
                    "user",
                    "auth"
                ],
                "summary": "Sign in as an user",
                "operationId": "userSignIn",
                "parameters": [
                    {
                        "name": "usernameOrEmail",
                        "in": "query",
                        "description": "Username or email of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "password",
                        "in": "query",
                        "description": "Password of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "201": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invalid input or incorrect credentials"
                    }
                },
                "security": []
            }
        },
        "/user/auth/sign-out": {
            "post": {
                "tags": [
                    "user",
                    "auth"
                ],
                "summary": "Sign out as an user",
                "operationId": "userSignOut",
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "401": {
                        "description": "Missing bearer token"
                    }
                }
            }
        },
        "/user/oauth/{driver}/redirect": {
            "get": {
                "tags": [
                    "user",
                    "auth"
                ],
                "summary": "Sign in with third party",
                "operationId": "oauthRedirect",
                "parameters": [
                    {
                        "name": "driver",
                        "in": "path",
                        "description": "Supported third party",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "enum": [
                                "facebook",
                                "google",
                                "github"
                            ]
                        }
                    }
                ],
                "responses": {
                    "301": {
                        "description": "Success"
                    }
                },
                "security": []
            }
        },
        "/user/email/verify": {
            "post": {
                "tags": [
                    "user",
                    "auth"
                ],
                "summary": "Request to send verification email",
                "operationId": "sendVerificationEmail",
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "401": {
                        "description": "Missing bearer token"
                    }
                }
            }
        },
        "/user/email/verify/{id}/{hash}": {
            "get": {
                "tags": [
                    "user",
                    "auth"
                ],
                "summary": "Verify an user's email",
                "operationId": "verifyUserEmail",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "description": "User id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        }
                    },
                    {
                        "name": "hash",
                        "in": "path",
                        "description": "Unique hash string unique hash generated by user id and secret key",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "signature",
                        "in": "query",
                        "description": "Signature of system",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "401": {
                        "description": "Missing bearer token"
                    },
                    "403": {
                        "description": "Not allow"
                    }
                }
            }
        },
        "/user/password": {
            "put": {
                "tags": [
                    "user",
                    "auth"
                ],
                "summary": "Update user's password",
                "operationId": "updateUserPassword",
                "parameters": [
                    {
                        "name": "password",
                        "in": "query",
                        "description": "Current password",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "password_confirmation",
                        "in": "query",
                        "description": "Confirm current password",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "new_password",
                        "in": "query",
                        "description": "New password",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invalid input"
                    },
                    "401": {
                        "description": "Missing bearer token"
                    }
                }
            }
        },
        "/user/password/forgot": {
            "post": {
                "tags": [
                    "user",
                    "auth"
                ],
                "summary": "Request to send reset-user-password email",
                "operationId": "forgotUserPassowrd",
                "parameters": [
                    {
                        "name": "email",
                        "in": "query",
                        "description": "Email of account forgot password",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invalid input"
                    }
                },
                "security": []
            }
        },
        "/user/password/reset": {
            "post": {
                "tags": [
                    "user",
                    "auth"
                ],
                "summary": "Reset user's password",
                "operationId": "resetUserPassword",
                "parameters": [
                    {
                        "name": "token",
                        "in": "query",
                        "description": "Unique token to reset password (deleted after resetting)",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "email",
                        "in": "query",
                        "description": "Email of account reset password",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "password",
                        "in": "query",
                        "description": "New password",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "passsword_confirmation",
                        "in": "query",
                        "description": "Confirm new password",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invalid input"
                    }
                },
                "security": []
            }
        },
        "/user/account/profile": {
            "get": {
                "tags": [
                    "user",
                    "account"
                ],
                "summary": "Get user's profile",
                "operationId": "getUserProfile",
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "401": {
                        "description": "Missing bearer token"
                    }
                }
            },
            "put": {
                "tags": [
                    "user",
                    "account"
                ],
                "summary": "Update user's profile",
                "operationId": "updateUserProfile",
                "parameters": [
                    {
                        "name": "display_name",
                        "in": "query",
                        "description": "Display name of user",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "phone",
                        "in": "query",
                        "description": "Phone number of user",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "gender",
                        "in": "query",
                        "description": "Gender of user",
                        "required": false,
                        "schema": {
                            "type": "integer",
                            "enum": [
                                1,
                                2,
                                3
                            ]
                        }
                    },
                    {
                        "name": "date_of_birth",
                        "in": "query",
                        "description": "Date of birth of user",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "avatar_image",
                        "in": "query",
                        "description": "Id of image",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invalid input"
                    },
                    "401": {
                        "description": "Missing bearer token"
                    }
                }
            }
        },
        "/user/account/addresses": {
            "get": {
                "tags": [
                    "user",
                    "account"
                ],
                "summary": "Get user's address",
                "operationId": "getAllUserAddresses",
                "responses": {
                    "200": {
                        "description": "Success"
                    },
                    "401": {
                        "description": "Missing bearer token"
                    }
                }
            },
            "post": {
                "tags": [
                    "user",
                    "account"
                ],
                "summary": "Create user's address",
                "operationId": "createUserAddress",
                "parameters": [
                    {
                        "name": "full_name",
                        "in": "query",
                        "description": "Full name of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "phone",
                        "in": "query",
                        "description": "Phone number of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "state",
                        "in": "query",
                        "description": "State of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "city",
                        "in": "query",
                        "description": "City of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "town",
                        "in": "query",
                        "description": "Town of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "address",
                        "in": "query",
                        "description": "Detailed address of user",
                        "required": true,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "is_home",
                        "in": "query",
                        "description": "Mark as home address",
                        "required": false,
                        "schema": {
                            "type": "boolean"
                        }
                    },
                    {
                        "name": "is_pickup_address",
                        "in": "query",
                        "description": "Mark as pickup address",
                        "required": false,
                        "schema": {
                            "type": "boolean"
                        }
                    },
                    {
                        "name": "is_default_address",
                        "in": "query",
                        "description": "Mark as default address",
                        "required": false,
                        "schema": {
                            "type": "boolean"
                        }
                    },
                    {
                        "name": "is_return_address",
                        "in": "query",
                        "description": "Mark as return address",
                        "required": false,
                        "schema": {
                            "type": "boolean"
                        }
                    }
                ],
                "responses": {
                    "201": {
                        "description": "Success"
                    },
                    "400": {
                        "description": "Invalid input"
                    },
                    "401": {
                        "description": "Missing bearer token"
                    }
                }
            }
        },
        "/user/account/addresses/{id}": {
            "put": {
                "tags": [
                    "user",
                    "account"
                ],
                "summary": "Update user's address",
                "operationId": "updateUserAddress",
                "parameters": [
                    {
                        "name": "id",
                        "in": "path",
                        "description": "Address's id",
                        "required": true,
                        "schema": {
                            "type": "integer"
                        }
                    },
                    {
                        "name": "full_name",
                        "in": "query",
                        "description": "Full name of user",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "phone",
                        "in": "query",
                        "description": "Phone number of user",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "state",
                        "in": "query",
                        "description": "State of user",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "city",
                        "in": "query",
                        "description": "City of user",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "town",
                        "in": "query",
                        "description": "Town of user",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "address",
                        "in": "query",
                        "description": "Detailed address of user",
                        "required": false,
                        "schema": {
                            "type": "string"
                        }
                    },
                    {
                        "name": "is_home",
                        "in": "query",
                        "description": "Mark as home address",
                        "required": false,
                        "schema": {
                            "type": "boolean"
                        }
                    },
                    {
                        "name": "is_pickup_address",
                        "in": "query",
                        "description": "Mark as pickup address",
                        "required": false,
                        "schema": {
                            "type": "boolean"
                        }
                    },
                    {
                        "name": "is_default_address",
                        "in": "query",
                        "description": "Mark as default address",
                        "required": false,
                        "schema": {
                            "type": "boolean"
                        }
                    },
