info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Swimlane Email
  title: Swimlane Email
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Parser for metadata of EML or MSG attachment files
      parameters:
      - description: Attachment from an email, parses eml or msg
        example: ''
        in: query
        name: Email Attachment
        required: true
        schema: {type: string}
      - description: check if emails adhere to rfc882 format
        example: ''
        in: query
        name: rfc882 Only
        required: false
        schema: {type: boolean}
      - description: A csv of extensions you would like to ignore. Useful if image
          files are being parsed out of email footer as attachments. Default is .jp(e)g,
          .png, .gif. Filling this value will OVERWRITE existing default
        example: .jpeg,.png,.exe,.gif
        in: query
        name: exclude extensions
        required: false
        schema: {type: string}
      - description: Ignore errors parsing an email. Defaults to False
        example: ''
        in: query
        name: Ignore Errors
        required: false
        schema: {type: boolean}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Email Get Email Metadata
  tmp1:
    post:
      description: Ingest from IMAP server
      parameters:
      - description: Ingest unread emails only
        example: ''
        in: query
        name: Ingest Unread Only
        required: true
        schema: {type: boolean}
      - description: Limit the number of email ingested
        example: ''
        in: body
        name: Email Limit
        required: false
        schema: {type: string}
      - description: Filter by subject of the Email
        example: ''
        in: query
        name: Subject
        required: false
        schema: {type: string}
      - description: Filter by sender email address
        example: ''
        in: query
        name: Sender Address
        required: false
        schema: {type: string}
      - description: True will mark mail as read, False will mark mail as unread.
        example: ''
        in: query
        name: Mark as Read or Unread
        required: false
        schema: {type: boolean}
      - description: Ignore parsing errors, continue parsing
        example: ''
        in: query
        name: Ignore Parsing Errors
        required: false
        schema: {type: boolean}
      - description: Exclude extension types from the attachments of the parsed email
        example: .png,.jpg,.jpeg,.gif
        in: query
        name: Exclude Extensions
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Email Ingest from IMAP
  tmp2:
    post:
      description: Render HTML from an Email to an Image
      parameters:
      - description: HTML data to render
        example: ''
        in: query
        name: HTML Data
        required: true
        schema: {type: string}
      - description: Disable javascript when rendering the HTML
        example: ''
        in: query
        name: Disable Javascript
        required: true
        schema: {type: boolean}
      - description: Disable access to files on the local machine (might be needed
          for images)
        example: ''
        in: query
        name: Disable Local File Access
        required: true
        schema: {type: boolean}
      - description: Proxy to route all requests to, defaults to 127.0.0.1
        example: ''
        in: query
        name: Request Proxy
        required: false
        schema: {type: string}
      - description: Use a proxy for all requests (recommended)
        example: ''
        in: query
        name: Use Proxy
        required: true
        schema: {type: boolean}
      - description: No images in output, defaults to true
        example: ''
        in: query
        name: No Images
        required: false
        schema: {type: boolean}
      - description: Two integers, comma separated for the width & height of the thumbnail.
          Defaults to 800,600
        example: ''
        in: query
        name: Thumbnail Size Max (Width, Height)
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp2'}
          description: Successful request
      summary: Email Render HTML
  tmp3:
    post:
      description: Send an email through an SMTP server
      parameters:
      - description: CSV list of email addresses to send to
        example: email1@domain.com,email2@domain.com
        in: query
        name: Recipients
        required: true
        schema: {type: string}
      - description: Subject of the Email
        example: ''
        in: query
        name: Subject
        required: true
        schema: {type: string}
      - description: CSV list of email addresses to Carbon Copy
        example: email1@domain.com,email2@domain.com
        in: query
        name: CC
        required: false
        schema: {type: string}
      - description: CSV list of email addresses to Blind Carbon Copy
        example: email1@domain.com,email2@domain.com
        in: query
        name: BCC
        required: false
        schema: {type: string}
      - description: Text body of the Email body
        example: ''
        in: query
        name: Text Body
        required: false
        schema: {type: string}
      - description: HTML body of the Email body
        example: ''
        in: query
        name: HTML Body
        required: false
        schema: {type: string}
      - description: Attachments to include in the Email. Must be Swimlane Attachment
          Type
        example: ''
        in: query
        name: Attachments
        required: false
        schema: {type: string}
      - description: Sign the email with PGP
        example: 'True'
        in: query
        name: PGP Sign
        required: true
        schema: {type: string}
      - description: Encrypt the email with PGP
        example: 'True'
        in: query
        name: PGP Encrypt
        required: true
        schema: {type: string}
      - description: Collect debug info on send, without sending mail. Fills output
          'debug info'
        example: ''
        in: query
        name: Debug Send (Won't actually send mail)
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp3'}
          description: Successful request
      summary: Email Send with SMTP
tags:
- {name: Communication}
