              },
              {
                "required": [
                  "wkt"
                ],
                "properties": {
                  "wkt": {
                    "description": "A string defining the CRS using the JSON encodng for Well Known Text",
                    "type": "object"
                  }
                }
              },
              {
                "required": [
                  "referenceSystem"
