        "description": "crs for the specified subset",
        "required": false,
        "style": "form",
        "explode": true,
        "schema": {
          "type": "string"
        }
      },
      "bgcolor": {
        "name": "bgcolor",
        "in": "query",
        "description": "Web color name or hexadecimal 0x[AA]RRGGBB color value for the background color (default to 0x9C9C9C gray). If alpha is not specified, full opacity is assumed.",
        "required": false,
        "style": "form",
        "explode": false,
        "schema": {
          "type": "string",
          "default": "0xFFFFFF"
        }
      },
      "transparent": {
        "name": "transparent",
        "in": "query",
        "description": "Background transparency of map (default=true).",
        "required": false,
        "style": "form",
        "explode": false,
        "schema": {
          "type": "boolean",
          "default": true
        }
      },
      "width": {
        "name": "width",
        "in": "query",
        "description": "Width of the map in pixel. If omitted and height is specified, defaults to the width maintaining a 1:1 aspect ratio. If both width and height are omitted, the server will select a default dimensions.",
        "required": false,
        "style": "form",
        "explode": false,
        "schema": {
          "type": "integer"
        }
      },
      "height": {
        "name": "height",
        "in": "query",
        "description": "Height of the map in pixel. If omitted and width is specified, defaults to the height maintaining a 1:1 aspect ratio. If both width and height are omitted, the server will select a default dimensions.",
        "required": false,
        "style": "form",
        "explode": false,
        "schema": {
          "type": "integer"
        }
      },
      "f-map": {
        "name": "f",
        "in": "query",
        "description": "The format of the map response (e.g. png). Accepted values are 'png', 'jpg' or 'tiff' (GeoTIFF).",
        "required": false,
        "schema": {
          "type": "string",
          "enum": [
            "png",
            "jpg",
            "tiff"
          ]
        },
        "style": "form",
        "explode": false
      },
      "styleId": {
        "name": "styleId",
        "in": "path",
        "description": "An identifier representing a specific style.",
        "required": true,
        "allowEmptyValue": false,
        "schema": {
          "$ref": "#/components/schemas/styles"
        }
      },
      "styleId-collection": {
        "name": "styleId",
