{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para Cadastro de Recursos para produtos TOTVS",
			"url": "{{host}}/api/sfc/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8040"
				}
			}
		}
	],
	"info": {
		"description": "API para a entidade objeto de Recursos para produtos TOTVS",
		"version": "1.001",
		"title": "Recursos",
		"x-totvs": {
			"messageDocumentation": {
				"name": "Resources",
				"description": "Recursos",
				"segment": "Manufatura"
			},
			"productInformation": [
				{
					"product": "Protheus",
					"contact": "renan.roeder@totvs.com.br",
					"description": "Cadastro de Recursos",
					"adapter": ""
				}
			]
		}
	},
	"paths": {
		"/resources": {
			"post": {
				"tags": [
					"resources"
				],
				"summary": "Adiciona novo Recurso",
				"description": "Adiciona novo Recurso",
				"operationId": "postResource",
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Resource_1_001.json#/definitions/Resource"
								}
							}
						}
					},
					"400": {
						"description": "Erro no momento da Inclusão",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				},
				"x-totvs": {
					"productInformation": [
						{
							"product": "Protheus",
							"available": true,
							"note": "Este verbo está disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Resource_1_001.json#/definitions/Resource"
							}
						}
					},
					"description": "Objeto de Recurso que deve ser adicionado",
					"required": true
				}
			},
			"get": {
				"tags": [
					"resources"
				],
				"summary": "Lista de Recursos",
				"description": "Retorna lista de Recursos",
				"operationId": "getResources",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
