	"components": {
		"parameters": {
			"ResourceId": {
				"name": "ResourceId",
				"in": "path",
				"description": "ID Recurso",
				"required": true,
				"schema": {
					"type": "string",
					"format": "char(10)"
				}
			}
		},
		"schemas": {}
	}
