{
    "openapi": "3.0.2",
    "info": {
        "title": "openHAB REST API"
    },
    "paths": {
        "/": {
            "get": {
                "responses": {
                    "default": {
                        "description": "successful operation"
                    }
                },
                "operationId": "getRoot"
            }
        },
        "/bindings": {
            "get": {
                "tags": [
                    "bindings"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "uniqueItems": true,
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/BindingInfoDTO"
                                    }
                                }
                            }
                        },
                        "description": "OK"
                    }
                },
                "operationId": "getAll",
                "summary": "Get all bindings.",
                "description": ""
            }
        },
        "/bindings/{bindingId}/config": {
            "get": {
                "tags": [
                    "bindings"
                ],
                "parameters": [
                    {
                        "name": "bindingId",
                        "description": "service ID",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "description": "OK"
                    },
                    "404": {
                        "description": "Binding does not exist"
                    },
                    "500": {
                        "description": "Configuration can not be read due to internal error"
                    }
                },
                "operationId": "getConfiguration",
                "summary": "Get binding configuration for given binding ID.",
                "description": ""
            },
            "put": {
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "additionalProperties": {
                                    "type": "object"
                                }
                            }
                        }
                    },
                    "required": false
                },
                "tags": [
                    "bindings"
                ],
                "parameters": [
                    {
                        "name": "bindingId",
                        "description": "service ID",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "description": "OK"
                    },
                    "204": {
                        "description": "No old configuration"
                    },
                    "404": {
                        "description": "Binding does not exist"
                    },
                    "500": {
                        "description": "Configuration can not be updated due to internal error"
                    }
                },
                "operationId": "updateConfiguration",
                "summary": "Updates a binding configuration for given binding ID and returns the old configuration.",
                "description": ""
            }
        },
        "/channel-types": {
            "get": {
                "tags": [
                    "channel-types"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "Accept-Language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "uniqueItems": true,
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/ChannelTypeDTO"
                                    }
                                }
                            }
                        },
                        "description": "OK"
                    }
                },
                "operationId": "getAll",
                "summary": "Gets all available channel types.",
                "description": ""
            }
        },
        "/channel-types/{channelTypeUID}": {
            "get": {
                "tags": [
                    "channel-types"
                ],
                "parameters": [
                    {
                        "name": "channelTypeUID",
                        "description": "channelTypeUID",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    },
                    {
                        "name": "Accept-Language",
                        "description": "Accept-Language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ChannelTypeDTO"
                                }
                            }
                        },
                        "description": "Channel type with provided channelTypeUID does not exist."
                    },
                    "404": {
                        "description": "No content"
                    }
                },
                "operationId": "getByUID",
                "summary": "Gets channel type by UID.",
                "description": ""
            }
        },
        "/channel-types/{channelTypeUID}/linkableItemTypes": {
            "get": {
                "tags": [
                    "channel-types"
                ],
                "parameters": [
                    {
                        "name": "channelTypeUID",
                        "description": "channelTypeUID",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "uniqueItems": true,
                                    "type": "array",
                                    "items": {
                                        "type": "string"
                                    }
                                }
                            }
                        },
                        "description": "OK"
                    },
                    "204": {
                        "description": "No content: channel type has no linkable items or is no trigger channel."
                    },
                    "404": {
                        "description": "Given channel type UID not found."
                    }
                },
                "operationId": "getLinkableItemTypes",
                "summary": "Gets the item types the given trigger channel type UID can be linked to.",
                "description": ""
            }
        },
        "/config-descriptions": {
            "get": {
                "tags": [
                    "config-descriptions"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "Accept-Language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    },
                    {
                        "name": "scheme",
                        "description": "scheme filter",
                        "schema": {
                            "type": "string"
                        },
                        "in": "query",
                        "required": false
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/ConfigDescriptionDTO"
                                    }
                                }
                            }
                        },
                        "description": "OK"
                    }
                },
                "operationId": "getAll",
                "summary": "Gets all available config descriptions.",
                "description": ""
            }
        },
        "/config-descriptions/{uri}": {
            "get": {
                "tags": [
                    "config-descriptions"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "Accept-Language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    },
                    {
                        "name": "uri",
                        "description": "uri",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/ConfigDescriptionDTO"
                                }
                            }
                        },
                        "description": "OK"
                    },
                    "400": {
                        "description": "Invalid URI syntax"
                    },
                    "404": {
                        "description": "Not found"
                    }
                },
                "operationId": "getByURI",
                "summary": "Gets a config description by URI.",
                "description": ""
            }
        },
        "/discovery": {
            "get": {
                "tags": [
                    "discovery"
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "uniqueItems": true,
                                    "type": "array",
                                    "items": {
                                        "type": "string"
                                    }
                                }
                            }
                        },
                        "description": "OK"
                    }
                },
                "operationId": "getDiscoveryServices",
                "summary": "Gets all bindings that support discovery.",
                "description": ""
            }
        },
        "/discovery/bindings/{bindingId}/scan": {
            "post": {
                "tags": [
                    "discovery"
                ],
                "parameters": [
                    {
                        "name": "bindingId",
                        "description": "bindingId",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "text/plain": {
                                "schema": {
                                    "format": "int32",
                                    "type": "integer"
                                }
                            }
                        },
                        "description": "OK"
                    }
                },
                "operationId": "scan",
                "summary": "Starts asynchronous discovery process for a binding and returns the timeout in seconds of the discovery operation.",
                "description": ""
            }
        },
        "/extensions": {
            "get": {
                "tags": [
                    "extensions"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "description": "OK"
                    }
                },
                "operationId": "getExtensions",
                "summary": "Get all extensions.",
                "description": ""
            }
        },
        "/extensions/types": {
            "get": {
                "tags": [
                    "extensions"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "description": "OK"
                    }
                },
                "operationId": "getTypes",
                "summary": "Get all extension types.",
                "description": ""
            }
        },
        "/extensions/url/{url}/install": {
            "post": {
                "tags": [
                    "extensions"
                ],
                "parameters": [
                    {
                        "name": "url",
                        "description": "extension install URL",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    },
                    "400": {
                        "description": "The given URL is malformed or not valid."
                    }
                },
                "operationId": "installExtensionByURL",
                "summary": "Installs the extension from the given URL.",
                "description": ""
            }
        },
        "/extensions/{extensionId}": {
            "get": {
                "tags": [
                    "extensions"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    },
                    {
                        "name": "extensionId",
                        "description": "extension ID",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9-]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "description": "OK"
                    },
                    "404": {
                        "description": "Not found"
                    }
                },
                "operationId": "getById",
                "summary": "Get extension with given ID.",
                "description": ""
            }
        },
        "/extensions/{extensionId}/install": {
            "post": {
                "tags": [
                    "extensions"
                ],
                "parameters": [
                    {
                        "name": "extensionId",
                        "description": "extension ID",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9-:]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    }
                },
                "operationId": "installExtension",
                "summary": "Installs the extension with the given ID.",
                "description": ""
            }
        },
        "/extensions/{extensionId}/uninstall": {
            "post": {
                "tags": [
                    "extensions"
                ],
                "parameters": [
                    {
                        "name": "extensionId",
                        "description": "extension ID",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9-:]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    }
                },
                "operationId": "uninstallExtension"
            }
        },
        "/habpanel/gallery/{galleryName}/widgets": {
            "get": {
                "tags": [
                    "habpanel"
                ],
                "parameters": [
                    {
                        "name": "galleryName",
                        "description": "gallery name e.g. 'community'",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "description": "OK"
                    },
                    "404": {
                        "description": "Unknown gallery"
                    }
                },
                "operationId": "getGalleryWidgetList",
                "summary": "Gets the list of widget gallery items.",
                "description": ""
            }
        },
        "/habpanel/gallery/{galleryName}/widgets/{id}": {
            "get": {
                "tags": [
                    "habpanel"
                ],
                "parameters": [
                    {
                        "name": "galleryName",
                        "description": "gallery name e.g. 'community'",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    },
                    {
                        "name": "id",
                        "description": "id within the gallery",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "description": "OK"
                    },
                    "404": {
                        "description": "Unknown gallery or gallery item not found"
                    }
                },
                "operationId": "getGalleryWidgetsItem",
                "summary": "Gets the details about a widget gallery item.",
                "description": ""
            }
        },
        "/iconsets": {
            "get": {
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    }
                ],
                "responses": {
                    "default": {
                        "description": "successful operation"
                    }
                },
                "operationId": "getAll"
            }
        },
        "/inbox": {
            "get": {
                "tags": [
                    "inbox"
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/DiscoveryResultDTO"
                                }
                            }
                        },
                        "description": "OK"
                    }
                },
                "operationId": "getAll",
                "summary": "Get all discovered things.",
                "description": ""
            }
        },
        "/inbox/{thingUID}": {
            "delete": {
                "tags": [
                    "inbox"
                ],
                "parameters": [
                    {
                        "name": "thingUID",
                        "description": "thingUID",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    },
                    "404": {
                        "description": "Discovery result not found in the inbox."
                    }
                },
                "operationId": "delete",
                "summary": "Removes the discovery result from the inbox.",
                "description": ""
            }
        },
        "/inbox/{thingUID}/approve": {
            "post": {
                "requestBody": {
                    "description": "thing label",
                    "content": {
                        "text/plain": {
                            "schema": {
                                "type": "string"
                            }
                        }
                    },
                    "required": false
                },
                "tags": [
                    "inbox"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    },
                    {
                        "name": "thingUID",
                        "description": "thingUID",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    },
                    "404": {
                        "description": "Thing unable to be approved."
                    },
                    "409": {
                        "description": "No binding found that supports this thing."
                    }
                },
                "operationId": "approve",
                "summary": "Approves the discovery result by adding the thing to the registry.",
                "description": ""
            }
        },
        "/inbox/{thingUID}/ignore": {
            "post": {
                "tags": [
                    "inbox"
                ],
                "parameters": [
                    {
                        "name": "thingUID",
                        "description": "thingUID",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    }
                },
                "operationId": "ignore",
                "summary": "Flags a discovery result as ignored for further processing.",
                "description": ""
            }
        },
        "/inbox/{thingUID}/unignore": {
            "post": {
                "tags": [
                    "inbox"
                ],
                "parameters": [
                    {
                        "name": "thingUID",
                        "description": "thingUID",
                        "schema": {
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    }
                },
                "operationId": "unignore",
                "summary": "Removes ignore flag from a discovery result.",
                "description": ""
            }
        },
        "/items": {
            "get": {
                "tags": [
                    "items"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "language",
                        "schema": {
                            "type": "string"
                        },
                        "in": "header",
                        "required": false
                    },
                    {
                        "name": "type",
                        "description": "item type filter",
                        "schema": {
                            "type": "string"
                        },
                        "in": "query",
                        "required": false
                    },
                    {
                        "name": "tags",
                        "description": "item tag filter",
                        "schema": {
                            "type": "string"
                        },
                        "in": "query",
                        "required": false
                    },
                    {
                        "name": "metadata",
                        "description": "metadata selector",
                        "schema": {
                            "type": "string"
                        },
                        "in": "query",
                        "required": false
                    },
                    {
                        "name": "recursive",
                        "description": "get member items recursively",
                        "schema": {
                            "default": false,
                            "type": "boolean"
                        },
                        "in": "query",
                        "required": false
                    },
                    {
                        "name": "fields",
                        "description": "limit output to the given fields (comma separated)",
                        "schema": {
                            "type": "string"
                        },
                        "in": "query",
                        "required": false
                    }
                ],
                "responses": {
                    "200": {
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "items": {
                                        "$ref": "#/components/schemas/EnrichedItemDTO"
                                    }
                                }
                            }
                        },
                        "description": "OK"
                    }
                },
                "operationId": "getItems",
                "summary": "Get all available items.",
                "description": ""
            },
            "put": {
                "requestBody": {
                    "description": "array of item data",
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "array",
                                "items": {
                                    "$ref": "#/components/schemas/GroupItemDTO"
                                }
                            }
                        }
                    },
                    "required": true
                },
                "tags": [
                    "items"
                ],
                "responses": {
                    "200": {
                        "content": {
                            "*/*": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "description": "OK"
                    },
                    "400": {
                        "description": "Item list is null."
                    }
                },
                "operationId": "createOrUpdateItems",
                "summary": "Adds a list of items to the registry or updates the existing items.",
                "description": ""
            }
        },
        "/items/{itemName}/members/{memberItemName}": {
            "put": {
                "tags": [
                    "items"
                ],
                "parameters": [
                    {
                        "name": "itemName",
                        "description": "item name",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    },
                    {
                        "name": "memberItemName",
                        "description": "member item name",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    },
                    "404": {
                        "description": "Item or member item not found or item is not of type group item."
                    },
                    "405": {
                        "description": "Member item is not editable."
                    }
                },
                "operationId": "addMember",
                "summary": "Adds a new member to a group item.",
                "description": ""
            },
            "delete": {
                "tags": [
                    "items"
                ],
                "parameters": [
                    {
                        "name": "itemName",
                        "description": "item name",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    },
                    {
                        "name": "memberItemName",
                        "description": "member item name",
                        "schema": {
                            "pattern": "[a-zA-Z_0-9]*",
                            "type": "string"
                        },
                        "in": "path",
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK"
                    },
                    "404": {
                        "description": "Item or member item not found or item is not of type group item."
                    },
                    "405": {
                        "description": "Member item is not editable."
                    }
                },
                "operationId": "removeMember",
                "summary": "Removes an existing member from a group item.",
                "description": ""
            }
        },
        "/items/{itemname}": {
            "get": {
                "tags": [
                    "items"
                ],
                "parameters": [
                    {
                        "name": "Accept-Language",
                        "description": "language",
                        "schema": {
                            "type": "string"
