{
  "openapi": "3.0.0",
  "info": {
    "version": "2018-08-20",
    "x-release": "s3v4",
    "title": "AWS S3 Control",
    "description": "AWS S3 Control provides access to Amazon S3 control plane operations.",
    "x-logo": {
      "url": "https://raw.githubusercontent.com/chainjet/assets/master/integrations/aws/Arch_Amazon-S3-Standard_64.svg"
    },
    "termsOfService": "https://aws.amazon.com/service-terms/",
    "contact": {
      "name": "Mike Ralphson",
      "email": "mike.ralphson@gmail.com",
      "url": "https://github.com/mermade/aws2openapi",
      "x-twitter": "PermittedSoc"
    },
    "license": {
      "name": "Apache 2.0 License",
      "url": "http://www.apache.org/licenses/"
    },
    "x-providerName": "amazonaws.com",
    "x-serviceName": "s3control",
    "x-origin": [
      {
        "contentType": "application/json",
        "url": "https://raw.githubusercontent.com/aws/aws-sdk-js/master/apis/s3control-2018-08-20.normal.json",
        "converter": {
          "url": "https://github.com/mermade/aws2openapi",
          "version": "1.0.0"
        },
        "x-apisguru-driver": "external"
      }
    ],
    "x-apiClientRegistration": {
      "url": "https://portal.aws.amazon.com/gp/aws/developer/registration/index.html?nc2=h_ct"
    },
    "x-apisguru-categories": ["cloud"],
    "x-preferred": true
  },
  "externalDocs": {
    "description": "Amazon Web Services documentation",
    "url": "https://docs.aws.amazon.com/s3-control/"
  },
  "servers": [
    {
      "url": "http://s3-control.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The AWS S3 Control multi-region endpoint"
    },
    {
      "url": "https://s3-control.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The AWS S3 Control multi-region endpoint"
    },
    {
      "url": "http://s3-control.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The AWS S3 Control endpoint for China (Beijing) and China (Ningxia)"
    },
    {
      "url": "https://s3-control.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The AWS S3 Control endpoint for China (Beijing) and China (Ningxia)"
    }
  ],
  "paths": {
    "/v20180820/accesspoint/{name}#x-amz-account-id": {
      "put": {
        "operationId": "CreateAccessPoint",
        "description": "Creates an access point and associates it with the specified bucket.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/CreateAccessPointResult"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "x-amz-account-id",
            "in": "header",
            "required": true,
            "description": "The AWS account ID for the owner of the bucket for which you want to create an access point.",
            "schema": {
              "type": "string",
              "pattern": "^\\d{12}$",
              "maxLength": 64
            }
          },
          {
            "name": "name",
            "in": "path",
            "required": true,
            "description": "The name you want to assign to this access point.",
            "schema": { "type": "string", "minLength": 3, "maxLength": 50 }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "text/xml": {
              "schema": {
                "type": "object",
                "required": ["Bucket"],
                "properties": {
                  "Bucket": {
                    "description": "The name of the bucket that you want to associate this access point with.",
                    "type": "string",
                    "minLength": 3,
                    "maxLength": 255
                  },
                  "VpcConfiguration": {
                    "description": "The virtual private cloud (VPC) configuration for an access point.",
                    "type": "object",
                    "properties": {
                      "VpcId": {
                        "allOf": [
                          { "$ref": "#/components/schemas/VpcId" },
                          {
                            "description": "If this field is specified, this access point will only allow connections from the specified VPC ID."
                          }
                        ]
                      }
                    }
                  },
                  "PublicAccessBlockConfiguration": {
                    "description": "The <code>PublicAccessBlock</code> configuration that you want to apply to this Amazon S3 bucket. You can enable the configuration options in any combination. For more information about when Amazon S3 considers a bucket or object public, see <a href=\"https://docs.aws.amazon.com/AmazonS3/latest/dev/access-control-block-public-access.html#access-control-block-public-access-policy-status\">The Meaning of \"Public\"</a> in the Amazon Simple Storage Service Developer Guide.",
                    "type": "object",
                    "properties": {
                      "BlockPublicAcls": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Setting" },
                          {
                            "description": "<p>Specifies whether Amazon S3 should block public access control lists (ACLs) for buckets in this account. Setting this element to <code>TRUE</code> causes the following behavior:</p> <ul> <li> <p>PUT Bucket acl and PUT Object acl calls fail if the specified ACL is public.</p> </li> <li> <p>PUT Object calls fail if the request includes a public ACL.</p> </li> <li> <p>PUT Bucket calls fail if the request includes a public ACL.</p> </li> </ul> <p>Enabling this setting doesn't affect existing policies or ACLs.</p>"
                          }
                        ]
                      },
                      "IgnorePublicAcls": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Setting" },
                          {
                            "description": "<p>Specifies whether Amazon S3 should ignore public ACLs for buckets in this account. Setting this element to <code>TRUE</code> causes Amazon S3 to ignore all public ACLs on buckets in this account and any objects that they contain. </p> <p>Enabling this setting doesn't affect the persistence of any existing ACLs and doesn't prevent new public ACLs from being set.</p>"
                          }
                        ]
                      },
                      "BlockPublicPolicy": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Setting" },
                          {
                            "description": "<p>Specifies whether Amazon S3 should block public bucket policies for buckets in this account. Setting this element to <code>TRUE</code> causes Amazon S3 to reject calls to PUT Bucket policy if the specified bucket policy allows public access. </p> <p>Enabling this setting doesn't affect existing bucket policies.</p>"
                          }
                        ]
                      },
                      "RestrictPublicBuckets": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Setting" },
                          {
                            "description": "<p>Specifies whether Amazon S3 should restrict public bucket policies for buckets in this account. Setting this element to <code>TRUE</code> restricts access to buckets with public policies to only AWS services and authorized users within this account.</p> <p>Enabling this setting doesn't affect previously stored bucket policies, except that public and cross-account access within any public bucket policy, including non-public delegation to specific accounts, is blocked.</p>"
                          }
                        ]
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "summary": "Create Access Point"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "delete": {
        "operationId": "DeleteAccessPoint",
        "description": "Deletes the specified access point.",
        "responses": { "200": { "description": "Success" } },
        "parameters": [
          {
            "name": "x-amz-account-id",
            "in": "header",
            "required": true,
            "description": "The account ID for the account that owns the specified access point.",
            "schema": {
              "type": "string",
              "pattern": "^\\d{12}$",
              "maxLength": 64
            }
          },
          {
            "name": "name",
            "in": "path",
            "required": true,
            "description": "The name of the access point you want to delete.",
            "schema": { "type": "string", "minLength": 3, "maxLength": 50 }
          }
        ],
        "summary": "Delete Access Point"
      },
      "get": {
        "operationId": "GetAccessPoint",
        "description": "Returns configuration information about the specified access point.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/GetAccessPointResult"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "x-amz-account-id",
            "in": "header",
            "required": true,
            "description": "The account ID for the account that owns the specified access point.",
            "schema": {
              "type": "string",
              "pattern": "^\\d{12}$",
              "maxLength": 64
            }
          },
          {
            "name": "name",
            "in": "path",
            "required": true,
            "description": "The name of the access point whose configuration information you want to retrieve.",
            "schema": { "type": "string", "minLength": 3, "maxLength": 50 }
          }
        ],
        "summary": "Get Access Point"
      }
    },
    "/v20180820/jobs#x-amz-account-id": {
      "post": {
        "operationId": "CreateJob",
        "description": "<p>You can use Amazon S3 Batch Operations to perform large-scale Batch Operations on Amazon S3 objects. Amazon S3 Batch Operations can execute a single operation or action on lists of Amazon S3 objects that you specify. For more information, see <a href=\"https://docs.aws.amazon.com/AmazonS3/latest/dev/batch-ops-basics.html\">Amazon S3 Batch Operations</a> in the Amazon Simple Storage Service Developer Guide.</p> <p>Related actions include:</p> <ul> <li> <p> <a>DescribeJob</a> </p> </li> <li> <p> <a>ListJobs</a> </p> </li> <li> <p> <a>UpdateJobPriority</a> </p> </li> <li> <p> <a>UpdateJobStatus</a> </p> </li> </ul>",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/xml": {
                "schema": { "$ref": "#/components/schemas/CreateJobResult" }
              }
            }
          },
          "480": {
            "description": "TooManyRequestsException",
            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/TooManyRequestsException"
                }
              }
            }
          },
          "481": {
            "description": "BadRequestException",
            "content": {
              "text/xml": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          },
          "482": {
            "description": "IdempotencyException",
            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/IdempotencyException"
                }
              }
            }
          },
          "483": {
            "description": "InternalServiceException",
            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServiceException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "x-amz-account-id",
            "in": "header",
            "required": true,
            "description": "<p/>",
            "schema": {
              "type": "string",
              "pattern": "^\\d{12}$",
              "maxLength": 64
            }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "text/xml": {
              "schema": {
                "type": "object",
                "required": [
                  "Operation",
                  "Report",
                  "ClientRequestToken",
                  "Manifest",
                  "Priority",
                  "RoleArn"
                ],
                "properties": {
                  "ConfirmationRequired": {
                    "description": "Indicates whether confirmation is required before Amazon S3 runs the job. Confirmation is only required for jobs created through the Amazon S3 console.",
                    "type": "boolean"
                  },
                  "Operation": {
                    "description": "The operation that you want this job to perform on each object listed in the manifest. For more information about the available operations, see <a href=\"https://docs.aws.amazon.com/AmazonS3/latest/dev/batch-ops-operations.html\">Available Operations</a> in the <i>Amazon Simple Storage Service Developer Guide</i>.",
                    "type": "object",
                    "properties": {
                      "LambdaInvoke": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/LambdaInvokeOperation"
                          },
                          {
                            "description": "Directs the specified job to invoke an AWS Lambda function on each object in the manifest."
                          }
                        ]
                      },
                      "S3PutObjectCopy": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/S3CopyObjectOperation"
                          },
                          {
                            "description": "Directs the specified job to execute a PUT Copy object call on each object in the manifest."
                          }
                        ]
                      },
                      "S3PutObjectAcl": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/S3SetObjectAclOperation"
                          },
                          {
                            "description": "Directs the specified job to execute a PUT Object acl call on each object in the manifest."
                          }
                        ]
                      },
                      "S3PutObjectTagging": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/S3SetObjectTaggingOperation"
                          },
                          {
                            "description": "Directs the specified job to execute a PUT Object tagging call on each object in the manifest."
                          }
                        ]
                      },
                      "S3InitiateRestoreObject": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/S3InitiateRestoreObjectOperation"
                          },
                          {
                            "description": "Directs the specified job to execute an Initiate Glacier Restore call on each object in the manifest."
                          }
                        ]
                      },
                      "S3PutObjectLegalHold": {
                        "$ref": "#/components/schemas/S3SetObjectLegalHoldOperation"
                      },
                      "S3PutObjectRetention": {
                        "$ref": "#/components/schemas/S3SetObjectRetentionOperation"
                      },
                      "S3DeleteObjectTagging": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/S3DeleteObjectTaggingOperation"
                          },
                          {
                            "description": "Directs the specified job to execute a DELETE Object tagging call on every object in the manifest."
                          }
                        ]
                      }
                    }
                  },
                  "Report": {
                    "description": "Contains the configuration parameters for a job-completion report.",
                    "type": "object",
                    "properties": {
                      "Bucket": {
                        "allOf": [
                          { "$ref": "#/components/schemas/S3BucketArnString" },
                          {
                            "description": "The Amazon Resource Name (ARN) for the bucket where specified job-completion report will be stored."
                          }
                        ]
                      },
                      "Format": {
                        "allOf": [
                          { "$ref": "#/components/schemas/JobReportFormat" },
                          {
                            "description": "The format of the specified job-completion report."
                          }
                        ]
                      },
                      "Enabled": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Boolean" },
                          {
                            "description": "Indicates whether the specified job will generate a job-completion report."
                          }
                        ]
                      },
                      "Prefix": {
                        "allOf": [
                          { "$ref": "#/components/schemas/ReportPrefixString" },
                          {
                            "description": "An optional prefix to describe where in the specified bucket the job-completion report will be stored. Amazon S3 will store the job-completion report at &lt;prefix&gt;/job-&lt;job-id&gt;/report.json."
                          }
                        ]
                      },
                      "ReportScope": {
                        "allOf": [
                          { "$ref": "#/components/schemas/JobReportScope" },
                          {
                            "description": "Indicates whether the job-completion report will include details of all tasks or only failed tasks."
                          }
                        ]
                      }
                    }
                  },
                  "ClientRequestToken": {
                    "description": "An idempotency token to ensure that you don't accidentally submit the same request twice. You can use any string up to the maximum length.",
                    "type": "string",
                    "minLength": 1,
                    "maxLength": 64
                  },
                  "Manifest": {
                    "description": "Contains the configuration information for a job's manifest.",
                    "type": "object",
                    "properties": {
                      "Spec": {
                        "allOf": [
                          { "$ref": "#/components/schemas/JobManifestSpec" },
                          {
                            "description": "Describes the format of the specified job's manifest. If the manifest is in CSV format, also describes the columns contained within the manifest."
                          }
                        ]
                      },
                      "Location": {
                        "allOf": [
                          {
                            "$ref": "#/components/schemas/JobManifestLocation"
                          },
                          {
                            "description": "Contains the information required to locate the specified job's manifest."
                          }
                        ]
                      }
                    }
                  },
                  "Description": {
                    "description": "A description for this job. You can use any string within the permitted length. Descriptions don't need to be unique and can be used for multiple jobs.",
                    "type": "string",
                    "minLength": 1,
                    "maxLength": 256
                  },
                  "Priority": {
                    "description": "The numerical priority for this job. Higher numbers indicate higher priority.",
                    "type": "integer",
                    "minimum": 0,
                    "maximum": 2147483647
                  },
                  "RoleArn": {
                    "description": "The Amazon Resource Name (ARN) for the AWS Identity and Access Management (IAM) role that Batch Operations will use to execute this job's operation on each object in the manifest.",
                    "type": "string",
                    "pattern": "arn:[^:]+:iam::\\d{12}:role/.*",
                    "minLength": 1,
                    "maxLength": 2048
                  },
                  "Tags": {
                    "description": "A set of tags to associate with the Amazon S3 Batch Operations job. This is an optional parameter. ",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/S3Tag" }
                  }
                }
              }
            }
          }
        },
        "summary": "Create Job"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "get": {
        "operationId": "ListJobs",
        "description": "<p>Lists current Amazon S3 Batch Operations jobs and jobs that have ended within the last 30 days for the AWS account making the request. For more information, see <a href=\"https://docs.aws.amazon.com/AmazonS3/latest/dev/batch-ops-basics.html\">Amazon S3 Batch Operations</a> in the <i>Amazon Simple Storage Service Developer Guide</i>.</p> <p>Related actions include:</p> <p/> <ul> <li> <p> <a>CreateJob</a> </p> </li> <li> <p> <a>DescribeJob</a> </p> </li> <li> <p> <a>UpdateJobPriority</a> </p> </li> <li> <p> <a>UpdateJobStatus</a> </p> </li> </ul>",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/xml": {
                "schema": { "$ref": "#/components/schemas/ListJobsResult" }
              }
            }
          },
          "480": {
            "description": "InvalidRequestException",
            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidRequestException"
                }
              }
            }
          },
          "481": {
            "description": "InternalServiceException",
            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServiceException"
                }
              }
            }
          },
          "482": {
            "description": "InvalidNextTokenException",
            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidNextTokenException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "x-amz-account-id",
            "in": "header",
            "required": true,
            "description": "<p/>",
            "schema": {
              "type": "string",
              "pattern": "^\\d{12}$",
              "maxLength": 64
            }
          },
          {
            "name": "jobStatuses",
            "in": "query",
            "required": false,
            "description": "The <code>List Jobs</code> request returns jobs that match the statuses listed in this element.",
            "schema": {
              "type": "array",
              "items": { "$ref": "#/components/schemas/JobStatus" }
            }
          },
          {
            "name": "nextToken",
            "in": "query",
            "required": false,
            "description": "A pagination token to request the next page of results. Use the token that Amazon S3 returned in the <code>NextToken</code> element of the <code>ListJobsResult</code> from the previous <code>List Jobs</code> request.",
            "schema": {
              "type": "string",
              "pattern": "^[A-Za-z0-9\\+\\:\\/\\=\\?\\#-_]+$",
              "minLength": 1,
              "maxLength": 1024
            }
          },
          {
            "name": "maxResults",
            "in": "query",
            "required": false,
            "description": "The maximum number of jobs that Amazon S3 will include in the <code>List Jobs</code> response. If there are more jobs than this number, the response will include a pagination token in the <code>NextToken</code> field to enable you to retrieve the next page of results.",
            "schema": { "type": "integer", "minimum": 1, "maximum": 1000 }
          },
          {
            "name": "MaxResults",
            "in": "query",
            "schema": { "type": "string" },
            "description": "Pagination limit",
            "required": false
          },
          {
            "name": "NextToken",
            "in": "query",
            "schema": { "type": "string" },
            "description": "Pagination token",
            "required": false
          }
        ],
        "summary": "List Jobs"
      }
    },
    "/v20180820/accesspoint/{name}/policy#x-amz-account-id": {
      "delete": {
        "operationId": "DeleteAccessPointPolicy",
        "description": "Deletes the access point policy for the specified access point.",
        "responses": { "200": { "description": "Success" } },
        "parameters": [
          {
            "name": "x-amz-account-id",
            "in": "header",
            "required": true,
            "description": "The account ID for the account that owns the specified access point.",
            "schema": {
              "type": "string",
              "pattern": "^\\d{12}$",
              "maxLength": 64
            }
          },
          {
            "name": "name",
            "in": "path",
            "required": true,
            "description": "The name of the access point whose policy you want to delete.",
            "schema": { "type": "string", "minLength": 3, "maxLength": 50 }
          }
        ],
        "summary": "Delete Access Point Policy"
      },
      "parameters": [
