            "required": false,
            "description": "<p>Allows grantee the read, write, read ACP, and write ACP permissions on the bucket.</p> <note> <p>This is not supported by Amazon S3 on Outposts buckets.</p> </note>",
            "schema": { "type": "string" }
          },
