    },
    "/api/argument-scripts/templates": {
      "get": {
        "tags": [
          "argument_scripts"
        ],
        "summary": "List available argument script templates",
        "description": "Argument script templates are scripts with placeholders. When values replace the placeholders, the script can run in a given script runtime.nnnnnnnnArgument script templates help to convert simple type values to objects of types that can only be produced in a given script runtime. Those objects can be used as arguments to scripts' parameters.\n\nThis operation retrieves the available argument script templates.",
        "operationId": "list-argument-scripts-templates",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/ArgumentScriptTemplate"
                  }
                }
              }
            }
          }
        },
        "security": [
          {
            "apiKeyAuth": [ ]
          }
        ]
      }
    },
    "/api/argument-scripts/templates/{id}": {
      "get": {
        "tags": [
          "argument_scripts"
        ],
        "summary": "Retrieves argument script template by given unique template identifier",
        "description": "This operation returns argument script template for the specified template id.",
        "operationId": "get-argument-scripts-template",
        "parameters": [
