            }
          },
          "500": {
            "description": "Server Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ErrorDetails"
                }
              }
            }
          }
        },
        "security": [
          {
            "basicAuth": [ ]
          },
          {
            "signAuth": [ ]
          }
        ],
        "x-vmw-vapi-servicename": "authentication",
        "x-vmw-vapi-methodname": "login"
      }
    },
