paths:
  tmp0:
    post:
      description: MDATP Delete a Indicator
      parameters:
      - description: A Indicator ID
        example: 220e7d15b0b3d7fac48f2bd61114db1022197f7f
        in: query
        name: Indicator ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: MDATP Delete Indicator
  tmp1:
    post:
      description: MDATP Get a single Alert
      parameters:
      - description: A Alert ID
        example: da636972645033942704_533969862
        in: query
        name: Alert ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: MDATP Get Alert
  tmp10:
    post:
      description: MDATP Get file information based on a hash
      parameters:
      - description: A MD5, SHA1, or SHA256 Hash
        example: 6532ec91d513acc05f43ee0aa3002599729fd3e1
        in: query
        name: File Hash
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp10'}
          description: Successful request
      summary: MDATP Get File Information
  tmp11:
    post:
      description: MDATP Get alerts based on a file hash
      parameters:
      - description: A MD5, SHA1, or SHA256 Hash
        example: 6532ec91d513acc05f43ee0aa3002599729fd3e1
        in: query
        name: File Hash
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp11'}
          description: Successful request
      summary: MDATP Get File Related Alerts
  tmp12:
    post:
      description: MDATP Get machines based on a file hash
      parameters:
      - description: A MD5, SHA1, or SHA256 Hash
        example: 6532ec91d513acc05f43ee0aa3002599729fd3e1
        in: query
        name: File Hash
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp12'}
          description: Successful request
      summary: MDATP Get File Related Machines
  tmp13:
    post:
      description: MDATP Get statistics based on a file hash
      parameters:
      - description: Hash
        example: 02d14d3529bb1be124f6ac084c908c73
        in: query
        name: Hash
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp13'}
          description: Successful request
      summary: MDATP Get File Statistics
  tmp14:
    post:
      description: MDATP Get a investigation collection package
      parameters:
      - description: A Action ID
        example: 5b1c0caf-14fe-41e1-83bf-118b1a9e391d
