            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The imagebuilder multi-region endpoint"
    },
    {
      "url": "https://imagebuilder.{region}.amazonaws.com",
