        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["name", "distributions", "clientToken"],
                "properties": {
                  "name": {
                    "description": " The name of the distribution configuration. ",
                    "type": "string",
                    "pattern": "^[-_A-Za-z-0-9][-_A-Za-z0-9 ]{1,126}[-_A-Za-z-0-9]$"
