      }
    },
    "schemas": {
      "authorization_grant": {
        "type": "object",
        "required": [
          "grant_type",
          "client_id",
          "client_secret"
        ],
        "properties": {
          "grant_type": {
            "type": "string",
            "example": "password",
            "description": "Used to identify which credentials the authorization server needs to check\n  * **client_credentials** - Requires a client_id and client_secret only\n  * **password** - Requires a username and password as well as client_id and client_secret\n"
          },
          "client_id": {
            "type": "string",
            "example": "XBRL US",
            "description": "Unique identifier agreed upon by **XBRL US** and the 3rd party client"
          },
          "client_secret": {
            "type": "string",
            "example": "xhOEnAX2M...gaIYLXOvCe25Q",
            "format": "byte",
            "description": "Base64 key used to authenticate the 3rd party client"
          },
          "username": {
            "type": "string",
            "example": "joe90@WorldIntelligenceNetwork.org",
            "description": "Unique identifier for a given user"
          },
          "password": {
            "type": "string",
            "format": "password",
            "example": "someSecurePassword",
            "description": "Password used to authenticate the 3rd party user"
          },
          "platform": {
            "type": "string",
            "example": "github",
            "description": "Identifies the platform that is requesting the token (i.e. <YourProgramName>)"
          }
        }
      },
      "error_message": {
        "type": "object",
        "properties": {
          "errors": {
            "type": "string",
            "description": "Identifier for the error"
          },
          "error_description": {
            "type": "string",
            "description": "Adds more details to the error"
          }
        }
      },
      "paging": {
        "type": "object",
        "properties": {
          "limit": {
            "type": "integer",
            "example": 1000
          },
          "offset": {
            "type": "integer",
            "example": -1
          },
          "count": {
            "type": "integer",
            "example": 1
          }
        }
      },
      "dimensions": {
        "type": "object",
        "properties": {
