      },
      "unauthorized": {
        "description": "Bad credentials, or could not read credentials due to malformed JSON.\n * \"Full authentication is required...\" -> malformed JSON\n * \"Bad client credentials\" -> client_id/client_secret pair invalid\n * \"Bad credentials\" -> username/password pair invalid\n",
        "content": {
          "applications/json": {
