{
  "openapi": "3.0.0",
  "info": {
    "version": "2018-10-26",
    "x-release": "v4",
    "title": "AWS Security Hub",
    "description": "Security Hub provides you with a comprehensive view of the security state of your AWS environment and resources. It also provides you with the readiness status of your environment based on controls from supported security standards. Security Hub collects security data from AWS accounts, services, and integrated third-party products and helps you analyze security trends in your environment to identify the highest priority security issues. For more information about Security Hub, see the  AWS Security Hub User Guide . When you use operations in the Security Hub API, the requests are executed only in the AWS Region that is currently active or in the specific AWS Region that you specify in your request. Any configuration or settings change that results from the operation is applied only to that Region. To make the same change in other Regions, execute the same command for each Region to apply the change to. For example, if your Region is set to us-west-2, when you use  CreateMembers  to add a member account to Security Hub, the association of the member account with the master account is created only in the us-west-2 Region. Security Hub must be enabled for the member account in the same Region that the invitation was sent from. The following throttling limits apply to using Security Hub API operations.     GetFindings  - RateLimit of 3 requests per second. BurstLimit of 6 requests per second.     UpdateFindings  - RateLimit of 1 request per second. BurstLimit of 5 requests per second.   All other operations - RateLimit of 10 requests per second. BurstLimit of 30 requests per second.",
    "x-logo": {
      "url": "https://raw.githubusercontent.com/chainjet/assets/master/integrations/aws/Arch_AWS-Security-Hub_64.svg"
    },
    "termsOfService": "https://aws.amazon.com/service-terms/",
    "contact": {
      "name": "Mike Ralphson",
      "email": "mike.ralphson@gmail.com",
      "url": "https://github.com/mermade/aws2openapi",
      "x-twitter": "PermittedSoc"
    },
    "license": {
      "name": "Apache 2.0 License",
      "url": "http://www.apache.org/licenses/"
    },
    "x-providerName": "amazonaws.com",
    "x-serviceName": "securityhub",
    "x-origin": [
      {
        "contentType": "application/json",
        "url": "https://raw.githubusercontent.com/aws/aws-sdk-js/master/apis/securityhub-2018-10-26.normal.json",
        "converter": {
          "url": "https://github.com/mermade/aws2openapi",
          "version": "1.0.0"
        },
        "x-apisguru-driver": "external"
      }
    ],
    "x-apiClientRegistration": {
      "url": "https://portal.aws.amazon.com/gp/aws/developer/registration/index.html?nc2=h_ct"
    },
    "x-apisguru-categories": ["cloud"],
    "x-preferred": true
  },
  "externalDocs": {
    "description": "Amazon Web Services documentation",
    "url": "https://docs.aws.amazon.com/securityhub/"
  },
  "servers": [
    {
      "url": "http://securityhub.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The AWS SecurityHub multi-region endpoint"
    },
    {
      "url": "https://securityhub.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The AWS SecurityHub multi-region endpoint"
    },
    {
      "url": "http://securityhub.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The AWS SecurityHub endpoint for China (Beijing) and China (Ningxia)"
    },
    {
      "url": "https://securityhub.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The AWS SecurityHub endpoint for China (Beijing) and China (Ningxia)"
    }
  ],
  "x-hasEquivalentPaths": true,
  "paths": {
    "/master": {
      "post": {
        "operationId": "AcceptInvitation",
        "description": "<p>Accepts the invitation to be a member account and be monitored by the Security Hub master account that the invitation was sent from.</p> <p>When the member account accepts the invitation, permission is granted to the master account to view findings generated in the member account.</p>",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/AcceptInvitationResponse"
                }
              }
            }
          },
          "480": {
            "description": "InternalException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/InternalException" }
              }
            }
          },
          "481": {
            "description": "InvalidInputException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidInputException"
                }
              }
            }
          },
          "482": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          },
          "483": {
            "description": "ResourceNotFoundException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ResourceNotFoundException"
                }
              }
            }
          },
          "484": {
            "description": "InvalidAccessException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidAccessException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["MasterId", "InvitationId"],
                "properties": {
                  "MasterId": {
                    "description": "The account ID of the Security Hub master account that sent the invitation.",
                    "type": "string",
                    "pattern": ".*\\S.*"
                  },
                  "InvitationId": {
                    "description": "The ID of the invitation sent from the Security Hub master account.",
                    "type": "string",
                    "pattern": ".*\\S.*"
                  }
                }
              }
            }
          }
        },
        "summary": "Accept Invitation",
        "deprecated": true
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "get": {
        "operationId": "GetMasterAccount",
        "description": "Provides the details for the Security Hub master account for the current member account. ",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/GetMasterAccountResponse"
                }
              }
            }
          },
          "480": {
            "description": "InternalException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/InternalException" }
              }
            }
          },
          "481": {
            "description": "InvalidInputException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidInputException"
                }
              }
            }
          },
          "482": {
            "description": "InvalidAccessException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidAccessException"
                }
              }
            }
          },
          "483": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          },
          "484": {
            "description": "ResourceNotFoundException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ResourceNotFoundException"
                }
              }
            }
          }
        },
        "parameters": [],
        "summary": "Get Master Account",
        "deprecated": true
      }
    },
    "/standards/deregister": {
      "post": {
        "operationId": "BatchDisableStandards",
        "description": "<p>Disables the standards specified by the provided <code>StandardsSubscriptionArns</code>.</p> <p>For more information, see <a href=\"https://docs.aws.amazon.com/securityhub/latest/userguide/securityhub-standards.html\">Security Standards</a> section of the <i>AWS Security Hub User Guide</i>.</p>",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/BatchDisableStandardsResponse"
                }
              }
            }
          },
          "480": {
            "description": "InternalException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/InternalException" }
              }
            }
          },
          "481": {
            "description": "InvalidInputException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidInputException"
                }
              }
            }
          },
          "482": {
            "description": "InvalidAccessException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidAccessException"
                }
              }
            }
          },
          "483": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["StandardsSubscriptionArns"],
                "properties": {
                  "StandardsSubscriptionArns": {
                    "description": "The ARNs of the standards subscriptions to disable.",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/NonEmptyString" },
                    "minItems": 1,
                    "maxItems": 25
                  }
                }
              }
            }
          }
        },
        "summary": "Batch Disable Standards"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/standards/register": {
      "post": {
        "operationId": "BatchEnableStandards",
        "description": "<p>Enables the standards specified by the provided <code>StandardsArn</code>. To obtain the ARN for a standard, use the <code> <a>DescribeStandards</a> </code> operation.</p> <p>For more information, see the <a href=\"https://docs.aws.amazon.com/securityhub/latest/userguide/securityhub-standards.html\">Security Standards</a> section of the <i>AWS Security Hub User Guide</i>.</p>",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/BatchEnableStandardsResponse"
                }
              }
            }
          },
          "480": {
            "description": "InternalException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/InternalException" }
              }
            }
          },
          "481": {
            "description": "InvalidInputException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidInputException"
                }
              }
            }
          },
          "482": {
            "description": "InvalidAccessException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidAccessException"
                }
              }
            }
          },
          "483": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["StandardsSubscriptionRequests"],
                "properties": {
                  "StandardsSubscriptionRequests": {
                    "description": "The list of standards checks to enable.",
                    "type": "array",
                    "items": {
                      "$ref": "#/components/schemas/StandardsSubscriptionRequest"
                    },
                    "minItems": 1,
                    "maxItems": 25
                  }
                }
              }
            }
          }
        },
        "summary": "Batch Enable Standards"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/findings/import": {
      "post": {
        "operationId": "BatchImportFindings",
        "description": "<p>Imports security findings generated from an integrated third-party product into Security Hub. This action is requested by the integrated product to import its findings into Security Hub.</p> <p>The maximum allowed size for a finding is 240 Kb. An error is returned for any finding larger than 240 Kb.</p> <p>After a finding is created, <code>BatchImportFindings</code> cannot be used to update the following finding fields and objects, which Security Hub customers use to manage their investigation workflow.</p> <ul> <li> <p> <code>Confidence</code> </p> </li> <li> <p> <code>Criticality</code> </p> </li> <li> <p> <code>Note</code> </p> </li> <li> <p> <code>RelatedFindings</code> </p> </li> <li> <p> <code>Severity</code> </p> </li> <li> <p> <code>Types</code> </p> </li> <li> <p> <code>UserDefinedFields</code> </p> </li> <li> <p> <code>VerificationState</code> </p> </li> <li> <p> <code>Workflow</code> </p> </li> </ul>",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/BatchImportFindingsResponse"
                }
              }
            }
          },
          "480": {
            "description": "InternalException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/InternalException" }
              }
            }
          },
          "481": {
            "description": "InvalidInputException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidInputException"
                }
              }
            }
          },
          "482": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          },
          "483": {
            "description": "InvalidAccessException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidAccessException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["Findings"],
                "properties": {
                  "Findings": {
                    "description": "A list of findings to import. To successfully import a finding, it must follow the <a href=\"https://docs.aws.amazon.com/securityhub/latest/userguide/securityhub-findings-format.html\">AWS Security Finding Format</a>. Maximum of 100 findings per request.",
                    "type": "array",
                    "items": {
                      "$ref": "#/components/schemas/AwsSecurityFinding"
                    },
                    "minItems": 1,
                    "maxItems": 100
                  }
                }
              }
            }
          }
        },
        "summary": "Batch Import Findings"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/findings/batchupdate": {
      "patch": {
        "operationId": "BatchUpdateFindings",
        "description": "<p>Used by Security Hub customers to update information about their investigation into a finding. Requested by master accounts or member accounts. Master accounts can update findings for their account and their member accounts. Member accounts can update findings for their account.</p> <p>Updates from <code>BatchUpdateFindings</code> do not affect the value of <code>UpdatedAt</code> for a finding.</p> <p>Master accounts can use <code>BatchUpdateFindings</code> to update the following finding fields and objects.</p> <ul> <li> <p> <code>Confidence</code> </p> </li> <li> <p> <code>Criticality</code> </p> </li> <li> <p> <code>Note</code> </p> </li> <li> <p> <code>RelatedFindings</code> </p> </li> <li> <p> <code>Severity</code> </p> </li> <li> <p> <code>Types</code> </p> </li> <li> <p> <code>UserDefinedFields</code> </p> </li> <li> <p> <code>VerificationState</code> </p> </li> <li> <p> <code>Workflow</code> </p> </li> </ul> <p>Member accounts can only use <code>BatchUpdateFindings</code> to update the Note object.</p>",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/BatchUpdateFindingsResponse"
                }
              }
            }
          },
          "480": {
            "description": "InternalException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/InternalException" }
              }
            }
          },
          "481": {
            "description": "InvalidInputException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidInputException"
                }
              }
            }
          },
          "482": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          },
          "483": {
            "description": "InvalidAccessException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidAccessException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["FindingIdentifiers"],
                "properties": {
                  "FindingIdentifiers": {
                    "description": "<p>The list of findings to update. <code>BatchUpdateFindings</code> can be used to update up to 100 findings at a time.</p> <p>For each finding, the list provides the finding identifier and the ARN of the finding provider.</p>",
                    "type": "array",
                    "items": {
                      "$ref": "#/components/schemas/AwsSecurityFindingIdentifier"
                    }
                  },
                  "Note": {
                    "description": "The updated note.",
                    "type": "object",
                    "properties": {
                      "Text": {
                        "allOf": [
                          { "$ref": "#/components/schemas/NonEmptyString" },
                          { "description": "The updated note text." }
                        ]
                      },
                      "UpdatedBy": {
                        "allOf": [
                          { "$ref": "#/components/schemas/NonEmptyString" },
                          {
                            "description": "The principal that updated the note."
                          }
                        ]
                      }
                    }
                  },
                  "Severity": {
                    "description": "Updates to the severity information for a finding.",
                    "type": "object",
                    "properties": {
                      "Normalized": {
                        "allOf": [
                          { "$ref": "#/components/schemas/RatioScale" },
                          {
                            "description": "<p>The normalized severity for the finding. This attribute is to be deprecated in favor of <code>Label</code>.</p> <p>If you provide <code>Normalized</code> and do not provide <code>Label</code>, <code>Label</code> is set automatically as follows.</p> <ul> <li> <p>0 - <code>INFORMATIONAL</code> </p> </li> <li> <p>1–39 - <code>LOW</code> </p> </li> <li> <p>40–69 - <code>MEDIUM</code> </p> </li> <li> <p>70–89 - <code>HIGH</code> </p> </li> <li> <p>90–100 - <code>CRITICAL</code> </p> </li> </ul>"
                          }
                        ]
                      },
                      "Product": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Double" },
                          {
                            "description": "The native severity as defined by the AWS service or integrated partner product that generated the finding."
                          }
                        ]
                      },
                      "Label": {
                        "allOf": [
                          { "$ref": "#/components/schemas/SeverityLabel" },
                          {
                            "description": "<p>The severity value of the finding. The allowed values are the following.</p> <ul> <li> <p> <code>INFORMATIONAL</code> - No issue was found.</p> </li> <li> <p> <code>LOW</code> - The issue does not require action on its own.</p> </li> <li> <p> <code>MEDIUM</code> - The issue must be addressed but not urgently.</p> </li> <li> <p> <code>HIGH</code> - The issue must be addressed as a priority.</p> </li> <li> <p> <code>CRITICAL</code> - The issue must be remediated immediately to avoid it escalating.</p> </li> </ul>"
                          }
                        ]
                      }
                    }
                  },
                  "VerificationState": {
                    "description": "<p>Indicates the veracity of a finding.</p> <p>The available values for <code>VerificationState</code> are as follows.</p> <ul> <li> <p> <code>UNKNOWN</code> – The default disposition of a security finding</p> </li> <li> <p> <code>TRUE_POSITIVE</code> – The security finding is confirmed</p> </li> <li> <p> <code>FALSE_POSITIVE</code> – The security finding was determined to be a false alarm</p> </li> <li> <p> <code>BENIGN_POSITIVE</code> – A special case of <code>TRUE_POSITIVE</code> where the finding doesn't pose any threat, is expected, or both</p> </li> </ul>",
                    "type": "string",
                    "enum": [
                      "UNKNOWN",
                      "TRUE_POSITIVE",
                      "FALSE_POSITIVE",
                      "BENIGN_POSITIVE"
                    ]
                  },
                  "Confidence": {
