              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/TokenAlreadyExistsException"
                }
              }
            }
          }
        },
        "requestBody": {
          "content": {
            "text/xml": {
              "schema": {
                "$ref": "#/components/schemas/ContinueUpdateRollbackInput"
              }
            }
          }
        },
        "parameters": [
          {
            "name": "Action",
            "in": "query",
            "required": true,
            "schema": { "type": "string", "enum": ["ContinueUpdateRollback"] }
          },
          {
            "name": "Version",
            "in": "query",
            "required": true,
            "schema": { "type": "string", "enum": ["2010-05-15"] }
          }
        ],
        "summary": "Continue Update Rollback"
      }
    },
    "/#Action=CreateChangeSet": {
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
