            "content": {
              "text/xml": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          }
        },
        "requestBody": {
          "content": {
            "text/xml": {
              "schema": { "$ref": "#/components/schemas/CreateChangeSetInput" }
            }
          }
        },
        "parameters": [
          {
            "name": "Action",
            "in": "query",
            "required": true,
            "schema": { "type": "string", "enum": ["CreateChangeSet"] }
          },
          {
