paths:
  tmp0:
    post:
      description: Add a Scan
      parameters:
      - description: CSV of IPs to scan
        example: 1.0.0.4,12.3.2.2
        in: query
        name: IP List
        required: true
        schema: {type: string}
      - description: Repository ID to run scan in
        example: '1'
        in: query
        name: Repository ID
        required: true
        schema: {type: string}
      - description: Name of Scan
        example: My Test Can
        in: query
        name: Scan Name
        required: true
        schema: {type: string}
      - description: Description of Scan
        example: My Scan Description
        in: query
        name: Description
        required: true
        schema: {type: string}
      - description: Policy to use in scan
        example: '10003'
        in: query
        name: Policy ID
        required: true
        schema: {type: string}
      - description: ID of Credentials to use in scan
        example: '100032'
        in: query
        name: Credentials ID
        required: true
        schema: {type: string}
      - description: Start Schedule String in ical format
        example: TZID=America/Los_Angeles:20180713T130000
        in: query
        name: Start Schedule String
        required: false
        schema: {type: string}
      - description: Repeat Rule string
        example: FREQ=DAILY;INTERVAL=1
        in: query
        name: Repeat Rule
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Add A Scan
  tmp1:
    post:
      description: Adds an Asset Group
      parameters:
      - description: Asset Group Name
        example: Asset Group 1
        in: query
        name: Group Name
        required: true
        schema: {type: string}
      - description: Asset Description
        example: This is my asset description
        in: query
        name: description
        required: false
        schema: {type: string}
      - description: Create Default objects. 'true'/'false'
        example: 'true'
        in: query
        name: Create Default Objects
        required: false
        schema: {type: string}
      - description: List of repository IDs to add to group
        example: '33'
        in: query
        name: Repository IDs
        required: false
        schema: {type: string}
