{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para a entidade pessoa (Persons) para produtos TOTVS",
			"url": "{{host}}/api/rh/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8051"
				}
			}
		}
	],
	"info": {
		"description": "API para a entidade pessoa (Persons) para produtos TOTVS",
		"version": "1.000",
		"title": "Pessoa",
		"contact": {
			"name": "T-Talk",
			"url": "API.Totvs.com.br",
			"email": "comiteintegracao@totvs.com.br"
		},
		"x-totvs": {
			"messageDocumentation": {
				"name": "Persons",
				"description": "Pessoa",
				"segment": "Recursos Humanos"
			},
			"productInformation": [
				{
					"product": "RM",
					"contact": "rm_FW@totvs.com.br",
					"description": "Listagem de Pessoas"
				}
			]
		}
	},
	"paths": {
		"/persons/{id}": {
			"get": {
				"tags": [
					"Persons"
				],
				"summary": "Retorna dados de tabela da pessoa especificada",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Retorna dados de tabela da pessoa especificada",
				"operationId": "getPersonsId",
				"parameters": [
					{
						"$ref": "#/components/parameters/PersonsId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"200": {
						"description": "Operação executada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Persons_1_001.json#/definitions/PersonsInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"delete": {
				"tags": [
					"Persons"
				],
				"summary": "Exclui pessoa",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Exclui pessoa",
				"operationId": "DeletePersonsId",
				"parameters": [
					{
						"$ref": "#/components/parameters/PersonsId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"200": {
						"description": "Operação executada com sucesso"
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"put": {
				"tags": [
					"Persons"
				],
				"summary": "Atualiza um registro existente de tabela de pessoas",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Atualiza um registro existente de tabela de pessoas. Caso não seja informado alguma propriedade para ser atualizada, está será considerada nula.",
				"operationId": "putPersonsId",
				"parameters": [
					{
						"$ref": "#/components/parameters/PersonsId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"requestBody": {
					"description": "Objeto para atualizar o registro de tabela de pessoas",
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Persons_1_001.json#/definitions/PersonsInfo"
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Pessoas atualizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Persons_1_001.json#/definitions/PersonsInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		},
		"/persons": {
			"get": {
				"tags": [
					"Persons"
				],
				"summary": "Retorna todas as pessoas",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Retorna todos as pessoas",
				"operationId": "getPersons",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/PageSize"
					},
					{
						"$ref": "#/components/parameters/PersonsIds"
					},
					{
						"$ref": "#/components/parameters/Names"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Persons_1_001.json#/definitions/PagedPersons"
								}
							}
						}
					},
					"400": {
						"description": "Erro no momento da listagem das pessoas",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"post": {
				"tags": [
					"Persons"
				],
				"summary": "Inclui um novo registro na tabela de pessoas",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"description": "Inclui um novo registro na tabela de pessoas",
				"operationId": "postPersons",
				"requestBody": {
					"description": "Objeto para atualizar o registro de tabela de pessoas",
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Persons_1_001.json#/definitions/PersonsInfo"
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Operação executada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Persons_1_001.json#/definitions/PersonsInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		}
	},
	"components": {
		"parameters": {
			"PersonsId": {
				"name": "id",
				"in": "path",
				"required": true,
				"description": "Identificador Único representando a pessoa",
				"schema": {
					"type": "string"
				}
			},
			"PersonsIds": {
				"name": "personsIds",
				"in": "query",
				"description": "Códigos das pessoas retornados pelo get separado por vírgula (,) ",
				"required": false,
				"example": "param1,param2",
				"schema": {
					"type": "string"
				}
			},
			"Names": {
				"name": "name",
				"in": "query",
				"description": "Busca por nome da pessoa",
				"required": false,
				"example": "Usuário de busca",
				"schema": {
					"type": "string"
				}
			}
		},
		"schemas": {}
	}
}
