      "get": {
        "x-skipAuth": true,
        "x-noAction": true,
        "x-triggerHook": true,
        "x-triggerHookInstructions": "Go to Amazon SNS > Topics and select the topic you want to subscribe to. Then create a new subscription with protocol HTTPS and enter the above URL into the Endpoint field.",
