        "summary": "New Message",
        "operationId": "newMessageHook",
        "description": "Triggers when a message is published on a topic.",
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "Type": { "type": "string" },
                    "MessageId": { "type": "string" },
                    "TopicArn": { "type": "string" },
                    "Subject": { "type": "string" },
                    "Message": { "type": "string" },
