{
  "openapi": "3.0.0",
  "info": {
    "version": "v2",
    "title": "CircleCI",
    "description": "This describes the resources that make up the CircleCI API v2.",
    "x-categories": ["software-development"],
    "x-logo": {
      "url": "https://raw.githubusercontent.com/chainjet/assets/master/integrations/circleci.svg"
    },
    "license": { "name": "MIT" }
  },
  "servers": [{ "url": "https://circleci.com/api/v2" }],
  "security": [
    { "api_key_header": [] },
    { "basic_auth": [] },
    { "api_key_query": [] }
  ],
  "paths": {
    "/context": {
      "get": {
        "summary": "listContexts",
        "description": "List all contexts for an owner.",
        "tags": ["Context"],
        "operationId": "listContexts",
        "responses": {
          "200": {
            "description": "A paginated list of contexts",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "items": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string",
                            "format": "uuid",
                            "description": "The unique ID of the context."
                          },
                          "name": {
                            "type": "string",
                            "description": "The user defined name of the context."
                          },
                          "created_at": {
                            "type": "string",
                            "format": "date-time",
                            "description": "The date and time the context was created.",
                            "example": "2015-09-21T17:29:21.042Z"
                          }
                        },
                        "required": ["id", "name", "created_at"],
                        "title": "Context"
                      }
                    },
                    "next_page_token": {
                      "type": "string",
                      "x-nullable": true,
                      "description": "A token to pass as a `page-token` query parameter to return the next page of results."
                    }
                  },
                  "required": ["items", "next_page_token"]
                }
              }
            }
          },
          "default": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": { "message": { "type": "string" } }
                }
              }
            },
            "description": "Error response."
          }
        },
        "parameters": [
          {
            "in": "query",
            "name": "owner-id",
            "description": "The unique ID of the owner of the context. Specify either this or owner-slug.",
            "schema": { "type": "string", "format": "uuid" },
            "required": false
          },
          {
            "in": "query",
            "name": "owner-slug",
            "description": "A string that represents an organization. Specify either this or owner-id. Cannot be used for accounts.",
            "schema": { "type": "string" },
            "required": false
          },
          {
            "in": "query",
            "name": "owner-type",
            "description": "The type of the owner. Defaults to \"organization\". Accounts are only used as context owners in server.",
            "schema": { "type": "string", "enum": ["account", "organization"] },
            "required": false
          },
          {
            "in": "query",
            "name": "page-token",
            "description": "A token to retrieve the next page of results.",
            "schema": { "type": "string" },
            "required": false,
            "allowEmptyValue": true
          }
        ]
      },
      "post": {
        "summary": "createContext",
        "tags": ["Context"],
        "operationId": "createContext",
        "responses": {
          "200": {
            "description": "The new context",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string",
                      "format": "uuid",
                      "description": "The unique ID of the context."
                    },
                    "name": {
                      "type": "string",
                      "description": "The user defined name of the context."
                    },
                    "created_at": {
                      "type": "string",
                      "format": "date-time",
                      "description": "The date and time the context was created.",
                      "example": "2015-09-21T17:29:21.042Z"
                    }
                  },
                  "required": ["id", "name", "created_at"],
                  "title": "Context"
                }
              }
            }
          },
          "default": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": { "message": { "type": "string" } }
                }
              }
            },
            "description": "Error response."
          }
        },
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "name": {
                    "type": "string",
                    "description": "The user defined name of the context."
                  },
                  "owner": {
                    "type": "object",
                    "properties": {
                      "id": {
                        "type": "string",
                        "format": "uuid",
                        "description": "The unique ID of the owner of the context. Specify either this or slug."
                      },
                      "slug": {
                        "type": "string",
                        "description": "A string that represents an organization. Specify either this or id. Cannot be used for accounts."
                      },
                      "type": {
                        "enum": ["account", "organization"],
                        "type": "string",
                        "description": "The type of the owner. Defaults to \"organization\". Accounts are only used as context owners in server."
                      }
                    },
                    "oneOf": [
                      {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string",
                            "format": "uuid",
                            "description": "The unique ID of the owner of the context. Specify either this or slug."
                          },
                          "type": {
                            "enum": ["account", "organization"],
                            "type": "string",
                            "description": "The type of the owner. Defaults to \"organization\". Accounts are only used as context owners in server.",
                            "example": "organization"
                          }
                        },
                        "required": ["id"]
                      },
                      {
                        "type": "object",
                        "properties": {
                          "slug": {
                            "type": "string",
                            "description": "A string that represents an organization. Specify either this or id. Cannot be used for accounts."
                          },
                          "type": {
                            "enum": ["organization"],
                            "type": "string",
                            "description": "The type of owner. Defaults to \"organization\". Accounts are only used as context owners in server and must be specified by an id instead of a slug."
                          }
                        },
                        "required": ["slug"]
                      }
                    ]
                  }
                },
                "required": ["name", "owner"]
              }
            }
          }
        },
        "description": "Create a new context"
      }
    },
    "/context/{context-id}": {
      "delete": {
        "summary": "deleteContext",
        "tags": ["Context"],
        "operationId": "deleteContext",
        "responses": {
          "200": {
            "description": "A confirmation message",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "message": {
                      "type": "string",
                      "description": "A human-readable message"
                    }
                  },
                  "required": ["message"],
                  "description": "message response",
                  "title": "MessageResponse"
                }
              }
            }
          },
          "default": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": { "message": { "type": "string" } }
                }
              }
            },
            "description": "Error response."
          }
        },
        "parameters": [
          {
            "in": "path",
            "name": "context-id",
            "description": "ID of the context (UUID)",
            "schema": { "type": "string", "format": "uuid" },
            "required": true
          }
        ],
        "description": "Delete a context"
      },
      "get": {
        "summary": "getContext",
        "description": "Returns basic information about a context.",
        "tags": ["Context"],
        "operationId": "getContext",
        "responses": {
          "200": {
            "description": "The context",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string",
                      "format": "uuid",
                      "description": "The unique ID of the context."
                    },
                    "name": {
                      "type": "string",
                      "description": "The user defined name of the context."
                    },
                    "created_at": {
                      "type": "string",
                      "format": "date-time",
                      "description": "The date and time the context was created.",
                      "example": "2015-09-21T17:29:21.042Z"
                    }
                  },
                  "required": ["id", "name", "created_at"],
                  "title": "Context"
                }
              }
            }
          },
          "default": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": { "message": { "type": "string" } }
                }
              }
            },
            "description": "Error response."
          }
        },
        "parameters": [
          {
            "in": "path",
            "name": "context-id",
            "description": "ID of the context (UUID)",
            "schema": { "type": "string", "format": "uuid" },
            "required": true
          }
        ]
      }
    },
    "/context/{context-id}/environment-variable": {
      "get": {
        "summary": "listEnvironmentVariablesFromContext",
        "description": "List information about environment variables in a context, not including their values.",
        "tags": ["Context"],
        "operationId": "listEnvironmentVariablesFromContext",
        "responses": {
          "200": {
            "description": "A paginated list of environment variables",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "items": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "variable": {
                            "type": "string",
                            "description": "The name of the environment variable",
                            "example": "POSTGRES_USER"
                          },
                          "created_at": {
                            "type": "string",
                            "format": "date-time",
                            "description": "The date and time the environment variable was created.",
                            "example": "2015-09-21T17:29:21.042Z"
                          },
                          "context_id": {
                            "type": "string",
                            "format": "uuid",
                            "description": "ID of the context (UUID)"
                          }
                        },
                        "required": ["variable", "created_at", "context_id"]
                      }
                    },
                    "next_page_token": {
                      "type": "string",
                      "x-nullable": true,
                      "description": "A token to pass as a `page-token` query parameter to return the next page of results."
                    }
                  },
                  "required": ["items", "next_page_token"]
                }
              }
            }
          },
          "default": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": { "message": { "type": "string" } }
                }
              }
            },
            "description": "Error response."
          }
        },
        "parameters": [
          {
            "in": "path",
            "name": "context-id",
            "description": "ID of the context (UUID)",
            "schema": { "type": "string", "format": "uuid" },
            "required": true
          }
        ]
      }
    },
    "/context/{context-id}/environment-variable/{env-var-name}": {
      "put": {
        "summary": "addEnvironmentVariableToContext",
        "description": "Create or update an environment variable within a context. Returns information about the environment variable, not including its value.",
        "tags": ["Context"],
        "operationId": "addEnvironmentVariableToContext",
        "responses": {
          "200": {
            "description": "The new environment variable",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "variable": {
                      "type": "string",
                      "description": "The name of the environment variable",
                      "example": "POSTGRES_USER"
                    },
                    "created_at": {
                      "type": "string",
                      "format": "date-time",
                      "description": "The date and time the environment variable was created.",
                      "example": "2015-09-21T17:29:21.042Z"
                    },
                    "context_id": {
                      "type": "string",
                      "format": "uuid",
                      "description": "ID of the context (UUID)"
                    }
                  },
                  "required": ["variable", "created_at", "context_id"],
                  "anyOf": [
                    {
                      "type": "object",
                      "properties": {
                        "variable": {
                          "type": "string",
                          "description": "The name of the environment variable",
                          "example": "POSTGRES_USER"
                        },
                        "created_at": {
                          "type": "string",
                          "format": "date-time",
                          "description": "The date and time the environment variable was created.",
                          "example": "2015-09-21T17:29:21.042Z"
                        },
                        "context_id": {
                          "type": "string",
                          "format": "uuid",
                          "description": "ID of the context (UUID)"
                        }
                      },
                      "required": ["variable", "created_at", "context_id"]
                    },
                    {
                      "type": "object",
                      "properties": {
                        "message": {
                          "type": "string",
                          "description": "A human-readable message"
                        }
                      },
                      "required": ["message"],
                      "description": "message response",
                      "title": "MessageResponse"
                    }
                  ]
                }
              }
            }
          },
          "default": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": { "message": { "type": "string" } }
                }
              }
            },
            "description": "Error response."
          }
        },
        "parameters": [
          {
            "in": "path",
            "name": "context-id",
            "description": "ID of the context (UUID)",
            "schema": { "type": "string", "format": "uuid" },
            "required": true
          },
          {
            "in": "path",
            "name": "env-var-name",
            "description": "The name of the environment variable",
            "schema": { "type": "string" },
            "required": true,
            "example": "POSTGRES_USER"
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "value": {
                    "type": "string",
                    "description": "The value of the environment variable",
                    "example": "some-secret-value"
                  }
                },
                "required": ["value"]
              }
            }
          }
        }
      },
      "delete": {
        "summary": "deleteEnvironmentVariableFromContext",
        "description": "Delete an environment variable from a context.",
        "tags": ["Context"],
        "operationId": "deleteEnvironmentVariableFromContext",
        "responses": {
          "200": {
            "description": "A confirmation message",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "message": {
                      "type": "string",
                      "description": "A human-readable message"
                    }
                  },
                  "required": ["message"],
                  "description": "message response",
                  "title": "MessageResponse"
                }
              }
            }
          },
          "default": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": { "message": { "type": "string" } }
                }
              }
            },
            "description": "Error response."
          }
        },
        "parameters": [
          {
            "in": "path",
            "name": "env-var-name",
            "description": "The name of the environment variable",
            "schema": { "type": "string" },
            "required": true,
            "example": "POSTGRES_USER"
          },
          {
            "in": "path",
            "name": "context-id",
            "description": "ID of the context (UUID)",
            "schema": { "type": "string", "format": "uuid" },
            "required": true
          }
        ]
      }
    },
    "/insights/{project-slug}/workflows": {
      "get": {
        "summary": "getProjectWorkflowMetrics",
        "description": "Get summary metrics for a project's workflows. Workflow runs going back at most 90 days are included in the aggregation window. Metrics are refreshed daily, and thus may not include executions from the last 24 hours.",
        "tags": ["Insights"],
        "operationId": "getProjectWorkflowMetrics",
        "responses": {
          "200": {
            "description": "A paginated list of summary metrics by workflow",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "items": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "name": {
                            "type": "string",
                            "description": "The name of the workflow.",
                            "example": "build-and-test"
                          },
                          "window_start": {
                            "type": "string",
                            "format": "date-time",
                            "description": "The start of the aggregation window for workflow metrics."
                          },
                          "window_end": {
                            "type": "string",
                            "format": "date-time",
                            "description": "The end of the aggregation window for workflow metrics."
                          },
                          "metrics": {
                            "type": "object",
                            "properties": {
                              "success_rate": {
                                "type": "number",
                                "format": "float",
                                "description": "The ratio of successful runs / total runs."
                              },
                              "total_runs": {
                                "type": "integer",
                                "format": "int64",
                                "minimum": 0,
                                "description": "The total number of runs."
                              },
                              "failed_runs": {
                                "type": "integer",
                                "format": "int64",
                                "minimum": 0,
                                "description": "The number of failed runs."
                              },
                              "successful_runs": {
                                "type": "integer",
                                "format": "int64",
                                "minimum": 0,
                                "description": "The number of successful runs."
                              },
                              "throughput": {
                                "type": "number",
                                "format": "float",
                                "description": "The average number of workflow runs per day."
                              },
                              "mttr": {
                                "type": "integer",
                                "format": "int64",
                                "minimum": 0,
                                "description": "The mean time to recovery (mean time between failures and their next success) in seconds."
                              },
                              "total_credits_used": {
                                "type": "integer",
                                "format": "int64",
                                "minimum": 0,
                                "description": "The total credits consumed by the workflow in the aggregation window."
                              },
                              "duration_metrics": {
                                "type": "object",
                                "properties": {
                                  "min": {
                                    "type": "integer",
                                    "format": "int64",
                                    "minimum": 0,
                                    "description": "The minimum duration, in seconds, among a group of runs."
                                  },
                                  "mean": {
                                    "type": "integer",
                                    "format": "int64",
                                    "minimum": 0,
                                    "description": "The mean duration, in seconds, among a group of runs."
                                  },
                                  "median": {
                                    "type": "integer",
                                    "format": "int64",
                                    "minimum": 0,
                                    "description": "The median duration, in seconds, among a group of runs."
                                  },
                                  "p95": {
                                    "type": "integer",
                                    "format": "int64",
                                    "minimum": 0,
                                    "description": "The 95th percentile duration, in seconds, among a group of runs."
                                  },
                                  "max": {
                                    "type": "integer",
                                    "format": "int64",
                                    "minimum": 0,
                                    "description": "The max duration, in seconds, among a group of runs."
                                  },
                                  "standard_deviation": {
                                    "type": "number",
                                    "format": "float",
                                    "description": "The standard deviation, in seconds, among a group of runs."
                                  }
                                },
                                "required": [
                                  "min",
                                  "mean",
                                  "median",
                                  "p95",
                                  "max",
                                  "standard_deviation"
                                ],
                                "description": "Metrics relating to the duration of runs for a workflow."
                              },
                              "total_recoveries": {
                                "type": "integer",
                                "format": "int64",
                                "minimum": 0,
                                "description": "The number of recovered workflow executions per day."
                              }
                            },
                            "required": [
                              "success_rate",
                              "total_runs",
                              "failed_runs",
                              "successful_runs",
                              "throughput",
                              "mttr",
                              "total_credits_used",
                              "duration_metrics",
                              "duration_metrics"
                            ],
                            "description": "Metrics relating to a workflow's runs."
                          }
                        },
                        "required": [
                          "name",
                          "window_start",
                          "window_end",
                          "metrics"
                        ]
                      },
                      "description": "Workflow summary metrics."
                    },
                    "next_page_token": {
                      "type": "string",
                      "x-nullable": true,
                      "description": "A token to pass as a `page-token` query parameter to return the next page of results."
                    }
                  },
                  "required": ["items", "next_page_token"],
                  "description": "Paginated workflow summary metrics."
                }
              }
            }
          },
          "default": {
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": { "message": { "type": "string" } }
                }
              }
            },
            "description": "Error response."
          }
        },
        "parameters": [
          {
            "in": "path",
            "name": "project-slug",
            "description": "Project slug in the form `vcs-slug/org-name/repo-name`. The `/` characters may be URL-escaped.",
            "schema": { "type": "string" },
            "required": true,
            "example": "gh/CircleCI-Public/api-preview-docs",
            "allowReserved": true
          },
          {
            "in": "query",
            "name": "page-token",
            "description": "A token to retrieve the next page of results.",
            "schema": { "type": "string" },
            "required": false,
            "allowEmptyValue": true
          },
          {
            "in": "query",
            "name": "branch",
            "description": "The name of a vcs branch.",
            "schema": { "type": "string" },
            "required": false,
            "allowEmptyValue": true
          },
          {
            "in": "path",
            "name": "reporting-window",
            "description": "The time window used to calculate summary metrics.",
            "schema": {
              "type": "string",
              "enum": [
                "last-90-days",
                "last-24-hours",
                "last-24-hours",
                "last-30-days",
                "last-60-days"
              ]
            },
            "required": false,
            "example": "last-90-days"
          },
          {
            "in": "query",
            "name": "reporting-window",
            "description": "The time window used to calculate summary metrics.",
            "schema": {
              "type": "string",
              "enum": [
                "last-7-days",
                "last-90-days",
                "last-24-hours",
                "last-30-days",
                "last-60-days"
              ]
            },
            "required": false,
            "example": "last-90-days"
          }
        ]
      }
    },
    "/insights/{project-slug}/workflows/{workflow-name}": {
      "get": {
        "summary": "getProjectWorkflowRuns",
        "description": "Get recent runs of a workflow. Runs going back at most 90 days are returned.",
        "tags": ["Insights"],
        "operationId": "getProjectWorkflowRuns",
        "responses": {
          "200": {
            "description": "A paginated list of recent workflow runs",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "items": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string",
                            "format": "uuid",
                            "description": "The unique ID of the workflow."
                          },
                          "duration": {
                            "type": "integer",
                            "format": "int64",
                            "minimum": 0,
                            "description": "The duration in seconds of a run."
                          },
                          "created_at": {
                            "type": "string",
                            "format": "date-time",
                            "description": "The date and time the workflow was created."
                          },
                          "stopped_at": {
                            "type": "string",
                            "format": "date-time",
                            "x-nullable": true,
                            "description": "The date and time the workflow stopped."
                          },
                          "credits_used": {
                            "type": "integer",
                            "format": "int64",
                            "minimum": 0,
                            "description": "The number of credits used during execution"
                          },
                          "status": {
                            "enum": [
                              "success",
                              "failed",
                              "error",
                              "canceled",
                              "unauthorized"
                            ],
                            "type": "string",
