            "in": "header",
            "example": "sha=fpRyg5eVQletdZqEKaFlqwBXJzM=",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "If-Match",
            "description": "Ensure this item hasn't recently changed\nby ensuring the item's current `etag` matches the\n`etag` passed into this header.",
