                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "apiId",
            "in": "path",
            "required": true,
            "description": "The API ID.",
            "schema": { "type": "string" }
          }
        ],
        "summary": "Delete Api Cache"
      },
      "get": {
        "operationId": "GetApiCache",
        "description": "Retrieves an <code>ApiCache</code> object.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/GetApiCacheResponse" }
              }
            }
          },
          "480": {
            "description": "BadRequestException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          },
          "481": {
            "description": "ConcurrentModificationException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ConcurrentModificationException"
                }
              }
            }
          },
          "482": {
            "description": "NotFoundException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/NotFoundException" }
              }
            }
          },
          "483": {
            "description": "UnauthorizedException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/UnauthorizedException"
                }
              }
            }
          },
          "484": {
            "description": "InternalFailureException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalFailureException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "apiId",
            "in": "path",
            "required": true,
            "description": "The API ID.",
            "schema": { "type": "string" }
          }
        ],
        "summary": "Get Api Cache"
      }
    },
