    "/v1/apis/{apiId}/datasources": {
      "post": {
        "operationId": "CreateDataSource",
        "description": "Creates a <code>DataSource</code> object.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/CreateDataSourceResponse"
                }
              }
            }
