      "export_settings_request_body_schema": {
        "type": "object",
        "properties": {
          "export_settings": {
            "type": "object",
            "required": [
              "format",
              "model"
            ],
            "properties": {
              "format": {
                "type": "string",
                "description": "The format of the returned data.",
                "enum": [
                  "json",
                  "jsonl",
                  "xml"
                ],
                "default": "jsonl"
              },
              "model": {
                "type": "string",
                "description": "The type of data requested and returned.",
                "enum": [
                  "asset",
                  "fix",
                  "vulnerability"
                ]
              },
              "slim": {
                "type": "boolean",
                "description": "If `true`, the fields returned are reduced. Default is `false`.",
                "default": false
              }
            }
          },
          "id": {
            "type": "array",
            "items": {
              "type": "integer"
            },
            "description": "*Valid for asset, vulnerability, and fix models.*  Denpendent on the `model`, an array of asset, vulnerability, or fix IDs"
          },
          "max_priority": {
            "type": "integer",
            "description": "*Valid for asset, vulnerablibilty, and fix models.*  Search for all assets that have a priority less than this value."
          },
          "max_risk_meter_score": {
            "type": "integer",
            "description": "*Valid for asset, vulnerability, and fix models.*  Search for all assets that have a risk meter score less than this value."
          },
          "min_priority": {
            "type": "integer",
            "description": "*Valid for asset, vulnerability, and fix models.*  Search for all assets that have a priority greater than this value."
          },
          "min_risk_meter_score": {
            "type": "integer",
            "description": "*Valid for asset, vulnerability, and fix models.*  Search for all assets that have a risk meter score greater than this value."
          },
          "q": {
            "type": "string",
            "description": "*Valid for asset, vulnerability, and fix models.*  Additional text search parameters, as documented at: [Kenna Search Terms](https://help.kennasecurity.com/hc/en-us/articles/206280593-Kenna-Search-Terms).  Use the section that matches the model.*  Example q=tag:(mytag+AND+thetag)."
          },
          "records_updated_since": {
            "type": "string",
            "description": "*Valid for asset, vulnerability, and fix models.*  Supports **Incremental Exports**. It will fetch all records updated since that time. This can be added to both asset and vulnerabilities.  The format can be an offset date (\"now-2d\") following [this format](https://www.elastic.co/guide/en/elasticsearch/reference/current/common-options.html#date-math), a date (\"2020-09-09\") or a date-time (\"2020-10-10 12:30:00\")."
          },
          "records_updated_until": {
            "type": "string",
            "description": "*Valid for asset, vulnerability, and fix models.*  Effective only when `records_updated_since` is specified. It will fetch all records updated since `records_updated_since` (inclusive) datetime until `records_update_until` datetime (exclusive). The format is the same to `records_update_since` parameter."
          },
          "status": {
            "type": "array",
            "items": {
              "type": "string",
              "enum": [
                "active",
                "inactive",
                "open",
                "closed",
                "risk accepted",
                "false positive"
              ]
            },
            "description": "*Valid for asset, vulnerability, and fix models.*  An array of statuses. For the asset model, 'active' and 'open' are valid.  The asset model default is 'active'.  For the vulnerability and fix models, 'open', 'close', 'risk accpeted', and 'false positive' are valid.  The vulnerability and fix models default is 'open'."
          },
          "exclude_child_filter": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for asset model.*  If you want to include all assets regardless of vulnerability filters, pass a value of `[\"Include all assets\"]`; otherwise, omit this parameter."
          },
          "operating_system": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for asset model.*  An array of the names of the operating systems associated with a set of assets."
          },
          "primary_locator": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for asset model.*  The primary locator used for an asset. This should be one of the following values: ip_address, hostname, database, url, mac_address, netbios, fqdn, file, or application"
          },
          "search_id": {
            "type": "integer",
            "description": "*Valid for asset model.*  Search values specified in the risk meter group definition will override their respective values provided in this request when search_id is provided."
          },
          "service_names": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for asset model.*  An array of service names relating to your assets. Make sure all letters are lowercase."
          },
          "service_ports": {
            "type": "array",
            "items": {
              "type": "integer"
            },
            "description": "*Valid for asset model.*  An array of ports relating to your assets."
          },
          "service_products": {
            "type": "array",
            "items": {
              "type": "integer"
            },
            "description": "*Valid for asset model.*  An array of service products relating to your assets. Make sure all letters are lowercase."
          },
          "service_protocols": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for asset model.*  An array of service protocols relating to your assets. Make sure all letters are lowercase."
          },
          "status_set_manually": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for asset model.*  A boolean to indicate whether an asset\"s active/inactive status was set automatically by Kenna or manually by a human."
          },
          "tag_boolean_mode": {
            "type": "string",
            "description": "*Valid for asset model.*  Set this to \"all\" if you would like to return assets that contain all of the tags in your query rather than assets that contain any of the tags."
          },
          "tags": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for asset model.*  An array of tag names associated with your assets."
          },
          "classification": {
            "type": "array",
            "items": {
              "type": "string",
              "enum": [
                "Informational",
                "CVE",
                "CWE",
                "WASC"
              ]
            },
            "description": "*Valid for vulnerability and fixes models.*  Find all vulnerabilities matching the specified classifications."
          },
          "connector_names": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of connector names related to your vulnerabilities."
          },
          "connector_types": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of connector types related to your vulnerabilities."
          },
          "custom_fields": {
            "type": "string",
            "description": "*Valid for vulnerability and fixes models.*  Search for all vulnerabilities that have a specific custom field and its value.  The proper format for a custom field query is `custom_fields:`, the name of the custom field you are searching on, and the value you are searching for.  For example: `custom_fields:Category=good`.  <br>Note the following: <ol><li>Testing <code>custom_fields</code> in the \"Try It!\" pane does not work.</li><li>Use the example above as a guide in <code>curl</code> or some other language.</li><li>To search multiple custom fields, add another query parameter.</li><li>Search must have a value and there is no wildcarding.</li></ol>"
          },
          "has_known_exploits": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of booleans for filtering vulnerabilities by those that have have known exploits."
          },
          "has_known_malware": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of booleans for filtering your vulnerabilities by those that have known malware."
          },
          "max_rounded_risk_meter_score": {
            "type": "integer",
            "description": "*Valid for vulnerability and fixes models.*  Similar to max_risk_meter_score, rounded to the nearest integer."
          },
          "max_severity": {
            "type": "integer",
            "description": "*Valid for vulnerability and fixes models.*  Find all vulnerabilities with severity levels less than this value."
          },
          "max_threat": {
            "type": "integer",
            "description": "*Valid for vulnerability and fixes models.*  Find all vulnerabilities with threat levels less than this value."
          },
          "min_rounded_risk_meter_score": {
            "type": "integer",
            "description": "*Valid for vulnerability and fixes models.*  Similar to min_risk_meter_score, rounded to the nearest integer."
          },
          "min_severity": {
            "type": "integer",
            "description": "*Valid for vulnerability and fixes models.*  Find all vulnerabilities with severity levels greater than this value."
          },
          "min_threat": {
            "type": "integer",
            "description": "*Valid for vulnerability and fixes models.*  Find all vulnerabilities with threat levels greater than this value."
          },
          "no_vulnerability_definition_id": {
            "type": "array",
            "items": {
              "type": "integer"
            },
            "description": "*Valid for vulnerability and fixes models.*  Exclude all vulnerabilities for the identified definition IDs."
          },
          "pci_related": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of booleans for filtering your vulnerabilities by those that are PCI related"
          },
          "port": {
            "type": "array",
            "items": {
              "type": "integer"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of ports associated with your vulnerabilities."
          },
          "predicted_exploitable": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of booleans for filtering your vulnerabilities by those that have been predicted to be exploited."
          },
          "prioritized": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of booleans for filtering your vulnerabilities by those that are prioritized."
          },
          "remote_code_exection": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of booleans for filtering your vulnerabilities by those that be executed remotely."
          },
          "service_ticket_status": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of strings that represent the statuses of all service tickets connected to your vulnerabilities."
          },
          "top_exploit": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of booleans to filter by vulnerabilities whose exploits are actively being used for internet breaches."
          },
          "trending": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of booleans for filtering by vulnerabilities that are trending."
          },
          "vulnerability_class": {
            "type": "array",
            "items": {
              "type": "string",
              "enum": [
                "Web app",
                "Host or network"
              ]
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of strings for filtering by vulnerability classification."
          },
          "vulnerability_definition_id": {
            "type": "array",
            "items": {
              "type": "integer"
            },
            "description": "*Valid for vulnerability and fixes models.*  Find all vulnerabilities for the identified definition IDs."
          },
          "wasc_classification": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array to filter your vulnerabilities by specified WASC classifications."
          },
          "zero_day": {
            "type": "array",
            "items": {
              "type": "boolean"
            },
            "description": "*Valid for vulnerability and fixes models.*  An array of booleans for filtering by vulnerabilities that have zero days."
          }
        }
      },
