      "service_ticket_request_schema": {
        "type": "object",
        "description": "A service ticket to associate with a vulnerability. A null service_ ticket value will unassociate the service ticket associated with these vulnerabilities.",
        "properties": {
          "external_identifier": {
            "type": "string",
            "description": "<strong>Required</strong> for non-null service_ticket objects. The service ticket's external ID in its ticketing provider, e.g. \"JR-123\".",
            "example": "abc123"
          },
          "ticket_type": {
            "type": "string",
            "enum": [
              "jira",
              "service_now",
              "api"
            ],
            "description": "<strong>Required</strong> for non-null service_ticket objects. `api` type tickets are not integrated with ticketing connectors.",
            "example": "service_now"
          },
          "system_id": {
            "type": "string",
            "description": "The service ticket's internal ID in its ticketing provider; e.g. \"JR-123\" for Jira or \"12abcd345e6789101112f1g21h3ijk14\" for ServiceNow. Required for Jira and ServiceNow tickets for clients with the relevant ticketing connectors to allow the service ticket to be automatically updated in Kenna. Note that this value is the same as external_identifier for Jira type service tickets.",
            "example": "12abcd345e6789101112f1g21h3ijk14"
          },
          "status": {
            "type": "string",
            "description": "Will be ignored for jira or service_now type tickets when the client has the corresponding ticketing connector(s) in place. Will be honored for `api` type tickets.",
            "example": "Active"
          },
          "due_date": {
            "type": "string",
            "format": "date-time",
            "description": "Will be ignored for jira or service_now type tickets when the client has the corresponding ticketing connector(s) in place. Will be honored for `api` type tickets.",
            "example": "2021-08-12T03:14:57Z"
          }
        }
      },
      "service_ticket_response_schema": {
        "type": "object",
        "required": [
          "external_identifier",
          "ticket_type"
        ],
        "description": "Service ticket object to associate with a vulnerability.",
        "properties": {
          "external_identifier": {
            "type": "string",
            "description": "The service ticket's external ID in its ticketing provider.",
            "example": "JR-678"
          },
          "ticket_type": {
            "type": "string",
            "enum": [
              "api",
              "jira",
              "service_now"
            ],
            "description": "The service ticket type.  `api` type tickets are not integrated with ticketing connectors.",
            "example": "service_now"
          },
          "system_id": {
            "type": "string",
            "description": "The service ticket's internal ID in its ticketing provider.  Required for Jira and ServiceNow tickets for clients with the relevant ticketing connectors to allow the service ticket to be automatically updated in Kenna. Note that this value is the same as external_identifier for Jira type service tickets.",
            "example": "12abcd345e6789101112f1g21h3ijk14"
          },
          "status": {
            "type": "string",
            "description": "The service ticket status.  Will be honored for `api` type tickets.",
            "example": "Active"
          },
          "due_date": {
            "type": "string",
            "format": "date-time",
            "description": "May contain a default due date for internal use, so please refer to `due_date_override` for the service ticket's actual due date",
            "example": "2021-08-12T03:14:57Z",
            "default": null
          },
          "due_date_override": {
            "type": "string",
            "format": "date-time",
            "description": "Actual due date.",
