    "/audit_logs/": {
      "get": {
        "summary": "Audit Log Search",
        "description": "<p>Returns a compressed (gzip) JSON lines file with all entries for the specified date range. If there are no audit logs for the specified period, the returned file will have no content.  The date is in an ISO 8601 format.  Only the day portion (YYYY-MM-DD) is used for both start_date and end_date.</p><p>For more details, refer to <a href=\"https://apidocs.kennasecurity.com/reference/downloading-export-files\">Downloading Export Files<a>.</p>",
        "operationId": "audit-log-search",
        "security": [
          {
            "X-Risk-Token": [

            ]
          }
        ],
        "tags": [
          "AUDIT LOGS"
        ],
        "parameters": [
          {
            "name": "start_date",
            "in": "query",
            "description": "The start date of the period you wish to receive audit logs from. Must be less than or equal to end_date. <b>Required<b>.",
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "end_date",
            "in": "query",
            "description": "The end date of the period you wish to receive audit logs from. Must be greater than or equal to start_date. The date must be a full day before the current date. <b>Required<b>.",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "200"
          },
