      "SelfManagedActiveDirectoryConfiguration": {
        "type": "object",
        "required": ["DomainName", "UserName", "Password", "DnsIps"],
        "properties": {
          "DomainName": {
            "allOf": [
              {
                "$ref": "#/components/schemas/ActiveDirectoryFullyQualifiedName"
