          },
          "Password": {
            "allOf": [
              { "$ref": "#/components/schemas/DirectoryPassword" },
              {
                "description": "The password for the service account on your self-managed AD domain that Amazon FSx will use to join to your AD domain."
              }
            ]
          },
          "DnsIps": {
            "allOf": [
              { "$ref": "#/components/schemas/DnsIps" },
              {
                "description": "<p>A list of up to two IP addresses of DNS servers or domain controllers in the self-managed AD directory. The IP addresses need to be either in the same VPC CIDR range as the one in which your Amazon FSx file system is being created, or in the private IP version 4 (IPv4) address ranges, as specified in <a href=\"http://www.faqs.org/rfcs/rfc1918.html\">RFC 1918</a>:</p> <ul> <li> <p>10.0.0.0 - 10.255.255.255 (10/8 prefix)</p> </li> <li> <p>172.16.0.0 - 172.31.255.255 (172.16/12 prefix)</p> </li> <li> <p>192.168.0.0 - 192.168.255.255 (192.168/16 prefix)</p> </li> </ul>"
              }
            ]
          }
        },
        "description": "The configuration that Amazon FSx uses to join the Windows File Server instance to your self-managed (including on-premises) Microsoft Active Directory (AD) directory."
