        "items": { "$ref": "#/components/schemas/FilterValue" },
        "maxItems": 20
      },
      "Filter": {
        "type": "object",
        "properties": {
          "Name": {
            "allOf": [
              { "$ref": "#/components/schemas/FilterName" },
              { "description": "The name for this filter." }
