                "schema": {
                  "type": "array",
                  "items": {
                    "type": "integer"
                  }
                },
                "examples": {
                  "example": {
                    "value": [
                      1,
                      2,
                      3,
                      4
                    ]
                  }
                }
              },
              "application/octet-stream": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "integer"
                  }
                }
