{
  "openapi": "3.0.0",
  "tags": [],
  "info": {
    "title": "Fleet",
    "description": "OpenAPI schema for Fleet API endpoints",
    "version": "0.2",
    "contact": {
      "name": "Fleet Team"
    },
    "license": {
      "name": "Elastic License 2.0",
      "url": "https://www.elastic.co/licensing/elastic-license"
    }
  },
  "servers": [
    {
      "url": "http://localhost:5601/api/fleet",
      "description": "local"
    }
  ],
  "paths": {
    "/setup": {
      "post": {
        "summary": "Setup",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/fleet_setup_response"
                }
              }
            }
          },
          "500": {
            "description": "Internal Server Error",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "message": {
                      "type": "string"
                    }
                  }
                }
              }
            }
          }
        },
        "operationId": "setup",
        "parameters": [
          {
            "$ref": "#/components/parameters/kbn_xsrf"
          }
        ]
      }
    },
    "/settings": {
      "get": {
        "summary": "Settings",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/fleet_settings_response"
                }
              }
            }
          }
        },
        "operationId": "get-settings"
      },
      "put": {
        "summary": "Settings - Update",
        "tags": [],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "fleet_server_hosts": {
                    "type": "array",
                    "description": "Protocol and path must be the same for each URL",
                    "items": {
                      "type": "string"
                    }
                  },
                  "has_seen_add_data_notice": {
                    "type": "boolean"
                  },
                  "additional_yaml_config": {
                    "type": "string"
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/fleet_settings_response"
                }
              }
            }
          }
        },
        "operationId": "update-settings"
      }
    },
    "/health_check": {
      "post": {
        "summary": "Fleet Server Health Check",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "name": {
                      "type": "string"
                    },
                    "status": {
                      "type": "string"
                    },
                    "host": {
                      "type": "string"
                    }
                  }
                }
              }
            }
          }
        },
        "operationId": "fleet-server-health-check",
        "parameters": [
          {
            "$ref": "#/components/parameters/kbn_xsrf"
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "host": {
                    "type": "string"
                  }
                }
              }
            }
          }
        }
      }
    },
    "/service-tokens": {
      "post": {
        "summary": "Generate service tokens",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "name": {
                      "type": "string"
                    },
                    "value": {
                      "type": "string"
                    }
                  }
                }
              }
            }
          }
        },
        "operationId": "generate-service-token-deprecated",
        "parameters": [
          {
            "$ref": "#/components/parameters/kbn_xsrf"
          }
        ],
        "deprecated": true
      }
    },
    "/service_tokens": {
      "post": {
        "summary": "Generate service tokens",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "name": {
                      "type": "string"
                    },
                    "value": {
                      "type": "string"
                    }
                  }
                }
              }
            }
          }
        },
        "operationId": "generate-service-token",
        "parameters": [
          {
            "$ref": "#/components/parameters/kbn_xsrf"
          }
        ]
      }
    },
    "/epm/categories": {
      "get": {
        "summary": "Package categories",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/get_categories_response"
                }
              }
            }
          }
        },
        "operationId": "get-package-categories"
      },
      "parameters": [
        {
          "in": "query",
          "name": "prerelease",
          "schema": {
            "type": "boolean",
            "default": false
          },
          "description": "Whether to include prerelease packages in categories count (e.g. beta, rc, preview) "
        },
        {
          "in": "query",
          "name": "experimental",
          "deprecated": true,
          "schema": {
            "type": "boolean",
            "default": false
          }
        },
        {
          "in": "query",
          "name": "include_policy_templates",
          "schema": {
            "type": "boolean",
            "default": false
          }
        }
      ]
    },
    "/epm/packages/limited": {
      "get": {
        "summary": "Packages - Get limited list",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "items": {
                      "type": "array",
                      "items": {
                        "type": "string"
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "operationId": "list-limited-packages"
      },
      "parameters": []
    },
    "/epm/packages": {
      "get": {
        "summary": "Packages - List",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/get_packages_response"
                }
              }
            }
          }
        },
        "operationId": "list-all-packages"
      },
      "parameters": [
        {
          "in": "query",
          "name": "excludeInstallStatus",
          "schema": {
            "type": "boolean",
            "default": false
          },
          "description": "Whether to exclude the install status of each package. Enabling this option will opt in to caching for the response via `cache-control` headers. If you don't need up-to-date installation info for a package, and are querying for a list of available packages, providing this flag can improve performance substantially."
        },
        {
          "in": "query",
          "name": "prerelease",
          "schema": {
            "type": "boolean",
            "default": false
          },
          "description": "Whether to return prerelease versions of packages (e.g. beta, rc, preview) "
        },
        {
          "in": "query",
          "name": "experimental",
          "deprecated": true,
          "schema": {
            "type": "boolean",
            "default": false
          }
        },
        {
          "in": "query",
          "name": "category",
          "schema": {
            "type": "string"
          }
        }
      ]
    },
    "/epm/packages/_bulk": {
      "post": {
        "summary": "Packages - Bulk install",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/bulk_install_packages_response"
                }
              }
            }
          }
        },
        "operationId": "bulk-install-packages"
      },
      "parameters": []
    },
    "/epm/packages/{pkgkey}": {
      "get": {
        "summary": "Packages - Info",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "allOf": [
                    {
                      "properties": {
                        "response": {
                          "$ref": "#/components/schemas/package_info"
                        }
                      }
                    },
                    {
                      "properties": {
                        "status": {
                          "type": "string",
                          "enum": [
                            "installed",
                            "installing",
                            "install_failed",
                            "not_installed"
                          ]
                        },
                        "savedObject": {
                          "type": "string"
                        }
                      },
                      "required": [
                        "status",
                        "savedObject"
                      ]
                    }
                  ]
                }
              }
            }
          }
        },
        "operationId": "get-package-deprecated",
        "security": [
          {
            "basicAuth": []
          }
        ],
        "parameters": [
          {
            "schema": {
              "type": "string"
            },
            "name": "pkgkey",
            "in": "path",
            "required": true
          }
        ],
        "deprecated": true
      },
      "post": {
        "summary": "Packages - Install",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "response": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string"
                          },
                          "type": {
                            "oneOf": [
                              {
                                "$ref": "#/components/schemas/kibana_saved_object_type"
                              },
                              {
                                "$ref": "#/components/schemas/elasticsearch_asset_type"
                              }
                            ]
                          }
                        },
                        "required": [
                          "id",
                          "type"
                        ]
                      }
                    }
                  },
                  "required": [
                    "response"
                  ]
                }
              }
            }
          }
        },
        "operationId": "install-package-deprecated",
        "description": "",
        "parameters": [
          {
            "$ref": "#/components/parameters/kbn_xsrf"
          },
          {
            "schema": {
              "type": "string"
            },
            "name": "pkgkey",
            "in": "path",
            "required": true
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "force": {
                    "type": "boolean"
                  }
                }
              }
            }
          }
        },
        "deprecated": true
      },
      "delete": {
        "summary": "Packages - Delete",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "response": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string"
                          },
                          "type": {
                            "oneOf": [
                              {
                                "$ref": "#/components/schemas/kibana_saved_object_type"
                              },
                              {
                                "$ref": "#/components/schemas/elasticsearch_asset_type"
                              }
                            ]
                          }
                        },
                        "required": [
                          "id",
                          "type"
                        ]
                      }
                    }
                  },
                  "required": [
                    "response"
                  ]
                }
              }
            }
          }
        },
        "operationId": "delete-package-deprecated",
        "parameters": [
          {
            "$ref": "#/components/parameters/kbn_xsrf"
          },
          {
            "schema": {
              "type": "string"
            },
            "name": "pkgkey",
            "in": "path",
            "required": true
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "force": {
                    "type": "boolean"
                  }
                }
              }
            }
          }
        },
        "deprecated": true
      }
    },
    "/epm/packages/{pkgName}/{pkgVersion}": {
      "get": {
        "summary": "Packages - Info",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "allOf": [
                    {
                      "properties": {
                        "item": {
                          "$ref": "#/components/schemas/package_info"
                        }
                      }
                    },
                    {
                      "properties": {
                        "status": {
                          "type": "string",
                          "enum": [
                            "installed",
                            "installing",
                            "install_failed",
                            "not_installed"
                          ]
                        },
                        "savedObject": {
                          "type": "object"
                        },
                        "latestVersion": {
                          "type": "string"
                        },
                        "keepPoliciesUpToDate": {
                          "type": "boolean"
                        },
                        "notice": {
                          "type": "string"
                        },
                        "licensePath": {
                          "type": "string"
                        }
                      },
                      "required": [
                        "status",
                        "savedObject"
                      ]
                    }
                  ]
                }
              }
            }
          }
        },
        "operationId": "get-package",
        "security": [
          {
            "basicAuth": []
          }
        ]
      },
      "parameters": [
        {
          "schema": {
            "type": "string"
          },
          "name": "pkgName",
          "in": "path",
          "required": true
        },
        {
          "schema": {
            "type": "string"
          },
          "name": "pkgVersion",
          "in": "path",
          "required": true
        },
        {
          "schema": {
            "type": "boolean"
          },
          "name": "ignoreUnverified",
          "description": "Ignore if the package is fails signature verification",
          "in": "query"
        },
        {
          "schema": {
            "type": "boolean"
          },
          "name": "full",
          "description": "Return all fields from the package manifest, not just those supported by the Elastic Package Registry",
          "in": "query"
        }
      ],
      "post": {
        "summary": "Packages - Install",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "items": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string"
                          },
                          "type": {
                            "oneOf": [
                              {
                                "$ref": "#/components/schemas/kibana_saved_object_type"
                              },
                              {
                                "$ref": "#/components/schemas/elasticsearch_asset_type"
                              }
                            ]
                          }
                        },
                        "required": [
                          "id",
                          "type"
                        ]
                      }
                    },
                    "_meta": {
                      "type": "object",
                      "properties": {
                        "install_source": {
                          "type": "string",
                          "enum": [
                            "registry",
                            "upload",
                            "bundled"
                          ]
                        }
                      }
                    }
                  },
                  "required": [
                    "items"
                  ]
                }
              }
            }
          }
        },
        "operationId": "install-package",
        "description": "",
        "parameters": [
          {
            "$ref": "#/components/parameters/kbn_xsrf"
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "force": {
                    "type": "boolean"
                  },
                  "ignore_constraints": {
                    "type": "boolean"
                  }
                }
              }
            }
          }
        }
      },
      "put": {
        "summary": "Packages - Update",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "items": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string"
                          },
                          "type": {
                            "oneOf": [
                              {
                                "$ref": "#/components/schemas/kibana_saved_object_type"
                              },
                              {
                                "$ref": "#/components/schemas/elasticsearch_asset_type"
                              }
                            ]
                          }
                        },
                        "required": [
                          "id",
                          "type"
                        ]
                      }
                    }
                  },
                  "required": [
                    "items"
                  ]
                }
              }
            }
          }
        },
        "operationId": "update-package",
        "description": "",
        "parameters": [
          {
            "schema": {
              "type": "string"
            },
            "name": "pkgName",
            "in": "path",
            "required": true
          },
          {
            "schema": {
              "type": "string"
            },
            "name": "pkgVersion",
            "in": "path",
            "required": true
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "keepPoliciesUpToDate": {
                    "type": "boolean"
                  }
                }
              }
            }
          }
        }
      },
      "delete": {
        "summary": "Packages - Delete",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "items": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string"
                          },
                          "type": {
                            "oneOf": [
                              {
                                "$ref": "#/components/schemas/kibana_saved_object_type"
                              },
                              {
                                "$ref": "#/components/schemas/elasticsearch_asset_type"
                              }
                            ]
                          }
                        },
                        "required": [
                          "id",
                          "type"
                        ]
                      }
                    }
                  },
                  "required": [
                    "items"
                  ]
                }
              }
            }
          }
        },
        "operationId": "delete-package",
        "parameters": [
          {
            "$ref": "#/components/parameters/kbn_xsrf"
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "force": {
                    "type": "boolean"
                  }
                }
              }
            }
          }
        }
      }
    },
    "/epm/packages/{pkgName}/{pkgVersion}/{filePath}": {
      "get": {
        "summary": "Packages - Get file from registry",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "body": {
                      "type": "object"
                    },
                    "statusCode": {
                      "type": "number"
                    },
                    "headers": {
                      "type": "object"
                    }
                  }
                }
              }
            }
          }
        },
        "operationId": "packages-get-file"
      },
      "parameters": [
        {
          "schema": {
            "type": "string"
          },
          "name": "pkgName",
          "in": "path",
          "required": true
        },
        {
          "schema": {
            "type": "string"
          },
          "name": "pkgVersion",
          "in": "path",
          "required": true
        },
        {
          "schema": {
            "type": "string"
          },
          "name": "filePath",
          "in": "path",
          "required": true
        }
      ]
    },
    "/epm/packages/{pkgName}/stats": {
      "get": {
        "summary": "Get stats for a package",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "response": {
                      "$ref": "#/components/schemas/package_usage_stats"
                    }
                  },
                  "required": [
                    "response"
                  ]
                }
              }
            }
          }
        },
        "operationId": "get-package-stats",
        "security": [
          {
            "basicAuth": []
          }
        ]
      },
      "parameters": [
        {
          "schema": {
            "type": "string"
          },
          "name": "pkgName",
          "in": "path",
          "required": true
        }
      ]
    },
    "/agents/setup": {
      "get": {
        "summary": "Agents setup - Info",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/fleet_status_response"
                }
              }
            }
          }
        },
        "operationId": "get-agents-setup-status",
        "security": [
          {
            "basicAuth": []
          }
        ]
      },
      "post": {
        "summary": "Agents setup - Create",
        "operationId": "setup-agents",
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/fleet_setup_response"
                }
              }
            }
          }
        },
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "admin_username": {
                    "type": "string"
                  },
                  "admin_password": {
                    "type": "string"
                  }
                },
                "required": [
                  "admin_username",
                  "admin_password"
                ]
              }
            }
          }
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/kbn_xsrf"
          }
        ]
      }
    },
    "/agent-status": {
      "get": {
        "summary": "Agents - Summary stats",
        "tags": [],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "error": {
                      "type": "integer"
                    },
                    "events": {
                      "type": "integer"
                    },
                    "inactive": {
                      "type": "integer"
                    },
                    "offline": {
                      "type": "integer"
                    },
                    "online": {
                      "type": "integer"
                    },
                    "other": {
                      "type": "integer"
                    },
                    "total": {
                      "type": "integer"
                    },
                    "updating": {
                      "type": "integer"
                    }
                  },
                  "required": [
                    "error",
                    "events",
                    "inactive",
                    "offline",
                    "online",
                    "other",
                    "total",
                    "updating"
                  ]
