{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para a entidade avaliação de desempenhi (PerformanceEvaluations) para produtos TOTVS",
			"url": "{{host}}/api/rh/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8051"
				}
			}
		}
	],
	"info": {
		"description": "API para a entidade avaliação de desempenho (PerformanceEvaluations) para produtos TOTVS",
		"version": "1.000",
		"title": "Avaliação de desempenho",
		"contact": {
			"name": "T-Talk",
			"url": "API.Totvs.com.br",
			"email": "comiteintegracao@totvs.com.br"
		},
		"x-totvs": {
			"messageDocumentation": {
				"name": "PerformanceEvaluations",
				"description": "Avaliação de Desempenho",
				"segment": "Recursos Humanos"
			},
			"productInformation": [
				{
					"product": "RM",
					"contact": "rm_FW@totvs.com.br",
					"description": "Listagem das avaliações de desempenho"
				}
			]
		}
	},
	"paths": {
		"/performanceEvaluations/{id}": {
			"get": {
				"tags": [
					"PerformanceEvaluations"
				],
				"summary": "Retorna dados da avaliação de desempenho",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Retorna dados da avaliação de desempenho",
				"operationId": "get",
				"parameters": [
					{
						"$ref": "#/components/parameters/PerformanceEvaluationsId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"200": {
						"description": "Operação executada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/PerformanceEvaluations_1_000.json#/definitions/PerformanceEvaluationsInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"delete": {
				"tags": [
					"PerformanceEvaluations"
				],
				"summary": "Exclui avaliação de desempenho",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Exclui avaliação de desempenho",
				"operationId": "delete",
				"parameters": [
					{
						"$ref": "#/components/parameters/PerformanceEvaluationsId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"200": {
						"description": "Operação executada com sucesso"
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"put": {
				"tags": [
					"PerformanceEvaluations"
				],
				"summary": "Atualiza uma avaliação de desempenho",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Atualiza uma avaliação de desempenho. Caso não seja informado alguma propriedade para ser atualizada, está será considerada nula.",
				"operationId": "put",
				"parameters": [
					{
						"$ref": "#/components/parameters/PerformanceEvaluationsId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"requestBody": {
					"description": "Objeto para atualizar a avaliação de desempenho",
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/PerformanceEvaluations_1_000.json#/definitions/PerformanceEvaluationsInfo"
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Operação executada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/PerformanceEvaluations_1_000.json#/definitions/PerformanceEvaluationsInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		},
		"/performanceEvaluations": {
			"get": {
				"tags": [
					"PerformanceEvaluations"
				],
				"summary": "Retorna todas as avaliações de desempenho parametrizados da base",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Retorna todos as avaliações de desempenho",
				"operationId": "getAll",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/PageSize"
					},
					{
						"$ref": "#/components/parameters/PerformanceEvaluationsIds"
					},
					{
						"$ref": "#/components/parameters/Names"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/PerformanceEvaluations_1_000.json#/definitions/PerformanceEvaluationsInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro no momento da listagem das avaliações de desempenho",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"post": {
				"tags": [
					"PerformanceEvaluations"
				],
				"summary": "Inclui uma nova avaliação de desempenho",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponivel com todos parametros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"description": "Inclui um novo registro na tabela de avaliação de desempenho",
				"operationId": "post",
				"requestBody": {
					"description": "Objeto para atualizar a avaliação de desempenho",
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/PerformanceEvaluations_1_000.json#/definitions/PerformanceEvaluationsInfo"
							}
						}
					}
				},
				"responses": {
					"200": {
						"description": "Operação executada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/PerformanceEvaluations_1_000.json#/definitions/PerformanceEvaluationsInfo"
								}
							}
						}
					},
					"400": {
						"description": "Erro na execução da operação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		}
	},
	"components": {
		"parameters": {
			"PerformanceEvaluationsId": {
				"name": "id",
				"in": "path",
				"required": true,
				"description": "Identificador Único representando a avaliação de desempenho",
				"schema": {
					"type": "string"
				}
			},
			"PerformanceEvaluationsIds": {
				"name": "performanceEvaluationsIds",
				"in": "query",
				"description": "Códigos das avaliações de desempenho retornados pelo get separado por vírgula (,) ",
				"required": false,
				"example": "param1,param2",
				"schema": {
					"type": "string"
				}
			},
			"Names": {
				"name": "name",
				"in": "query",
				"description": "Busca avaliações de desempenho por nome",
				"required": false,
				"example": "Avaliação de desempenho busca",
				"schema": {
					"type": "string"
				}
			}
		},
		"schemas": {}
	}
}
