info:
  contact: {email: frikky@shuffler.io, name: '@frikkylikeme', url: 'https://twitter.com/frikkylikeme'}
  description: Automated generation of Cyberark Privileged Access Security
  title: Cyberark Privileged Access Security
  version: 1.0.0
  x-logo: data:image/png;base64,
openapi: 3.0.2
paths:
  tmp0:
    post:
      description: Add a new privileged account or SSH key to the Vault.
      parameters:
      - description: The name of the account.
        example: string5
        in: query
        name: Name
        required: true
        schema: {type: string}
      - description: Account username.
        example: string
        in: query
        name: User Name
        required: false
        schema: {type: string}
      - description: Whether or not to restrict access only to specified remote machines
        example: 'True'
        in: query
        name: Remote Machines Access Restricted To Remote Machines
        required: false
        schema: {type: boolean}
      - description: List of remote machines, separated by semicolons.
        example: server1.cyberark.com;server2.cyberark.com
        in: query
        name: Remote Machines Access Remote Machines
        required: false
        schema: {type: string}
      - description: Whether the account secret is automatically managed by the CPM.
        example: 'True'
        in: query
        name: Secret Management Automatic Management Enabled
        required: false
        schema: {type: boolean}
      - description: Reason for disabling automatic secret management.
        example: A reason goes here.
        in: query
        name: Secret Management Manual Management Reason
        required: false
        schema: {type: string}
      - description: The platform assigned to this account.
        example: WindowsJustInTime
        in: query
        name: Platform ID
        required: true
        schema: {type: string}
      - description: The password value. This will not be returned in the API output.
        example: string2
        in: query
        name: Secret
        required: false
        schema: {type: string}
      - description: JSON containing key-value pairs to associate with the account,
          as defined by the account platform. These properties are validated against
          the mandatory and optional properties of the specified platform's definition.
          Optional properties that do not exist on the account will not be returned
          here. Internal properties are not returned.
        example: '{"Location": "IT", "OwnerName": "MSSPAdmin"}'
        in: query
        name: Name
        required: false
        schema: {type: string}
      - description: The Safe where the account will be created.
        example: cicd_database
        in: query
        name: Safe Name
        required: true
        schema: {type: string}
      - description: The type of password. Either "password" or "key"
        example: key
        in: query
        name: Secret Type
        required: false
        schema: {type: string}
      - description: The name or address of the machine where the account will be
          used. vDNS/IP/URL where the account is managed
        example: 8.8.8.8
        in: query
        name: Address
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp0'}
          description: Successful request
      summary: Cyberark Privileged Access Security Add Account
  tmp1:
    post:
      description: Add an account as a member to an existing account group.
      parameters:
      - description: The unique ID of account group.
        example: '57_6'
        in: query
        name: Group ID
        required: true
        schema: {type: string}
      - description: The ID of the account that will be added as a member to the group.
        example: '57_10'
        in: query
        name: Account ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp1'}
          description: Successful request
      summary: Cyberark Privileged Access Security Add Account to Group
  tmp10:
    post:
      description: "Returns information about all of the user\u2019s Safes in the\
        \ Vault."
      parameters: []
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp10'}
          description: Successful request
      summary: Cyberark Privileged Access Security Get Safes
  tmp11:
    post:
      description: Returns PTA Security Events.
      parameters:
      - description: The status of the security event (open or closed).
        example: open
        in: query
        name: Status
        required: false
        schema: {type: string}
      - description: The time to get the security events from
        example: '{time.now():-1days}'
        in: body
        name: Last Updated Event Date
        required: false
        schema: {type: string}
      - description: The unique account identifier of the account that is referred
          to in the Security Event.
        example: '57_10'
        in: query
        name: Account ID
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp11'}
          description: Successful request
      summary: Cyberark Privileged Access Security Get Security Events
  tmp12:
    post:
      description: Returns information about a specific user in the Vault.
      parameters:
      - description: The ID of the user for which information is returned.
        example: '117'
        in: body
        name: User ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp12'}
          description: Successful request
      summary: Cyberark Privileged Access Security Get User Details
  tmp13:
    post:
      description: Returns all existing user groups.
      parameters:
      - description: Filter according to REST standard.
        example: groupType eq Directory
        in: query
        name: Filter
        required: false
        schema: {type: string}
      - description: Search groups. Search will match when ALL search terms appear
          in the group name.
        example: vault admins
        in: query
        name: Search
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp13'}
          description: Successful request
      summary: Cyberark Privileged Access Security Get User Groups
  tmp14:
    post:
      description: Returns all existing users in the Vault except for the Master and
        the Batch built-in users.
      parameters:
      - description: A custom filter, see plugin documentation for more details.
        example: userType eq Built-InAdmins
        in: query
        name: Filter
        required: false
        schema: {type: string}
      - description: 'A seach string that will match in the following fields: username,
          first name, last name.'
        example: search string
        in: query
        name: Search
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp14'}
          description: Successful request
      summary: Cyberark Privileged Access Security Get Users
  tmp15:
    post:
      description: Remove an account member from an account group.
      parameters:
      - description: The unique ID of the group
        example: '57_6'
        in: query
        name: Group ID
        required: true
        schema: {type: string}
      - description: The unique ID of the account
        example: '57_10'
        in: query
        name: Account ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp15'}
          description: Successful request
      summary: Cyberark Privileged Access Security Remove Account from Group
  tmp16:
    post:
      description: Remove a specific user from a user group in the Vault.
      parameters:
      - description: The unique ID of the group.
        example: '51'
        in: body
        name: Group ID
        required: true
        schema: {type: string}
      - description: The name of the user to be removed.
        example: newUser
        in: query
        name: Member ID
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp16'}
          description: Successful request
      summary: Cyberark Privileged Access Security Remove User from Group
  tmp17:
    post:
      description: Reset an existing Vault user's password.
      parameters:
      - description: The user's ID
        example: '118'
        in: body
        name: ID
        required: true
        schema: {type: string}
      - description: The new password
        example: Thisisnew123#@!
        in: query
        name: New Password
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp17'}
          description: Successful request
      summary: Cyberark Privileged Access Security Reset User Password
  tmp18:
    post:
      description: Update an existing account's details.
      parameters:
      - description: The ID of the account to update
        example: '42_4'
        in: query
        name: Account ID
        required: true
        schema: {type: string}
      - description: The name of the account.
        example: string5
        in: query
        name: Name
        required: false
        schema: {type: string}
      - description: Account username.
        example: string
        in: query
        name: User Name
        required: false
        schema: {type: string}
      - description: Whether or not to restrict access only to specified remote machines
        example: 'True'
        in: query
        name: Remote Machines Access Restricted To Remote Machines
        required: false
        schema: {type: boolean}
      - description: List of remote machines, separated by semicolons.
        example: server1.cyberark.com;server2.cyberark.com
        in: query
        name: Remote Machines Access Remote Machines
        required: false
        schema: {type: string}
      - description: Whether the account secret is automatically managed by the CPM.
        example: 'True'
        in: query
        name: Secret Management Automatic Management Enabled
        required: false
        schema: {type: boolean}
      - description: Reason for disabling automatic secret management.
        example: A reason goes here.
        in: query
        name: Secret Management Manual Management Reason
        required: false
        schema: {type: string}
      - description: The platform assigned to this account.
        example: WindowsJustInTime
        in: query
        name: Platform ID
        required: false
        schema: {type: string}
      - description: JSON containing key-value pairs to associate with the account,
          as defined by the account platform. These properties are validated against
          the mandatory and optional properties of the specified platform's definition.
          Optional properties that do not exist on the account will not be returned
          here. Internal properties are not returned.
        example: '{"Location": "IT", "OwnerName": "MSSPAdmin"}'
        in: query
        name: Name
        required: false
        schema: {type: string}
      - description: The name or address of the machine where the account will be
          used. vDNS/IP/URL where the account is managed
        example: 8.8.8.8
        in: query
        name: Address
        required: false
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp18'}
          description: Successful request
      summary: Cyberark Privileged Access Security Update Account
  tmp19:
    post:
      description: Update the status of a security event to open or closed.
      parameters:
      - description: The ID of the security event.
        example: 5e6024fee4b004e867f0d31f
        in: query
        name: Event ID
        required: true
        schema: {type: string}
      - description: The value to set status to, either "open" or "closed"
        example: closed
        in: query
        name: Status
        required: true
        schema: {type: string}
      responses:
        '200':
          content:
            application/json:
              schema: {$ref: '#/components/schemas/tmp19'}
          description: Successful request
      summary: Cyberark Privileged Access Security Update Security Event Status
  tmp2:
    post:
      description: Add a new user to the Vault.
      parameters:
      - description: Username
        example: ''
        in: query
        name: Username
        required: true
        schema: {type: string}
      - description: The user's distinguished name
        example: newUser6@cyberark
        in: query
