{
  "openapi": "3.0.2",
  "info": {
    "title": "simcore-service-director-v2",
    "description": " Orchestrates the pipeline of services defined by the user",
    "version": "2.0.0"
  },
  "paths": {
    "/": {
      "get": {
        "summary": "Check Service Health",
        "operationId": "check_service_health__get",
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {}
              }
            }
          }
        }
      }
    },
    "/meta": {
      "get": {
        "summary": "Get Service Metadata",
        "operationId": "get_service_metadata_meta_get",
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Meta"
                }
              }
            }
          }
        }
      }
    },
    "/v0/services": {
      "get": {
        "tags": [
          "services"
        ],
        "summary": "List Services",
        "description": "Lists services available in the deployed registry",
        "operationId": "list_services_v0_services_get",
        "parameters": [
          {
            "description": "The service type:\n   - computational - a computational service\n   - interactive - an interactive service\n",
            "required": false,
            "schema": {
              "allOf": [
                {
                  "$ref": "#/components/schemas/ServiceType"
                }
              ],
              "description": "The service type:\n   - computational - a computational service\n   - interactive - an interactive service\n"
            },
            "name": "service_type",
            "in": "query"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServicesArrayEnveloped"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v0/services/{service_key}/{service_version}/extras": {
      "get": {
        "tags": [
          "services"
        ],
        "summary": "Get Extra Service Versioned",
        "description": "Returns the service extras",
        "operationId": "get_extra_service_versioned_v0_services__service_key___service_version__extras_get",
        "parameters": [
          {
            "description": "Distinctive name for the node based on the docker registry path",
            "required": true,
            "schema": {
              "title": "Service Key",
              "pattern": "^(simcore)/(services)/(comp|dynamic|frontend)(/[\\w/-]+)+$",
              "type": "string",
              "description": "Distinctive name for the node based on the docker registry path"
            },
            "name": "service_key",
            "in": "path"
          },
          {
            "description": "The tag/version of the service",
            "required": true,
            "schema": {
              "title": "Service Version",
              "pattern": "^(0|[1-9]\\d*)(\\.(0|[1-9]\\d*)){2}(-(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*)(\\.(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*))*)?(\\+[-\\da-zA-Z]+(\\.[-\\da-zA-Z-]+)*)?$",
              "type": "string",
              "description": "The tag/version of the service"
            },
            "name": "service_version",
            "in": "path"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceExtrasEnveloped"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v0/services/{service_key}/{service_version}": {
      "get": {
        "tags": [
          "services"
        ],
        "summary": "Get Service Versioned",
        "description": "Returns details of the selected service if available in the platform",
        "operationId": "get_service_versioned_v0_services__service_key___service_version__get",
        "parameters": [
          {
            "description": "Distinctive name for the node based on the docker registry path",
            "required": true,
            "schema": {
              "title": "Service Key",
              "pattern": "^(simcore)/(services)/(comp|dynamic|frontend)(/[\\w/-]+)+$",
              "type": "string",
              "description": "Distinctive name for the node based on the docker registry path"
            },
            "name": "service_key",
            "in": "path"
          },
          {
            "description": "The tag/version of the service",
            "required": true,
            "schema": {
              "title": "Service Version",
              "pattern": "^(0|[1-9]\\d*)(\\.(0|[1-9]\\d*)){2}(-(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*)(\\.(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*))*)?(\\+[-\\da-zA-Z]+(\\.[-\\da-zA-Z-]+)*)?$",
              "type": "string",
              "description": "The tag/version of the service"
            },
            "name": "service_version",
            "in": "path"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServicesArrayEnveloped"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v0/running_interactive_services": {
      "get": {
        "tags": [
          "services"
        ],
        "summary": "List Running Interactive Services",
        "description": "Lists of running interactive services",
        "operationId": "list_running_interactive_services_v0_running_interactive_services_get",
        "parameters": [
          {
            "description": "The ID of the user that starts the service",
            "required": true,
            "schema": {
              "title": "User Id",
              "type": "string",
              "description": "The ID of the user that starts the service"
            },
            "name": "user_id",
            "in": "query"
          },
          {
            "description": "The ID of the project in which the service starts",
            "required": true,
            "schema": {
              "title": "Project Id",
              "type": "string",
              "description": "The ID of the project in which the service starts"
            },
            "name": "project_id",
            "in": "query"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/RunningServicesDetailsArrayEnveloped"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      },
      "post": {
        "tags": [
          "services"
        ],
        "summary": "Start Interactive Service",
        "description": "Starts an interactive service in the  platform",
        "operationId": "start_interactive_service_v0_running_interactive_services_post",
        "parameters": [
          {
            "description": "The ID of the user that starts the service",
            "required": true,
            "schema": {
              "title": "User Id",
              "type": "string",
              "description": "The ID of the user that starts the service"
            },
            "name": "user_id",
            "in": "query"
          },
          {
            "description": "The ID of the project in which the service starts",
            "required": true,
            "schema": {
              "title": "Project Id",
              "type": "string",
              "description": "The ID of the project in which the service starts"
            },
            "name": "project_id",
            "in": "query"
          },
          {
            "description": "distinctive name for the node based on the docker registry path",
            "required": true,
            "schema": {
              "title": "Service Key",
              "pattern": "^(simcore)/(services)/(comp|dynamic|frontend)(/[\\w/-]+)+$",
              "type": "string",
              "description": "distinctive name for the node based on the docker registry path"
            },
            "example": [
              "simcore/services/comp/itis/sleeper",
              "simcore/services/dynamic/3dviewer"
            ],
            "name": "service_key",
            "in": "query"
          },
          {
            "description": "The tag/version of the service",
            "required": true,
            "schema": {
              "title": "Service Tag",
              "pattern": "^(0|[1-9]\\d*)(\\.(0|[1-9]\\d*)){2}(-(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*)(\\.(0|[1-9]\\d*|\\d*[-a-zA-Z][-\\da-zA-Z]*))*)?(\\+[-\\da-zA-Z]+(\\.[-\\da-zA-Z-]+)*)?$",
              "type": "string",
              "description": "The tag/version of the service"
            },
            "example": "1.0.0",
            "name": "service_tag",
            "in": "query"
          },
          {
            "description": "The uuid to assign the service with",
            "required": true,
            "schema": {
              "title": "Service Uuid",
              "type": "string",
              "description": "The uuid to assign the service with"
            },
            "name": "service_uuid",
            "in": "query"
          },
          {
            "description": "predefined basepath for the backend service otherwise uses root",
            "required": false,
            "schema": {
              "title": "Service Base Path",
              "type": "string",
              "description": "predefined basepath for the backend service otherwise uses root",
              "default": ""
            },
            "example": "/x/EycCXbU0H/",
            "name": "service_base_path",
            "in": "query"
          }
        ],
        "responses": {
          "201": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {}
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v0/running_interactive_services/{service_uuid}": {
      "delete": {
        "tags": [
          "services"
        ],
        "summary": "Stop Interactive Service",
        "operationId": "stop_interactive_service_v0_running_interactive_services__service_uuid__delete",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Service Uuid",
              "type": "string"
            },
            "name": "service_uuid",
            "in": "path"
          }
        ],
        "responses": {
          "204": {
            "description": "Successful Response"
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v2/computations": {
      "post": {
        "tags": [
          "computations"
        ],
        "summary": "Create and optionally start a new computation",
        "operationId": "create_computation_v2_computations_post",
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/ComputationCreate"
              }
            }
          },
          "required": true
        },
        "responses": {
          "201": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ComputationGet"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v2/computations/{project_id}": {
      "get": {
        "tags": [
          "computations"
        ],
        "summary": "Returns a computation pipeline state",
        "operationId": "get_computation_v2_computations__project_id__get",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Project Id",
              "type": "string",
              "format": "uuid"
            },
            "name": "project_id",
            "in": "path"
          },
          {
            "required": true,
            "schema": {
              "title": "User Id",
              "exclusiveMinimum": true,
              "type": "integer",
              "minimum": 0
            },
            "name": "user_id",
            "in": "query"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ComputationGet"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      },
      "delete": {
        "tags": [
          "computations"
        ],
        "summary": "Deletes a computation pipeline",
        "operationId": "delete_computation_v2_computations__project_id__delete",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Project Id",
              "type": "string",
              "format": "uuid"
            },
            "name": "project_id",
            "in": "path"
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/ComputationDelete"
              }
            }
          },
          "required": true
        },
        "responses": {
          "204": {
            "description": "Successful Response"
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v2/computations/{project_id}:stop": {
      "post": {
        "tags": [
          "computations"
        ],
        "summary": "Stops a computation pipeline",
        "operationId": "stop_computation_v2_computations__project_id__stop_post",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Project Id",
              "type": "string",
              "format": "uuid"
            },
            "name": "project_id",
            "in": "path"
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/ComputationStop"
              }
            }
          },
          "required": true
        },
        "responses": {
          "202": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ComputationGet"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v2/computations/{project_id}/tasks/-/logfile": {
      "get": {
        "tags": [
          "computations"
        ],
        "summary": "Gets computation task logs file after is done",
        "description": "Returns download links to log-files of each task in a computation.\nEach log is only available when the corresponding task is done",
        "operationId": "get_all_tasks_log_files_v2_computations__project_id__tasks___logfile_get",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Project Id",
              "type": "string",
              "format": "uuid"
            },
            "name": "project_id",
            "in": "path"
          },
          {
            "required": true,
            "schema": {
              "title": "User Id",
              "exclusiveMinimum": true,
              "type": "integer",
              "minimum": 0
            },
            "name": "user_id",
            "in": "query"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "title": "Response Get All Tasks Log Files V2 Computations  Project Id  Tasks   Logfile Get",
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/TaskLogFileGet"
                  }
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v2/computations/{project_id}/tasks/{node_uuid}/logfile": {
      "get": {
        "tags": [
          "computations"
        ],
        "summary": "Gets computation task logs file after is done",
        "description": "Returns a link to download logs file of a give task.\nThe log is only available when the task is done",
        "operationId": "get_task_logs_file_v2_computations__project_id__tasks__node_uuid__logfile_get",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Project Id",
              "type": "string",
              "format": "uuid"
            },
            "name": "project_id",
            "in": "path"
          },
          {
            "required": true,
            "schema": {
              "title": "Node Uuid",
              "type": "string",
              "format": "uuid"
            },
            "name": "node_uuid",
            "in": "path"
          },
          {
            "required": true,
            "schema": {
              "title": "User Id",
              "exclusiveMinimum": true,
              "type": "integer",
              "minimum": 0
            },
            "name": "user_id",
            "in": "query"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TaskLogFileGet"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v2/dynamic_services": {
      "get": {
        "tags": [
          "dynamic services"
        ],
        "summary": "returns a list of running interactive services filtered by user_id and/or project_idboth legacy (director-v0) and modern (director-v2)",
        "operationId": "list_running_dynamic_services_v2_dynamic_services_get",
        "parameters": [
          {
            "required": false,
            "schema": {
              "title": "User Id",
              "exclusiveMinimum": true,
              "type": "integer",
              "minimum": 0
            },
            "name": "user_id",
            "in": "query"
          },
          {
            "required": false,
            "schema": {
              "title": "Project Id",
              "type": "string",
              "format": "uuid"
            },
            "name": "project_id",
            "in": "query"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "title": "Response List Running Dynamic Services V2 Dynamic Services Get",
                  "type": "array",
                  "items": {
                    "$ref": "#/components/schemas/RunningDynamicServiceDetails"
                  }
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      },
      "post": {
        "tags": [
          "dynamic services"
        ],
        "summary": "creates & starts the dynamic service",
        "operationId": "create_dynamic_service_v2_dynamic_services_post",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "X-Dynamic-Sidecar-Request-Dns",
              "type": "string"
            },
            "name": "x-dynamic-sidecar-request-dns",
            "in": "header"
          },
          {
            "required": true,
            "schema": {
              "title": "X-Dynamic-Sidecar-Request-Scheme",
              "type": "string"
            },
            "name": "x-dynamic-sidecar-request-scheme",
            "in": "header"
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/DynamicServiceCreate"
              }
            }
          },
          "required": true
        },
        "responses": {
          "201": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/RunningDynamicServiceDetails"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v2/dynamic_services/{node_uuid}": {
      "get": {
        "tags": [
          "dynamic services"
        ],
        "summary": "assembles the status for the dynamic-sidecar",
        "operationId": "get_dynamic_sidecar_status_v2_dynamic_services__node_uuid__get",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Node Uuid",
              "type": "string",
              "format": "uuid"
            },
            "name": "node_uuid",
            "in": "path"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/RunningDynamicServiceDetails"
                }
              }
            }
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      },
      "delete": {
        "tags": [
          "dynamic services"
        ],
        "summary": "stops previously spawned dynamic-sidecar",
        "operationId": "stop_dynamic_service_v2_dynamic_services__node_uuid__delete",
        "parameters": [
          {
            "required": true,
            "schema": {
              "title": "Node Uuid",
              "type": "string",
              "format": "uuid"
            },
            "name": "node_uuid",
            "in": "path"
          },
          {
            "required": false,
            "schema": {
              "title": "Can Save",
              "type": "boolean",
              "default": true
            },
            "name": "can_save",
            "in": "query"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful Response",
            "content": {
              "application/json": {
                "schema": {}
              }
            }
          },
          "204": {
            "description": "No Content"
          },
          "422": {
            "description": "Validation Error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/HTTPValidationError"
                }
              }
            }
          }
        }
      }
    },
    "/v2/dynamic_services/{node_uuid}:retrieve": {
      "post": {
        "tags": [
          "dynamic services"
        ],
        "summary": "Calls the dynamic service's retrieve endpoint with optional port_keys",
        "operationId": "service_retrieve_data_on_ports_v2_dynamic_services__node_uuid__retrieve_post",
        "parameters": [
