{
  "openapi": "3.0.0",
  "servers": [
    {
      "url": "http://localhost:3000/"
    }
  ],
  "info": {
    "version": "1.0.0",
    "title": "AutoRest Validation Test",
    "description": "Test Infrastructure for AutoRest. No server backend exists for these tests."
  },
  "paths": {
    "/fakepath/{subscriptionId}/{resourceGroupName}/{id}?api-version={apiVersion}": {
      "get": {
        "operationId": "validationOfMethodParameters",
        "summary": "",
        "description": "Validates input parameters on the method. See swagger for details.",
        "parameters": [
          {
            "$ref": "#/components/parameters/SubscriptionIdParamterer"
          },
          {
            "name": "resourceGroupName",
            "in": "path",
            "description": "Required string between 3 and 10 chars with pattern [a-zA-Z0-9]+.",
            "required": true,
            "schema": {
              "type": "string",
              "minLength": 3,
              "maxLength": 10,
              "pattern": "[a-zA-Z0-9]+"
            }
          },
          {
            "name": "id",
            "in": "path",
            "description": "Required int multiple of 10 from 100 to 1000.",
            "required": true,
            "schema": {
              "type": "integer",
              "minimum": 100,
              "maximum": 1000,
              "multipleOf": 10
            }
          },
          {
            "$ref": "#/components/parameters/ApiVersionParameter"
          }
        ],
        "tags": ["Redis"],
        "responses": {
          "200": {
            "description": "A list of caches",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Product"
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      },
      "put": {
        "operationId": "validationOfBody",
        "summary": "",
        "description": "Validates body parameters on the method. See swagger for details.",
        "parameters": [
          {
            "$ref": "#/components/parameters/SubscriptionIdParamterer"
          },
          {
            "name": "resourceGroupName",
            "in": "path",
            "description": "Required string between 3 and 10 chars with pattern [a-zA-Z0-9]+.",
            "required": true,
            "schema": {
              "type": "string",
              "minLength": 3,
              "maxLength": 10,
              "pattern": "[a-zA-Z0-9]+"
            }
          },
          {
            "name": "id",
            "in": "path",
            "description": "Required int multiple of 10 from 100 to 1000.",
            "required": true,
            "schema": {
              "type": "integer",
              "minimum": 100,
              "maximum": 1000,
              "multipleOf": 10
            }
          },
          {
            "$ref": "#/components/parameters/ApiVersionParameter"
          }
        ],
        "tags": ["Redis"],
        "responses": {
          "200": {
            "description": "A list of caches",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Product"
                }
              }
            }
          },
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        },
        "requestBody": {
          "x-ms-requestBody-name": "body",
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/Product"
              }
            }
          }
        }
      }
    },
    "/validation/constantsInPath/{constantParam}/value": {
      "get": {
        "operationId": "getWithConstantInPath",
        "parameters": [
          {
            "name": "constantParam",
            "in": "path",
            "required": true,
            "schema": {
              "type": "string",
              "enum": ["constant"]
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success"
          }
        }
      },
      "post": {
        "operationId": "postWithConstantInBody",
        "parameters": [
          {
            "name": "constantParam",
            "in": "path",
            "required": true,
            "schema": {
              "type": "string",
              "enum": ["constant"]
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Product"
                }
              }
            }
          }
        },
        "requestBody": {
          "x-ms-requestBody-name": "body",
          "content": {
            "application/json": {
              "schema": {
                "$ref": "#/components/schemas/Product"
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "Product": {
        "type": "object",
        "description": "The product documentation.",
        "required": ["child", "constString", "constInt", "constChild"],
        "properties": {
          "display_names": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Non required array of unique items from 0 to 6 elements.",
            "maxItems": 6,
            "uniqueItems": true,
            "minItems": 0
          },
          "capacity": {
            "type": "integer",
            "description": "Non required int betwen 0 and 100 exclusive.",
            "exclusiveMinimum": true,
            "exclusiveMaximum": true,
            "maximum": 100,
            "minimum": 0
          },
          "image": {
            "type": "string",
            "description": "Image URL representing the product.",
            "pattern": "http://\\w+"
          },
          "child": {
            "$ref": "#/components/schemas/ChildProduct"
          },
          "constChild": {
            "$ref": "#/components/schemas/ConstantProduct"
          },
          "constInt": {
            "type": "integer",
            "description": "Constant int",
            "enum": [0]
          },
          "constString": {
            "type": "string",
            "description": "Constant string",
            "enum": ["constant"]
          },
          "constStringAsEnum": {
            "type": "string",
            "description": "Constant string as Enum",
            "enum": ["constant_string_as_enum"],
            "x-ms-enum": {
              "name": "EnumConst",
              "modelAsString": false
            }
          }
        }
      },
      "ChildProduct": {
        "type": "object",
        "description": "The product documentation.",
        "required": ["constProperty"],
        "properties": {
          "constProperty": {
            "type": "string",
            "description": "Constant string",
            "enum": ["constant"]
          },
          "count": {
            "type": "integer",
            "description": "Count"
          }
        }
      },
      "ConstantProduct": {
        "type": "object",
        "description": "The product documentation.",
        "required": ["constProperty", "constProperty2"],
        "properties": {
          "constProperty": {
            "type": "string",
            "description": "Constant string",
            "enum": ["constant"]
          },
          "constProperty2": {
            "type": "string",
            "description": "Constant string2",
            "enum": ["constant2"]
          }
        }
      },
      "Error": {
        "type": "object",
        "properties": {
          "code": {
            "type": "integer",
            "format": "int32"
          },
          "message": {
            "type": "string"
          },
          "fields": {
            "type": "string"
          }
        }
      }
    },
    "parameters": {
      "SubscriptionIdParamterer": {
        "name": "subscriptionId",
        "in": "path",
        "description": "Subscription ID.",
        "required": true,
        "schema": {
          "type": "string"
        }
      },
      "ApiVersionParameter": {
        "name": "apiVersion",
        "in": "query",
        "description": "Required string following pattern \\d{2}-\\d{2}-\\d{4}",
        "required": true,
        "schema": {
          "type": "string",
          "pattern": "\\d{2}-\\d{2}-\\d{4}"
        }
      }
    }
  }
}
