{
  "openapi": "3.0.1",
  "info": {
    "title": "Release Candidate Messages",
    "description": "The specifications of messages which are ready for ICAR approval and release",
    "version": "1.0",
    "contact": {
      "name": "Animal Data Exchange Working Group",
      "url": "https://www.icar.org/index.php/technical-bodies/working-groups/animal-data-exchange-wg/",
      "email": "icar@icar.org"
    }
  },
  "servers": [{
    "url": "https://icar-ade.standard.com"
  }],
  "tags": [{
      "name": "ADE-1.2 additions",
      "description": "new endpoints not yet approved"
    },
    {
      "name": "ADE-1.2-feed",
      "description": "Feed messages approved by the working group"
    },
    {
      "name": "ADE-1.1-health",
      "description": "Health messages approved by the working group"
    },
    {
      "name": "ADE-1.1-management",
      "description": "Management messages approved by the working group"
    },
    {
      "name": "ADE-1.1-milk",
      "description": "Milk messages approved by the working group"
    },
    {
      "name": "ADE-1.1-performance",
      "description": "Performance messages approved by the working group"
    },
    {
      "name": "ADE-1.1-registration",
      "description": "Registration messages approved by the working group"
    },
    {
      "name": "ADE-1.1-reproduction",
      "description": "Reproduction messages approved by the working group"
    },
    {
      "name": "ADE-1.3-scheme",
      "description": "Scheme definitions approved by the working group"
    }
  ],
  "paths": {
    "/locations": {
      "get": {
        "operationId": "get-locations",
        "summary": "Get available schema/id combinations for locations.",
        "description": "# Purpose\nProvide a list of available locations.\nShould return only those combinations the user currently logged in has access to.",
        "tags": [
          "ADE-1.1-registration"
        ],
        "responses": {
          "200": {
            "description": "Successful. The response contains the available locations.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/icarLocationCollection"
                },
                "examples": {
                  "base": {
                    "$ref": "#/components/examples/locations"
                  }
                }
              }
            }
          },
          "default": {
            "$ref": "#/components/responses/default"
          }
        }
      }
    },
    "/locations/{location-scheme}/{location-id}/milking-visits": {
      "get": {
        "operationId": "get-milking-visits",
        "summary": "Get the data for milking visits",
        "description": "# Purpose\nProvides data from visits of animals to a milking parlour.\n \nThis comprises all possible data. Anybody can choose to define API's that return less information in the response.\n",
        "tags": [
          "ADE-1.1-milk"
        ],
        "parameters": [{
            "$ref": "#/components/parameters/location-scheme"
          },
          {
            "$ref": "#/components/parameters/location-id"
          },
          {
            "$ref": "#/components/parameters/meta-modified-from"
          },
          {
            "$ref": "#/components/parameters/meta-modified-to"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful. The response contains the milking results for the given location",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/icarMilkingVisitEventCollection"
                },
                "examples": {
                  "base": {
                    "$ref": "#/components/examples/milking-visit"
                  }
                }
              }
            }
          },
          "default": {
            "$ref": "#/components/responses/default"
          }
        }
      }
    },
    "/locations/{location-scheme}/{location-id}/test-days": {
      "get": {
        "operationId": "get-test-days",
        "summary": "Get the test days",
        "description": "# Purpose\nProvides data from the test days on a farm that intersects with the given start/end date if provided. Otherwise returns all available entries.",
        "tags": [
          "ADE-1.1-milk"
        ],
        "parameters": [{
          "$ref": "#/components/parameters/location-scheme"
          },
          {
            "$ref": "#/components/parameters/location-id"
          },
          {
            "$ref": "#/components/parameters/meta-modified-from"
          },
          {
            "$ref": "#/components/parameters/meta-modified-to"
          }
        ],
        "responses": {
          "200": {
            "description": "Successful. The response contains the test days for the given location",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/icarTestDayCollection"
                },
                "examples": {
                  "base": {
                    "$ref": "#/components/examples/test-days"
                  }
                }
              }
            }
          },
          "default": {
            "$ref": "#/components/responses/default"
          }
        }
      }
    },
