          "eventNotification": {
            "{$request.body#/evSubsc/notifUri}/notify": {
              "post": {
                "requestBody": {
                  "description": "Notification of an event occurrence in the PCF.",
                  "content": {
                    "application/json": {
                      "schema": {
                        "$ref": "#/components/schemas/EventsNotification"
                      }
                    }
                  }
