          "remember_for": {
            "description": "Remember Consent For\n\nRememberFor sets how long the consent authorization should be remembered for in seconds. If set to `0`, the\nauthorization will be remembered indefinitely.",
            "format": "int64",
            "type": "integer"
          },
          "session": {
