            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "userinfo_endpoint": {
            "description": "OpenID Connect Userinfo URL\n\nURL of the OP's UserInfo Endpoint.",
            "type": "string"
          },
          "userinfo_signed_response_alg": {
