          "303": {
            "$ref": "#/components/responses/emptyResponse"
          },
          "500": {
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/jsonError"
                }
              }
            },
            "description": "jsonError"
          }
        },
        "summary": "Complete Self-Service Logout",
        "tags": [
          "v0alpha2"
        ]
      }
